// Also check out Hair V2 here: https://www.shadertoy.com/view/3fsyzn

// I did two things here:
// 1. Apply the magnetic field pattern back to the first hair prototype
// 2. Try and fix the problem I had with hair where the semi transparent ends cause the ray to stop
// when it should pass though and render the next thing.
// (Maybe using transparecy as a tapering method is also not the way to go.)
// It works, mostly, but not with AA, still figuring that out
// I still have more to learn with raymarching SDFs.


#define AA 1.0  // AA is broken, don't use.
#define LATSPACING 0.02
#define AZISPACING 0.04
#define HAIRTHICK 0.01
#define HAIRLENGTH 0.6
#define INNER_RADIUS 0.4
#define OUTER_RADIUS 0.8
#define MAX_PASSES 4

float sdSphere(vec3 p, float r) {return length(p) - r;}

vec3 getHairField(vec3 p) {
    float r = length(p);
    if(r < 0.001) return vec3(0,-1,0);
    vec3 rh = p / r;
    float r3 = r * r * r;
    float mdotr = -rh.y;
    return normalize((3.0 * mdotr * rh + vec3(0,1,0)) / r3);
}

vec2 getHairParams(vec3 p) {
    float r = length(p);
    if(r < 0.001) return vec2(0);
    float sinTheta = length(p.xz) / r, sinTheta2 = sinTheta * sinTheta;
    return vec2(r / (sinTheta2 + 0.001), atan(p.z, p.x));
}

float LToLat(float L) {
    if(L < 1.0) return 1.57;
    return asin(clamp(1.0 / sqrt(L), -1.0, 1.0));
}
float latToL(float lat) {float s = sin(lat); return 1.0 / (s*s + 0.001);}

float sdHairLine(vec3 p, float latSpc, float aziSpc, float thick) {
    float r = length(p);
    if(r < INNER_RADIUS || r > INNER_RADIUS + HAIRLENGTH) return 1e10; 
    vec2 prm = getHairParams(p);
    float L = prm.x, phi = prm.y;
    if(L < 0.1 ) return 1e10;
    float lat = LToLat(L);
    if(lat < 0.1) return 1e10;
    float nearLat = round(lat / latSpc) * latSpc;
    if(nearLat < 0.1) return 1e10;
    float nearL = latToL(nearLat), nearPhi = round(phi / aziSpc) * aziSpc;
    float Ldiff = abs(L - nearL) / L;
    float phiDiff = abs(phi - nearPhi) * sin(lat);
    float dist = sqrt(Ldiff*Ldiff + phiDiff*phiDiff);
    float taper = 1.0 - smoothstep(INNER_RADIUS, INNER_RADIUS + HAIRLENGTH, r);
    if(taper <= 0.0) return 1e10;
    return dist - thick * taper;
}

float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.78));
    p += dot(p, p + 34.56);
    return fract(p.x * p.y);
}

vec2 getHairID(vec3 p, float latSpc, float aziSpc) {
    vec2 prm = getHairParams(p);
    if(prm.x < 1.0) return vec2(0);
    float lat = LToLat(prm.x);
    return vec2(round(lat / latSpc), round(prm.y / aziSpc));
}

float map(vec3 p) {return sdSphere(p, INNER_RADIUS);}

float mapHairs(vec3 p, float latSpc, float aziSpc, float thick) {
    return min(sdSphere(p, INNER_RADIUS), sdHairLine(p, latSpc, aziSpc, thick));
}

bool inHairZone(vec3 p) {
    float d = length(p);
    return d > INNER_RADIUS && d < INNER_RADIUS + HAIRLENGTH;
}

float getHairOpacity(vec3 p) {
    float r = length(p);
    float fade = 1.0 - smoothstep(INNER_RADIUS, INNER_RADIUS + HAIRLENGTH, r);
    return fade * 0.9;
}

vec3 getHairColor(vec3 p, float random, vec3 lightDir, vec3 lightDir2, vec3 viewDir) {
    vec3 nHit = normalize(p);
    float diff = max(dot(nHit, lightDir), 0.0);
    float diff2 = max(dot(nHit, lightDir2), 0.0);
    float ambient = 0.3;
    vec3 hairColA = vec3(0.85, 0.6, 0.3);
    vec3 hairColB = vec3(0.5, 0.4, 0.0);
    vec3 hairCol = mix(hairColA, hairColB, random);
    vec3 hitCol = hairCol * (diff + ambient);
    hitCol += vec3(0.3,0,0) * (diff2 + ambient);
    float spec = pow(max(dot(reflect(-lightDir, nHit), viewDir), 0.0), 16.0);
    hitCol += vec3(0.4) * spec;
    return hitCol;
