vec3 raymarchVolume(vec3 ro, vec3 rd, float latSpc, float aziSpc, float thick) {
    float t = 0.0;
    vec3 col = vec3(0.9, 0.85, 0.8); 

    vec3 lightDir = normalize(vec3(2,3,-2));
    vec3 lightDir2 = normalize(vec3(-1,-2,1));

    for(int pass = 0; pass < MAX_PASSES; pass++) {
        bool hitSomething = false;

        for(int i = 0; i < 150; i++) {
            vec3 p = ro + rd * t;
            float d = mapHairs(p, latSpc, aziSpc, thick);

            if(d < 0.001) {
                float objId = 0.0, opacity = 1.0;
                vec2 hairID = getHairID(p, latSpc, aziSpc);

                if(inHairZone(p)) {
                    float innerD = sdSphere(p, INNER_RADIUS);
                    float hairD = sdHairLine(p, latSpc, aziSpc, thick);
                    if(hairD < innerD && hairD < 0.001) {
                        objId = 1.0;
                        opacity = getHairOpacity(p);
                    }
                }

                if(objId > 0.5) {
                    // Hair hit - blend and continue if transparent
                    vec3 hairCol = getHairColor(p, hash(hairID), lightDir, lightDir2, -rd);
                    col = mix(col, hairCol, opacity);
                    // If mostly opaque, stop here
                    if(opacity > 0.8) return col;           
                    // Continue ray slightly past this hit
                    t += 0.001;
                    hitSomething = true;
