    vec3 col = vec3(0.9, 0.85, 0.8);

    vec2 outerHit = raySphere(ro, rd, OUTER_RADIUS);
    if(outerHit.x > 0.0) {
        vec3 pEnter = ro + rd * outerHit.x;
        vec3 roInner = pEnter + rd * 0.001;
        col = raymarchVolume(roInner, rd, LATSPACING, AZISPACING, HAIRTHICK);
    }

    fragColor = vec4(col, 1.0);
}

