    float s = 4., k; // s. is the number of samples
    vec2 j = vec2(.5);
    o = vec4(0);
    vec4 c;
    mainImage0(c, u);
    for (k = s; k-- > .5; ) {
        mainImage0(c, u + j - .5);
        o += c;
        j = fract(j + vec2(.754877669, .569840296).yx);
    };o /= s;o.a==1.;
}

