// iChannel0: Optional noise texture (can be blank)
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Base rotation and scaling
    float time = iTime * 0.2;
    vec2 pos = uv * 4.0;
    pos *= mat2(cos(time), sin(time), -sin(time), cos(time));

    // Fractal noise for organic texture
    float noise = 0.0;
    float amplitude = 1.0;
    for (float i = 0.0; i < 4.0; i++) {
        noise += amplitude * texture(iChannel0, pos * 0.5 + time * 0.1).x;
        pos *= 2.0;
        amplitude *= 0.5;
    }

    // Core soul shape
    float core = length(uv) * 2.0;
    core = smoothstep(0.5, 0.3, core);

    // Glowing energy trails
    float trails = 0.0;
    for (float i = 0.0; i < 5.0; i++) {
        float angle = time * 2.0 + i * 1.3;
        vec2 offset = vec2(cos(angle), sin(angle)) * 0.3;
        trails += smoothstep(0.05, 0.0, length(uv - offset * iTime * 0.1));
    }

    // Color composition
    vec3 color = vec3(0.0);

    // Base soul glow
    color += vec3(0.2, 0.5, 1.0) * pow(core, 2.0) * 0.8;

    // Energy trails
    color += vec3(0.8, 0.3, 1.0) * trails * 0.4;

    // Noise texture overlay
    color += vec3(noise) * 0.3 * vec3(0.5, 1.0, 0.8);

    // Vignette effect
    color *= smoothstep(0.8, 0.5, length(uv));

    // Final glow pass
    color = pow(color, vec3(0.8)) * 1.5;

    fragColor = vec4(color, 1.0);
}

