void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float bounds = iResolution.y/2.;

   if( iMouse.z>0. || iMouse.x>0. || iMouse.y>0.) { bounds = iMouse.y;}

   vec2 co = fragCoord;

   vec3 col = vec3(0);

   if( co.y>bounds ) {
    // three sample plus dxdy
    int pos = int(co.x)%2 + (int(co.y)%2 * 2);
    float ra = texture(iChannel0, co/iResolution.xy, 0.).r;
    float rb = texture(iChannel0, (co+vec2(1,0))/iResolution.xy, 0.).r;
    float rc = texture(iChannel0, (co+vec2(0,1))/iResolution.xy, 0.).r;
    float dx = dFdx(ra);
    float dy = dFdy(ra);
    if(pos == 1 || pos == 3){ dx = rb-ra; }
    if(pos == 2 || pos == 3){ dy = rc-ra; }
    float dz = sqrt((dx*dx) + (dy*dy));
    float a = (PI + atan(dx,dy))/(PI*2.);
    col = hsl2rgb(vec3(a,1., ra/2.));

   } else {
   // original method dxdy only
    col = texture(iChannel0, fragCoord/iResolution.xy, 0.).rgb;
    float r = col.r;  
    float dx = dFdx(r);
    float dy = dFdy(r);
    float a = (PI + atan(dx,dy))/(PI*2.);
    col = hsl2rgb(vec3(a, 1., r/2.)); 

   }

   if(floor(co.y)==floor(bounds)){col = vec3(0);}

   fragColor = vec4(col, 1.);
}

