            if(dispd<npixel && AA){ // inside overhang edge 2
                sphere.colour.a = dispd/npixel;
            }
            return sphere;
        } else { return Surface(vec3(0), vec4(0), vec4(0), false);}
    } else if(dispb>0.){
        sphere.position.xyz = vec3(coords, height+subpoint.z);
        sphere.normal = normalize(subpoint-sphere.position.xyz);
        if(dispb<npixel && AA){ // inside overhang edge 1
                sphere.colour.a = dispb/npixel;
            }
        return sphere;
    } else { return Surface(vec3(0), vec4(0), vec4(0), false);}
}

