float atan2(in float y, in float x)
{
    return x == 0.0 ? sign(y)*1.570796 : atan(y, x);
}

vec4 addRotation(vec4 shape, float rot){
    float angle = atan(shape.x/shape.z);
    float rad = length(shape.xz);
    angle += rot;
    //x = r cos θ , y = r sin θ
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec4(newx, shape.y, newz, shape.w);
}

vec3 addRotationToNormal(vec3 norm, float rot){
    float angle = atan(norm.x/norm.z);
    float rad = length(norm.xz);
    angle += rot;
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec3(newx, norm.y, newz);

}

vec3 contrast(vec3 color, float value) {
  return 0.5 + value * (color - 0.5);
}

vec3 gammaCorrection (vec3 colour, float gamma) {
  return pow(colour, vec3(1. / gamma));
}

vec3 toneMapping (vec3 colour){
    return 1.0 - 1.0/(3.0 * pow(colour, vec3(1.5)) + 1.0); //Modified from milesWaugh's filmic tonemapping curve
}

vec3 addLight(Surface surface, vec3 lcol, vec3 lpos){
    vec3 ref = reflect(vec3(0.0, 0.0, -1.0), surface.normal);
    vec3 diff = lpos - surface.position.xyz;
    float ldist = length(diff);
    diff = diff/ldist;
    float specular = pow(max(0.0, dot(diff, ref)), 16.0);
    float diffuse = max(0.0, dot(diff, surface.normal));
    vec3 col1 = surface.colour.rgb*0.1;
    vec3 col2 = surface.colour.rgb*0.5*(lcol*diffuse);
    col2 = col2+(lcol*specular);
    return col1+col2;
}

