// OSF - Orthographic Shape Function
// Returns normal and distance(depth) in a vec4
// All values set to -1 if point is outside the shape
vec4 sphereOSF( vec4 sphere, vec2 position ){
    float dist = distance(position, sphere.xy);
    if(dist>sphere.w){
        return vec4(-1, -1, -1, -1);
    } else {
        vec2 distv = position - sphere.xy;
        float height = sqrt((sphere.w*sphere.w)-(dist*dist));
        vec3 normal = vec3(distv.x, distv.y, height)/sphere.w;
        height+=sphere.z;
        return vec4(normal, height);
    }
}

vec4 sphereSubOSF( vec4 sphere, vec4 spheresub, vec2 position ){
    float dist = distance(position, sphere.xy);
    if(dist>sphere.w){
        return vec4(-1, -1, -1, -1);
    } else {
        vec2 distv = position - sphere.xy;
        float height = sqrt((sphere.w*sphere.w)-(dist*dist));
        vec3 normal = vec3(distv.x, distv.y, height)/sphere.w;
        height+=sphere.z;
        vec3 sposition = vec3(position, height);
        if(distance(sposition, spheresub.xyz)>spheresub.w){
            return vec4(normal, height);
        } else {
            dist = distance(position, spheresub.xy);
            distv = position-spheresub.xy;
            height = -sqrt((spheresub.w*spheresub.w)-(dist*dist));
            normal = vec3(-distv.x,-distv.y, -height)/spheresub.w;
            height+=spheresub.z;
            sposition = vec3(position, height);
            if(distance(sposition, sphere.xyz)<sphere.w){
                return vec4(normal, height);
            } else {
                return vec4(-1, -1, -1, -1);
            }
        }
    }
}



vec4 ellipsoidOSF(vec3 center, vec3 radii, vec3 orientationNormal, vec2 position) {

    // Normalize the orientation normal
    vec3 n = normalize(orientationNormal);

    // Generate orthonormal tangent and bitangent vectors
    vec3 t, bv;
    vec3 up = vec3(0.0, 0.0, 1.0);
    if (abs(dot(n, up)) > 0.999) {
        up = vec3(0.0, 1.0, 0.0);
    }
    t = normalize(cross(up, n));
    bv = cross(n, t);
    mat3 R = mat3(t, bv, n); // Rotation matrix

    // Calculate dx and dy from the center
    float dx = position.x - center.x;
    float dy = position.y - center.y;

    // Coefficients for the quadratic equation
    float A1 = R[0].z;
    float A2 = R[1].z;
    float A3 = R[2].z;

    float B1 = dot(R[0].xy, vec2(dx, dy)) - R[0].z * center.z;
    float B2 = dot(R[1].xy, vec2(dx, dy)) - R[1].z * center.z;
    float B3 = dot(R[2].xy, vec2(dx, dy)) - R[2].z * center.z;

    float a = radii.x, b = radii.y, c = radii.z;
    float A = (A1*A1)/(a*a) + (A2*A2)/(b*b) + (A3*A3)/(c*c);
    float B = 2.0 * ( (A1*B1)/(a*a) + (A2*B2)/(b*b) + (A3*B3)/(c*c) );
    float C = (B1*B1)/(a*a) + (B2*B2)/(b*b) + (B3*B3)/(c*c) - 1.0;

    float discriminant = B*B - 4.0*A*C;
    if (discriminant < 0.0) return vec4(-1.0);

    float sqrtD = sqrt(discriminant);

    float t_intersect = (-B + sqrtD) / (2.0*A);

    // Select the smallest positive intersection
    //float t0 = (-B - sqrtD) / (2.0*A);
    //float t_intersect = t1;
    //if (t0 > 0.0 && t1 > 0.0) {t_intersect = min(t0, t1);} else if (t0 > 0.0) {t_intersect = t0;} else if (t1 > 0.0) {t_intersect = t1;} else {return vec4(-1.0);}

    // Compute local normal components
    float Qx = (A1 * t_intersect + B1) / a;
    float Qy = (A2 * t_intersect + B2) / b;
    float Qz = (A3 * t_intersect + B3) / c;

    // Transform normal to world space and normalize
    vec3 normal = normalize(R * vec3(Qx/a, Qy/b, Qz/c));
    return vec4(normal, t_intersect);
}

vec4 eggOSF(vec4 sphere, vec3 orientation, vec2 position){
    vec4 ellipse = ellipsoidOSF(sphere.xyz, vec3(sphere.w, sphere.w, sphere.w*1.8), orientation, position);
    vec3 sposition = vec3(position, ellipse.w);
    vec3 bposition = sphere.xyz + (normalize(orientation) * sphere.w);
    if(distance(bposition, sposition)>(sphere.w*1.4)){
        ellipse = sphereOSF( sphere, position );
    }
    return ellipse;
}

vec4 addRotation(vec4 shape, float rot){
    float angle = atan(shape.x/shape.z);
    float rad = length(shape.xz);
    angle += rot;
    //x = r cos θ , y = r sin θ
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec4(newx, shape.y, newz, shape.w);

}
vec3 addRotationToNormal(vec3 norm, float rot){
    float angle = atan(norm.x/norm.z);
    float rad = length(norm.xz);
    angle += rot;
    //x = r cos θ , y = r sin θ
    float newx = rad*cos(angle);
    float newz = rad*sin(angle);
    return vec3(newx, norm.y, newz);

}


vec3 addLight(vec3 col, vec3 pos, vec3 nor, vec3 lcol, vec3 lpos){
    vec3 ref = reflect(vec3(0.0, 0.0, -1.0), nor);
    vec3 diff = lpos - pos;
    float ldist = length(diff);
    diff = diff/ldist;
    float specular = pow(max(0.0, dot(diff, ref)), 16.0);
    float diffuse = max(0.0, dot(diff, nor));
    col = col*(lcol*diffuse);
    col = col+(lcol*specular);
    return col;
}

// see the comments for some much shorter ways to do this!

#define I iResolution
#define F float
#define q ivec3
#define j vec3
#define k vec4
#define R return
F gp(j A, j B, j C, vec2 D){
    F d = (A.x - C.x) * (B.y - C.y) - (B.x - C.x) * (A.y - C.y); if (d == 0.){ R -1.; }
    F s = (D.x - C.x) * (B.y - C.y) - (B.x - C.x) * (D.y - C.y); F u = s / d;
    F t = (A.x - C.x) * (D.y - C.y) - (D.x - C.x) * (A.y - C.y); F v = t / d;
    if (u >= 0. && v >= 0. && u + v <= 1.){ F w = 1. - u - v; R u * A.z + v * B.z + w * C.z; } else { R -1.; }
}

j ar(j p, j a, F r){F c = cos(r);F s = sin(r); p = p-a;R j(p.z*c-p.x*s, p.y, p.z*s+p.x*c)+a;}

void mainImage( out k fC, in vec2 fc ) {
    vec2 uv = fc/I.y; j v[12]; j w[12]; F p = (1.+ sqrt(5.))/2.;
    int t[26] = int[26](10,0,1,8,6,9,3,2,11,5,10,0,4,0,5,2,9,8,0,7,11,3,6,1,10,11);
    k m = k(I.x/I.y/2., .5, 0., .2); j c = j(0.); F z = -1.;
    for (int i = 0; i<4; i++){
        v[i] = j(0., p, 1.0);
        if(i>1){v[i].y = -v[i].y;};
        if (i%2==1){v[i].z = -v[i].z;}
        v[i+4] = v[i].zxy;v[i+8] = v[i].yzx;
    }
    for (int i = 0; i<12; i++){
        normalize(v[i]);
        w[i] = v[i]; v[i] *= m.w; v[i] += m.xyz;
        v[i].yxz = ar(v[i].yxz, m.yxz, iMouse.y/-100.);
        v[i] = ar(v[i], m.xyz, iMouse.x/-100.);
    }
    for (int i = 0; i<20; i++){
        q T = q(t[i],t[i+1],t[i+2]);
        if(i>9){ T = q(t[12],t[i+3],t[i+4]);}
        if(i>14){ T = q(t[19],t[i+5],t[i+6]);}
        F n = gp(v[T.x],v[T.y],v[T.z], uv);
        if(n>z){z=n; c =(.2 + n/2.)+(w[T.x]+w[T.y]+w[T.z])/20.+.1;}
    }
    fC = k(c, 1.);
}

