float n2 = 3.0;    // Tertiary ridge frequency
float m2 = 0.5;    // Tertiary ridge offset
float k2 = 0.1;    // Tertiary ridge amplitude

float g = 0.6;     // Global size offset
float p = 0.9;     // Vertical squash factor
float h = -0.3;    // Top/bottom flattening

