// Mode selection - comment out for random mode
#define USE_SLIDERS

// Slider values on the right are pumpkin then stalk metrics.
// Slider values on the left are camera/view at the top, then AA, then face then lid metrics at the bottom.

// iChannel0 is this buffers output, iChannel1 is the font texture

// Pumpkin Shader, SDF Based on:
// https://www.desmos.com/3d/afwrvhoa9n


// Random seed for random mode
float randomSeed = 0.0;

float n0 = 20.0;   // Primary ridge frequency (higher = more ridges)
float m0 = 0.5;    // Primary ridge offset
float k0 = 0.1;    // Primary ridge amplitude

float n1 = 20.0;    // Secondary ridge frequency
float m1 = 0.5;    // Secondary ridge offset  
float k1 = 0.15;   // Secondary ridge amplitude

float n2 = 3.0;    // Tertiary ridge frequency
float m2 = 0.5;    // Tertiary ridge offset
float k2 = 0.1;    // Tertiary ridge amplitude

float g = 0.6;     // Global size offset
float p = 0.9;     // Vertical squash factor
float h = -0.3;    // Top/bottom flattening

float[40] sliderValues;
bool[40] hasInteracted;

float thickness = 0.02;    // Wall thickness for hollow effect
float cutSphereRadius = 0.6; // Size of sphere to cut from top
float cutSphereY = 0.5;    // Height of cutting sphere center
float lidRaise = 0.0;      // How much to raise the lid
float lidTiltX = 0.04;      // Tilt angle around X axis
float lidTiltZ = 0.05;      // Tilt angle around Z axis
float lidGap = 0.02;       // Gap between lid and opening

// Stalk parameters (now adjustable via sliders)
float stalkRadius = 0.04;   // Base radius of stalk
int   stalkStarN = 10;      // Number of star points
float stalkStarM = 2.5;     // Star sharpness (between 2 and n)
float stalkHeight = 0.35;   // Total height of stalk
float stalkTwist = 0.1;     // Twisting amount around Y axis
float stalkBend = 0.1;      // Bending amount (vertical curvature)
float stalkTaper = -1.2;    // Tapering factor (0 = no taper, 1 = full taper)
float stalkBulbHeight = 0.22; // Height of bulbous base
float stalkBulbRadius = 0.45; // Maximum radius of bulbous base
float stalkHeightOffset = 0.0; //minor adjustment when it gets displaced.

// Face cutout parameters
float faceScale = 0.3;     // Size of face characters
float faceDepth = 0.1;     // How deep the cut goes  
float faceZ = 0.0;         // Z offset for face position

float stemSubtract;

float mouthSliderA = 0.5;        // number telling us which mouth to use
float mouthSliderB = 0.5;       // number of second shape for interpolation
float mouthSliderC = 0.5;       // interpolation value
int mouthTypes = 10;       // number of possible mouths for slider bounds calculation
float eyeSliderA = 0.5;       // interpolation value
int eyeTypes = 8;       // number of possible eyes for slider bounds calculation

// Slider parameters
const float sliderLineWidth = 0.003;   // Width of the slider line (smaller)
const float sliderCircleRadius = 0.012; // Radius of the slider circle (smaller)
const int totalSliders = 40;           // Total number of sliders

// Simple hash function for deterministic random values
float hash(float seed) {
    return fract(sin(seed * 12.9898) * 43758.5453);
}

// Parameter mapping functions - convert 0-1 values to parameter ranges
float mapLidRaise(float t) { return t * 0.5; }
float mapLidTiltX(float t) { return (t - 0.5) * 0.4; }
float mapLidTiltZ(float t) { return (t - 0.5) * 0.4; }
float mapCutSphereY(float t) { return mix(0.2, 1.0, clamp(t, 0.0, 1.0)); }
float mapCutSphereRadius(float t) { return mix(0.4, 0.7, clamp(t, 0.0, 1.0)); }
float mapThickness(float t) { return mix(0.04, 0.08, clamp(t, 0.0, 1.0)); }
float mapLidGap(float t) { return mix(0.0, 0.05, clamp(t, 0.0, 1.0)); }
float mapMouthSliderA(float t) { return t; }
float mapMouthSliderB(float t) { return t; }
float mapMouthSliderC(float t) { return t; }
float mapFaceScale(float t) { return mix(3.25, 4.5, clamp(t, 0.0, 1.0)); }
float mapEyeSliderA(float t) { return t; }
float mapSubsurfaceDistance(float t) { return mix(0.0001, 0.2, clamp(t, 0.0, 1.0)); }  // How far to probe into surface
float mapSubsurfaceAttenuation(float t) { return mix(0.1, 10.0, clamp(t, 0.0, 1.0)); } // Light falloff strength

// Camera/scene parameters (sliders 14-19, hardcoded in random mode)
float mapFocal(float t) { return t * 2.0; }
float mapBaseCameraDist(float t) { return mix(4.0, 10.0, clamp(t, 0.0, 1.0)); }
float mapScreenMag(float t) { return (t < 0.5) ? mix(0.75, 2.0, t * 2.0) : mix(2.0, 3.0, (t - 0.5) * 2.0); }
float mapPitch(float t) { return radians(mix(-89.0, 70.0, clamp(t, 0.0, 1.0))); }
float mapYaw(float t) { return mix(-4.7123, 1.5708, clamp(t, 0.0, 1.0)); }

// Stalk parameters (sliders 20-27)
float mapStalkRadius(float t) { return mix(0.04, 0.1, clamp(t, 0.0, 1.0)); }
float mapStalkStarN(float t) { return floor(mix(5.0, 20.0, clamp(t, 0.0, 1.0))); }
float mapStalkStarM(float t) { return mix(1.8, 4.0, clamp(t, 0.0, 1.0)); }
float mapStalkHeight(float t) { return mix(0.2, 0.5, clamp(t, 0.0, 1.0)); }
float mapStalkTwist(float t) { return mix(0.0, 1.5, clamp(t, 0.0, 1.0)); }
float mapStalkBend(float t) { return mix(0.0, 0.6, clamp(t, 0.0, 1.0)); }
float mapStalkTaper(float t) { return mix(-0.5, 0.3, clamp(t, 0.0, 1.0)); }
float mapStalkBulbHeight(float t) { return mix(0.1, 0.3, clamp(t, 0.0, 1.0)); }

// Pumpkin shape parameters (sliders 28-39)
float mapK2(float t) { return mix(0.02, 0.3, clamp(t, 0.0, 1.0)); }
float mapM2(float t) { return mix(0.0, 6.28, clamp(t, 0.0, 1.0)); }
float mapN2(float t) { return floor(mix(4.0, 12.0, clamp(t, 0.0, 1.0))); }
float mapK1(float t) { return mix(0.02, 0.3, clamp(t, 0.0, 1.0)); }
float mapM1(float t) { return mix(0.0, 6.28, clamp(t, 0.0, 1.0)); }
float mapN1(float t) { return floor(mix(5.0, 30.0, clamp(t, 0.0, 1.0))); }
float mapK0(float t) { return mix(0.02, 0.3, clamp(t, 0.0, 1.0)); }
float mapM0(float t) { return mix(0.0, 3.14, clamp(t, 0.0, 1.0)); }
float mapN0(float t) { return floor(mix(6.0, 60.0, clamp(t, 0.0, 1.0))); }
float mapH(float t) { return mix(-0.3, -0.05, clamp(t, 0.0, 1.0)); }
float mapP(float t) { return mix(0.5, 0.8, clamp(t, 0.0, 1.0)); }
float mapG(float t) { return mix(0.5, 0.7, clamp(t, 0.0, 1.0)); }

// Get parameter value - either from slider or random
float getParamValue(int sliderIndex) {
#ifdef USE_SLIDERS
    return sliderValues[sliderIndex];
#else
    // For random mode, hardcode camera/scene values (sliders 14-19)
    if (sliderIndex == 14) return 0.0;  // AA
    if (sliderIndex == 15) return 0.5;  // Focal
    if (sliderIndex == 16) return 0.5;  // Camera distance
    if (sliderIndex == 17) return 0.5;  // Screen magnification
    if (sliderIndex == 18) return 0.5 - (0.5-(iMouse.y/iResolution.y))/6.; ;  // Pitch
    if (sliderIndex == 19) return 0.5 - (0.5-(iMouse.x/iResolution.x))/3.;  // Yaw

    // For other parameters, use deterministic random based on slider index
    return hash(randomSeed + float(sliderIndex) * 0.1);
    //return (0.5-range/2.) + (hash(randomSeed + float(sliderIndex) * 0.1))*range;
#endif
}

// SDF Operations
float opOnion(float sdf, float thickness) {
    return abs(sdf) - thickness;
}

float opSubtraction(float d1, float d2) {
    return max(-d1, d2);
}

float opUnion(float d1, float d2) {
    return min(d1, d2);
}

// Sphere SDF
float sphereSDF(vec3 pos, float radius) {
    return length(pos) - radius;
}

// 2D Star SDF
float sdStar(vec2 p, float r, int n, float m) {
    // Precomputed values for given shape
    float an = 3.141593 / float(n);
    float en = 3.141593 / m;  // m is between 2 and n
    vec2 acs = vec2(cos(an), sin(an));
    vec2 ecs = vec2(cos(en), sin(en)); // ecs=vec2(0,1) for regular polygon

    float bn = mod(atan(p.y, p.x), 2.0 * an) - an;
    p = length(p) * vec2(cos(bn), abs(sin(bn)));
    p -= r * acs;
    p += ecs * clamp(-dot(p, ecs), 0.0, r * acs.y / ecs.y);
    return length(p) * sign(p.x);
}

// get 2D sdf data from font texture.
float GetCharacter(ivec2 ch, vec2 cuv, float th) {
    cuv = clamp(cuv, vec2(0), vec2(1));
    float tx = (cuv.x/16.) + (float(ch.x)*(1./16.));
    float ty = (cuv.y/16.) + (float(ch.y)*(1./16.));
    return (-.5*th) + texture(iChannel1, vec2(tx, ty)).a;
}

// Single character 2D SDF for face cutting
float char2DSDF(vec2 p, float scale, ivec2 ch) {
    // Map world XY to [0,1] glyph UV, centered at origin
    vec2 uv = p / scale + 0.5;
    return GetCharacter(ch, uv, 1.0);
}

// Rotated character SDF
float char2DSDFRotated(vec2 p, vec2 scale, ivec2 ch, float angle, float th) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 rotP = vec2(c * p.x - s * p.y, s * p.x + c * p.y);
    vec2 uv = rotP / scale + 0.5;
    return GetCharacter(ch, uv, th);
}

float eyesSDF(vec2 p, float scale, int index) {

    ivec2 echars = ivec2(0);
    float erotation = 0.;
    vec2  escale = vec2(2,1) * scale;
    vec2  eoffset = vec2(0,-0.4) * scale;
    float ethickness = 1.0;

    switch (index) {
        case 0:echars = ivec2(5,15);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );erotation = -3.141;break;
        case 1:echars = ivec2(14,14);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );erotation = -3.141;break;
        case 2:echars = ivec2(4,15);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );escale = vec2(1,1) * scale;erotation = -3.141;break;
        case 3:echars = ivec2(3,15);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );escale = vec2(1,1) * scale;erotation = -3.141;break;
        case 4:echars = ivec2(12,14);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );escale = vec2(1,1) * scale;erotation = -3.141;break;
        case 5:echars = ivec2(10,13);eoffset += vec2(-0.5,-0.2)*scale;p.x = abs (p.x );erotation = -3.141;break;
        case 6:echars = ivec2(11,13);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );erotation = -3.141;break;
        case 7:echars = ivec2(12,13);eoffset += vec2(-0.4,-0.4)*scale;p.x = abs (p.x );escale = vec2(2) * scale;break;
        case 8:echars = ivec2(5,15);eoffset += vec2(-0.5,0)*scale;p.x = abs (p.x );erotation = -3.141;break;
    }
    return  char2DSDFRotated(p + eoffset, escale, echars, erotation, ethickness);
}

float mouthSDF(vec2 p, float scale, int index) {

    ivec2 mchars = ivec2(0);
    float mrotation = 0.;
    vec2  mscale = vec2(2,1) * scale;
    vec2  moffset = vec2(0,0.3) * scale;
    float mthickness = 1.0;

    switch (index) {
        //-------mouth is single character
        case 0:mchars = ivec2(6,10);p.x = abs(p.x)<0.5?(fract( p.x * 6.)-0.5)/3.:p.x;break;
        case 1:mchars = ivec2(15,6);moffset += vec2(-0.4,0)*scale;p.x = abs (p.x );mrotation = -.2;break;
        case 2:mchars = ivec2(7,8); break;
        case 3:mchars = ivec2(7,10);break;
        case 4: mchars = ivec2(13,10);mrotation = 1.57;mscale = vec2(1.2,1.5) * scale;moffset += vec2(-0.1,0)*scale;
        break;   case 5: mchars = ivec2(13,8);mrotation = 1.57;mscale = vec2(1.2,1.5) * scale;moffset += vec2(-0.1,0)*scale;
        break;   case 6: mchars = ivec2(9,13);mrotation = 1.57;mscale = vec2(1.2,1.5) * scale;moffset += vec2(-0.1,0)*scale;
        break;   case 7: mchars = ivec2(8,13);mrotation = 1.57;mscale = vec2(1.2,1.5) * scale;moffset += vec2(-0.1,0)*scale;
        break;   case 8: mchars = ivec2(11,10);mrotation = 1.57;mscale = vec2(1.2,1.5) * scale;moffset += vec2(-0.1,0)*scale; 
        break;   case 9: mchars = ivec2(6,10);p.x = abs(p.x)<0.35?(fract( p.x * 6.)-0.5)/3.:p.x;break; 
        break;  case 10:mchars = ivec2(6,10);p.x = abs(p.x)<0.65?(fract( p.x * 6.)-0.5)/3.:p.x;break;
        break;  case 11: mchars = ivec2(15,5);moffset += vec2(0,0.3)*scale;
        break;  case 12: mchars = ivec2(3,15);mscale = vec2(2.5,1) * scale;
        break;  case 13: mchars = ivec2(3,15);mscale = vec2(1.5,1.5) * scale;
    }

    return  char2DSDFRotated(p + moffset, mscale, mchars, mrotation, mthickness);
}

// Complete face SDF - combines all features
float faceSDF(vec2 p, float scale) {


    float mouthA = mouthSDF(p, scale, int(floor(mouthSliderA * float(mouthTypes))));
    float mouthB = mouthSDF(p, scale, 1+int(floor(mouthSliderA * float(mouthTypes))));
    float mouthC = mouthSDF(p, scale, 13);
    float mouthD = mouthSDF(p, scale, 12);
    float mouthE = mouthSDF(p, scale, 11);
    float mouth = mix(mouthA, mouthB, fract(mouthSliderA * float(mouthTypes)));
    mouth -= (0.5-abs(fract(mouthSliderA * float(mouthTypes)) - 0.5))/15.;
    float mouthF = mouthSliderB<0.5 ? mix(mouthC, mouthD, mouthSliderB*2.) : mix(mouthD, mouthE, (mouthSliderB-0.5)*1.6);
    mouth = mix(mouth, mouthF, mouthSliderC);

    float eyesA = eyesSDF(p, scale, int(floor(eyeSliderA * float(eyeTypes))));
    float eyesB = eyesSDF(p, scale, 1+int(floor(eyeSliderA * float(eyeTypes))));  
    float eyes = mix(eyesA, eyesB, fract(eyeSliderA * float(eyeTypes)));

    return min(mouth, eyes);

}


// Extrude a 2D SDF along Z axis
float extrudeSDF(vec3 pos, float char2D, float halfDepth) {
    vec2 w = vec2(char2D, abs(pos.z) - halfDepth);
    return min(max(w.x, w.y), 0.0) + length(max(w, 0.0));
}

// 3D Extrusion operation
float opExtrusion(vec2 p, float h, float r, int n, float m) {
    float d = sdStar(p, r, n, m);
    vec2 w = vec2(d, abs(h) - stalkHeight * 0.5);
    return min(max(w.x, w.y), 0.0) + length(max(w, 0.0));
}

// Twisting operation (rotates around Y axis as Y increases)
vec3 opTwist(vec3 p, float k) {
    float c = cos(k * p.y);
    float s = sin(k * p.y);
    return vec3(p.x * c - p.z * s, p.y, p.x * s + p.z * c);
}

// Bending operation (curves the stalk in the XZ plane)
vec3 opBend(vec3 p, float k) {
    float bendAmount = k * p.y * p.y / stalkHeight; // Quadratic bend for natural curve
    return vec3(p.x + bendAmount, p.y, p.z);
}



// Rotation matrices
mat3 rotateX(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(1.0, 0.0, 0.0,
                0.0, c, -s,
                0.0, s, c);
}

mat3 rotateZ(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(c, -s, 0.0,
                s, c, 0.0,
                0.0, 0.0, 1.0);
}

// Convert Cartesian to spherical coordinates
vec3 cartesianToSpherical(vec3 pos) {
    float rho = length(pos);
    float theta = atan(pos.z, pos.x);
    float phi = acos(pos.y / rho);
    return vec3(rho, theta, phi);
}
float pown(float f, float p){
    return pow(abs(f), p);
}

// Pumpkin equation SDF
float pumpkinSDF(vec3 pos) {
    vec3 spherical = cartesianToSpherical(pos);
    float theta = spherical.y;
    float phi = spherical.z;

    float sin_term0 = sin(n0 * theta + m0);
    float sin_term1 = sin(n1 * theta + m1);
    float sin_term2 = sin(n2 * theta + m2);

    float part1 = (1.0 - pown(sin_term0, 4.0)) * (k0 / n0) +
                  (1.0 - sin_term1) * (k1 / n1) +
                  (1.0 - sin_term2) * (k2 / n2);

    float cos_2phi = cos(2.0 * phi);
    float part2 = pown((1.0 - cos_2phi) / 2.0, 2.0);
    float part3 = exp(-pown(phi - 3.14159 / 2.0, 2.0)) + p;
    float part4 = 1.0 - h * cos_2phi;

    float target_rho = (part1 * part2 + g) * part3 * part4;

    return length(pos) - target_rho;
}


// Stalk SDF - curved, twisted, tapered star extrusion
float stalkSDF(vec3 pos) {
    // Transform position to match the lid's transformation
    vec3 stalkPos = pos;

    // Move to lid's raised position
    stalkPos.y -= lidRaise;

    // Apply inverse rotations (same as lid, but in reverse order)
    stalkPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * stalkPos;

    // Position stalk above the lid center (now in lid's local space)
    stalkPos = stalkPos - vec3(0.0, stalkHeightOffset + (g * p *1.2) - (h/3.), 0.0);

    // Apply twisting (the existing "bend" that creates spiral ridges)
    vec3 twistedPos = opTwist(stalkPos, stalkTwist / stalkHeight);

    // Apply bending (new vertical curvature)
    vec3 bentPos = opBend(twistedPos, stalkBend / stalkHeight);

    // Calculate radius with both tapering and bulbous base
    float heightFactor = clamp((bentPos.y) / stalkHeight, 0.0, 1.0);

    // Bulbous base effect - smooth falloff from bulb radius to normal radius
    float bulbFactor = 1.0 - smoothstep(0.0, stalkBulbHeight, bentPos.y);
    float bulbRadius = mix(stalkRadius, stalkBulbRadius, bulbFactor);

    // Apply tapering on top of bulb radius
    float taperedRadius = bulbRadius * (1.0 - stalkTaper * heightFactor);

    // Create extruded star - star shape in XZ plane, extruded along Y
    float starDist = sdStar(bentPos.xz, taperedRadius, stalkStarN, stalkStarM);
    float heightDist = (abs(bentPos.y - stalkHeight * 0.5) - stalkHeight * 0.5);

    // Combine using extrusion formula
    vec2 w = vec2(starDist, heightDist);
    float stalkBase = min(max(w.x, w.y), 0.0) + length(max(w, 0.0));

    // Only show stalk outside the original pumpkin surface
    // use stemsubtract stored in lidSDF()
    return opSubtraction(stemSubtract, stalkBase);
}



// Hollow pumpkin with top cut off
float hollowPumpkinSDF(vec3 pos) {
    // Get the base pumpkin shape
    float pumpkin = pumpkinSDF(pos);

    // Make it hollow using onion operation
    float hollow = opOnion(pumpkin, thickness);

    // Create cutting sphere at the top - slightly larger for the gap
    vec3 cutSpherePos = pos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius + lidGap);

    // Subtract the sphere from the hollow pumpkin
    return opSubtraction(cuttingSphere, hollow);
}

// Lid (the cut-off part) - raised and tilted
float lidSDF(vec3 pos) {
    // Transform position for the lid
    vec3 lidPos = pos;

    // Move down to original position, apply rotations, then raise up
    lidPos.y -= lidRaise;
    lidPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * lidPos;

    // Get the base pumpkin shape
    float pumpkin = pumpkinSDF(lidPos);

    stemSubtract = pumpkin;

    // Make it hollow
    float hollow = opOnion(pumpkin, thickness);

    // Create cutting sphere - we want only the part that was cut off
    vec3 cutSpherePos = lidPos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius);

    // Return only the intersection (the part that was inside the cutting sphere)
    // We want the hollow pumpkin AND inside the cutting sphere
    return max(hollow, cuttingSphere);
}

// Main scene SDF
float sceneSDF(vec3 pos) {
    float hollowPumpkin = hollowPumpkinSDF(pos);
    float lid = lidSDF(pos);
    float stalk = stalkSDF(pos);

    // Create complete face cutout
    float faceShape = faceSDF(pos.xy, faceScale);

    vec3 facePos = pos - vec3(0.0, 0.0, faceZ);
    float faceCutout = extrudeSDF(facePos, faceShape, faceDepth);

    // Subtract face cutout from hollow pumpkin only
    hollowPumpkin = opSubtraction(faceCutout, hollowPumpkin);

    return opUnion(opUnion(hollowPumpkin, lid), stalk);
}


// Debug plane (world-space Z plane) helpers
float rayPlaneIntersectZ(vec3 ro, vec3 rd, float planeZ) {
    if (abs(rd.z) < 1e-4) return -1.0; // Parallel
    return (planeZ - ro.z) / rd.z;
}

float rayPlaneIntersectX(vec3 ro, vec3 rd, float planeX) {
    if (abs(rd.x) < 1e-4) return -1.0;
    return (planeX - ro.x) / rd.x;
}

float rayPlaneIntersectY(vec3 ro, vec3 rd, float planeY) {
    if (abs(rd.y) < 1e-4) return -1.0;
    return (planeY - ro.y) / rd.y;
}

vec3 debugSDFColor(vec3 pos) {
    float d = sceneSDF(pos);
    vec3 col = (d > 0.0) ? vec3(0.9, 0.6, 0.3) : vec3(0.65, 0.85, 1.0);
    col *= 1.0 - exp(-6.0 * abs(d));
    col *= 0.8 + 0.2 * cos(150.0 * d);
    col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0, 0.005, abs(d)));
    return col;
}
float getSliderLength(vec2 resolution) {
    return resolution.x / 4.0; // 1/6 screen width
}
// Slider functions
vec2 getSliderPosition(vec2 resolution, int sliderIndex) {
    float spacing = sliderCircleRadius * 4.0 * resolution.y; // More spacing
    bool leftSide = sliderIndex < 20;
    int localIndex = leftSide ? sliderIndex : (sliderIndex - 20);

    float x = leftSide ? resolution.x * 0.02 : resolution.x * 0.98 - getSliderLength(resolution);
    float y = resolution.y * 0.02 + float(localIndex) * spacing;

    return vec2(x, y);
}



vec4 getSliderState(vec2 resolution, int sliderIndex) {
    // Read state from different pixels for each slider (8x5 grid)
    int x = sliderIndex % 8;
    int y = sliderIndex / 8;
    vec2 pixelPos = vec2(float(x) + 0.5, float(y) + 0.5) / resolution;
    return texture(iChannel0, pixelPos);
}

bool isInSliderHitArea(vec2 pos, vec2 sliderPos, float sliderLength, vec2 resolution) {
    vec2 lineStart = sliderPos;
    vec2 lineEnd = sliderPos + vec2(sliderLength, 0.0);

    // Expand hit area by circle radius
    float clearance = sliderCircleRadius * resolution.y * 2.;

    return pos.x >= lineStart.x - clearance && 
           pos.x <= lineEnd.x + clearance &&
           pos.y >= lineStart.y - clearance && 
           pos.y <= lineEnd.y + clearance;
}

float updateSliderValue(vec2 mousePos, vec2 sliderPos, float sliderLength) {
    // Calculate value based on mouse position along the line
    float t = (mousePos.x - sliderPos.x) / sliderLength;
    return clamp(t, 0.0, 1.0);
}

int getActiveSlider(vec2 mousePos, vec2 resolution) {
    float sliderLength = getSliderLength(resolution);

    for (int i = 0; i < totalSliders; i++) {
        vec2 sliderPos = getSliderPosition(resolution, i);
        if (isInSliderHitArea(mousePos, sliderPos, sliderLength, resolution)) {
            return i;
        }
    }
    return -1; // No slider hit
}

vec3 renderSliders(vec2 fragCoord, vec2 resolution, float sliderValues[40]) {
    float sliderLength = getSliderLength(resolution);

    // Pixel-precise screen position
    vec2 screenPos = fragCoord;
    vec3 col1 = vec3(0);
    vec3 col2 = vec3(0.5);
    vec3 col3 = vec3(0.25);
    vec3 col4 = vec3(0.7);

    // Calculate slider area boundaries
    float spacing = sliderCircleRadius * 4.0 * resolution.y;
    float leftStartX = (resolution.x * 0.02)-2.0;
    float leftEndX = (leftStartX + sliderLength)+4.0;
    float rightStartX = (resolution.x * 0.98 - sliderLength)-2.0;
    float rightEndX = (resolution.x * 0.98)+2.0;
    float startY = (resolution.y * 0.02)-2.0;
    float maxY = startY  + 19.0 * spacing + 4.0; // 20 sliders (0-19), plus knob height for last slider

    // Early exit if not in any slider area
    bool inLeftArea = (screenPos.x >= leftStartX && screenPos.x <= leftEndX);
    bool inRightArea = (screenPos.x >= rightStartX && screenPos.x <= rightEndX);
    if (!inLeftArea && !inRightArea) {
        return vec3(-1.0); // Transparent (no slider)
    }
    if (screenPos.y < startY || screenPos.y > maxY) {
        return vec3(-1.0); // Above or below slider area
    }

    // Determine which slider we're potentially in
    bool isLeftSide = inLeftArea;
    float relativeY = screenPos.y - startY;
    int localIndex = int(floor(relativeY / spacing));

    // Bounds check
    if (localIndex < 0 || localIndex >= 20) {
        return vec3(-1.0);
    }

    int sliderIndex = isLeftSide ? localIndex : (localIndex + 20);

    // Now check if we're actually on this specific slider
    //bool inactive = sliderIndex > 11 && sliderIndex < 14;
    vec2 sliderPos = getSliderPosition(resolution, sliderIndex);

    // Convert to pixel-aligned coordinates
    float xStart = floor(sliderPos.x + 0.5);
    float xEnd   = floor(sliderPos.x + sliderLength + 0.5);
    float yLine  = floor(sliderPos.y + 0.5);
    vec2 circlePos = vec2(xStart + sliderValues[sliderIndex] * (xEnd - xStart), yLine + 0.5);

    // 2-pixel-high, pixel-precise line: match exactly two rows (yLine and yLine+1)
    float row = floor(screenPos.y + 0.5);
    bool onLine = (screenPos.x >= xStart && screenPos.x <= xEnd) &&
                  (row == yLine || row == (yLine + 1.0));

    // 4x4 black square knob centered on line (rows yLine-1..yLine+2)
    float knobX = floor(circlePos.x + 0.5);
    bool onKnob = (screenPos.x >= knobX - 2.0 && screenPos.x <= knobX + 1.0) &&
                  (row >= (yLine - 1.0) && row <= (yLine + 2.0));
    if (onKnob) {
        //if(inactive){return col4;}
        return col1;
    }
    if (onLine) {
        //if(inactive){return col4;}
        // Dark grey to the left of the knob, medium grey to the right
        return (screenPos.x < circlePos.x) ? col3 : col2;
    }

    return vec3(-1.0); // Transparent (no slider)
}

// Raymarching function
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 pos = ro + t * rd;
        float d = sceneSDF(pos);
        if (d < 0.0005) break;
        t += d * .9; // Use smaller step for complex shapes
        if (t > 50.0) break;
    }
    return t;
}

// Calculate normal using finite differences
vec3 getNormal(vec3 pos) {
    float eps = 0.001;
    vec3 n = vec3(
        sceneSDF(pos + vec3(eps, 0, 0)) - sceneSDF(pos - vec3(eps, 0, 0)),
        sceneSDF(pos + vec3(0, eps, 0)) - sceneSDF(pos - vec3(0, eps, 0)),
        sceneSDF(pos + vec3(0, 0, eps)) - sceneSDF(pos - vec3(0, 0, eps))
    );
    return normalize(n);
}


// Subsurface scattering
float calculateSubsurface(vec3 pos, vec3 interiorLightPos, float probeDistance, float atten) {
    vec3 toLightDir = normalize(interiorLightPos - pos);
    float toLightDist = distance(interiorLightPos , pos);
    vec3 probePos = pos + toLightDir * probeDistance;
    float d = sceneSDF(probePos);

    // Only apply subsurface if we're actually inside the object
    // and not just in free space
    float surfaceDist = sceneSDF(pos);
    if (surfaceDist > 0.001) return 0.0;  // We're not on the surface

    return clamp(max(0., (d*atten)/toLightDist), 0.0, 1.0);
}

vec3 lighting(vec3 pos, vec3 normal, vec3 viewDir, float subsurfaceDistance, float subsurfaceAttenuation, mat3 cameraMatrix) {
    float flicker = 0.5 + hash(randomSeed) + 0.2 * sin(iTime * 37.0) + 0.15 * sin(iTime * 71.0) * (0.5 + hash(randomSeed));

    vec3 worldKeyLightDir = normalize( vec3(0,.7,1.) );  // Original direction
    vec3 keyLightDir = normalize(cameraMatrix * worldKeyLightDir);  // Transform to camera space
    vec3 keyLightColor = vec3(.8, 0.9, 1.0);

    vec3 interiorLightPos = vec3(0.0, 0.15, 0.0);
    vec3 interiorLightColor = vec3(1.2, .8, 0.2);
    float interiorLightIntensity = flicker;

    vec3 ambient = vec3(0.4, 0.3, 0.2);

    float keyDiff = max(dot(normal, keyLightDir), 0.0);
    vec3 keyDiffuse = keyDiff * keyLightColor;

    vec3 keyReflectDir = reflect(-keyLightDir, normal);
    float keySpec = pow(max(dot(viewDir, keyReflectDir), 0.0), 32.0);
    vec3 keySpecular = keySpec * keyLightColor;

    vec3 toInteriorLight = interiorLightPos - pos;
    float interiorLightDist = length(toInteriorLight);
    vec3 interiorLightDir = toInteriorLight / interiorLightDist;

    float attenuation = interiorLightIntensity / (1.0 + 0.5 * interiorLightDist * interiorLightDist);

    float interiorDiff = max(dot(normal, interiorLightDir), 0.0);
    vec3 interiorDiffuse = interiorDiff * interiorLightColor * attenuation;

    vec3 diffuse = keyDiffuse + interiorDiffuse;

    float hollowDist = hollowPumpkinSDF(pos);
    float lidDist = lidSDF(pos);
    float stalkDist = stalkSDF(pos);

    vec3 materialColor;
    float fleshSpecular = 1.0;

    if (stalkDist < hollowDist && stalkDist < lidDist) {
        materialColor = vec3(0.36, 0.57, 0.13);
    } else if (lidDist < hollowDist) {
        vec3 lidPos = pos;
        lidPos.y -= lidRaise;
        lidPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * lidPos;

        float originalLidPumpkin = pumpkinSDF(lidPos);
        float fleshDepth = max(0.0, -originalLidPumpkin + thickness);

        if (fleshDepth > 0.001) {
            float depthFactor = clamp(fleshDepth / 0.1, 0.0, 1.0);
            vec3 fleshColor = mix(vec3(1.2, 0.9, 0.4), vec3(0.8, 0.4, 0.1), depthFactor);
            materialColor = fleshColor;
            fleshSpecular = max(0., mix(0.8, 0.1, depthFactor * 20.));
        } else {
            materialColor = vec3(0.8, 0.4, 0.12);
        }
    } else {
        float originalPumpkin = pumpkinSDF(pos);
        float fleshDepth = max(0.0, -originalPumpkin + (thickness));
        //shadow = clamp(10.* clamp(-originalPumpkin, 0.01, 10.5) * (1.-guv.x), 0., 0.6);

        if (fleshDepth > 0.001) {
            float depthFactor = clamp(fleshDepth / 0.1, 0.0, 1.0);
            vec3 fleshColor = mix(vec3(1.2, 0.9, 0.4), vec3(0.8, 0.4, 0.1), depthFactor);

            materialColor = fleshColor;
            fleshSpecular = max(0., mix(0.8, 0.1, depthFactor * 20.));
        } else {
            materialColor = vec3(0.9, 0.45, 0.15);
        }
    }

    float subsurfaceDistance_val = calculateSubsurface(pos, interiorLightPos, subsurfaceDistance, subsurfaceAttenuation);
    float subsurfaceFactor = subsurfaceDistance_val > 0.0 ? clamp(subsurfaceDistance_val, 0.0, 1.0) : 0.0;
    vec3 subsurfaceLight = subsurfaceFactor * interiorLightColor;

    return (materialColor * (ambient + diffuse + subsurfaceLight) + fleshSpecular * keySpecular) ;
}


// Main function
void mainImage0(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

#ifdef USE_SLIDERS
    // Read current states
    for (int i = 0; i < totalSliders; i++) {
        vec4 state = getSliderState(iResolution.xy, i);
        sliderValues[i] = state.r;
        hasInteracted[i] = state.g > 0.25;

        // Default values before first interaction
        if (!hasInteracted[i]) {
            sliderValues[i] = i==14?0.0:0.5;
        }
    }
#else
    // In random mode, set up random seed (could be based on time, mouse, etc.)
    randomSeed = iMouse.x + (iMouse.y/iResolution.y);  // Change over time, or use iMouse.x for user control
#endif

#ifdef USE_SLIDERS
    // Check for mouse interaction
    bool mouseDown = (iMouse.z > 0.0); // abs() the z value
    vec2 currentMousePos = abs(iMouse.xy); // Use current position for dragging, abs() as requested
    vec2 clickPos = abs(iMouse.zw); // Click position, abs() as requested

    if (mouseDown) {
        // Use click position to determine which slider was initially clicked
        int activeSlider = getActiveSlider(clickPos, iResolution.xy);
        if (activeSlider >= 0) {
            // But use current mouse position for the actual value update (allows dragging)
            vec2 sliderPos = getSliderPosition(iResolution.xy, activeSlider);
            float sliderLength = getSliderLength(iResolution.xy);
            sliderValues[activeSlider] = updateSliderValue(currentMousePos, sliderPos, sliderLength);
            hasInteracted[activeSlider] = true;
        }
    }
#endif

    // Parameter assignments using new mapping system
    lidRaise = mapLidRaise(getParamValue(0));
    lidTiltX = mapLidTiltX(getParamValue(1));
    lidTiltZ = mapLidTiltZ(getParamValue(2));
    cutSphereY = mapCutSphereY(getParamValue(3));
    cutSphereRadius = mapCutSphereRadius(getParamValue(4));
    thickness = mapThickness(getParamValue(5));
    lidGap = mapLidGap(getParamValue(6));
    mouthSliderA = mapMouthSliderA(getParamValue(7));
    mouthSliderB = mapMouthSliderB(getParamValue(8));
    mouthSliderC = mapMouthSliderC(getParamValue(9));
    eyeSliderA = mapEyeSliderA(getParamValue(11));

    // Subsurface scattering parameters (sliders 12-13)
    float subsurfaceDistance = mapSubsurfaceDistance(getParamValue(12));
    float subsurfaceAttenuation = mapSubsurfaceAttenuation(getParamValue(13));

    // Camera/scene parameters (sliders 14-19)
    float focal = mapFocal(getParamValue(15));
    float baseCameraDist = mapBaseCameraDist(getParamValue(16));

    // Stalk parameters (sliders 20-27)
    stalkRadius = mapStalkRadius(getParamValue(20));
    stalkStarN = int(mapStalkStarN(getParamValue(21)));
    stalkStarM = mapStalkStarM(getParamValue(22));
    stalkHeight = mapStalkHeight(getParamValue(23));
    stalkTwist = mapStalkTwist(getParamValue(24));
    stalkBend = mapStalkBend(getParamValue(25));
    stalkTaper = mapStalkTaper(getParamValue(26));
    stalkBulbHeight = mapStalkBulbHeight(getParamValue(27));

    // Pumpkin shape parameters (sliders 28-39)
    k2 = mapK2(getParamValue(28));
    m2 = mapM2(getParamValue(29));
    n2 = mapN2(getParamValue(30));
    k1 = mapK1(getParamValue(31));
    m1 = mapM1(getParamValue(32));
    n1 = mapN1(getParamValue(33));
    k0 = mapK0(getParamValue(34));
    m0 = mapM0(getParamValue(35));
    n0 = mapN0(getParamValue(36));
    h = mapH(getParamValue(37));
    p = mapP(getParamValue(38));
    g = mapG(getParamValue(39));

    // Derived parameters
    stalkBulbRadius = cutSphereRadius * 0.6;
    stalkHeightOffset = 0.0; // not used currently
    faceScale = g*(p+.3)*((h+1.)/3.)*mapFaceScale(getParamValue(10));
    faceDepth = 1.1;// should be fixed here now.
    faceZ = 1.1;// should be fixed here now.

    // Screen-space magnify (scene-only)
    float screenMag = mapScreenMag(getParamValue(17));

    baseCameraDist /= pow(max(0.5,focal), 2.);
    screenMag /= pow(max(0.5,focal), 2.);

    vec2 suv = uv; // Save original UV for UI rendering
    vec2 uvScene = uv / screenMag; // Scene-only magnification

    // Camera
    float yaw = mapYaw(getParamValue(19));
    float pitch = mapPitch(getParamValue(18));

    vec3 target = vec3(0.0, 0.35, 0.0);
    vec3 dir = vec3(cos(pitch) * cos(yaw),
                    sin(pitch),
                    cos(pitch) * sin(yaw));
    vec3 ro = target - dir * baseCameraDist;

    vec3 forward = normalize(target - ro);
    vec3 upRef = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(forward, upRef));
    vec3 up = cross(right, forward);

    // Camera to world matrix (inverse of view matrix rotation part)
    mat3 cameraMatrix = mat3(right, up, -forward);
    vec3 rd = normalize(forward + uvScene.x * right + uvScene.y * up);

    vec3 color = vec3(0.8,1.0,1.);//vec3(0.0);

    float t = raymarch(ro, rd);

    if (t < 50.0){
        vec3 pos = ro + t * rd;
        vec3 normal = getNormal(pos);
        vec3 viewDir = normalize(ro - pos);
        color = lighting(pos, normal, viewDir, subsurfaceDistance, subsurfaceAttenuation, cameraMatrix);

    } else {
        // Background
        color = vec3(0.6, 0.8,1.);
    }

    // Gamma correction
    //color = pow(color, vec2(1.0/2.2).xxx);
    color = atan(color);
    fragColor = vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) 
{ 

    vec2 j = vec2(.5); 
    fragColor= vec4(0); 
    vec4 c; 
    mainImage0(c, fragCoord);
    float s = 1.0 + floor(getParamValue(14) * 4.0);
    float k;
    for (k = s; k-- > .5; ) { 
        mainImage0(c, fragCoord + j - .5); 
        fragColor += c; 
        j = fract(j + vec2(.755, .57).yx); 
    };fragColor /= s;fragColor.a==1.;
    vec3 color = fragColor.rgb;

#ifdef USE_SLIDERS
    // Render all sliders on top
    vec3 sliderColor = renderSliders(fragCoord, iResolution.xy, sliderValues);

    if (sliderColor.x >= 0.0) {
        color = sliderColor;
    }

    // Write slider states to 8x5 pixel grid (40 sliders)
    if (fragCoord.y < 5.0 && fragCoord.x < 8.0) {
        int sliderIndex = int(fragCoord.x) + int(fragCoord.y) * 8;
        //float startVal = 0.5;
        //if(sliderIndex == 14) { startVal = 0.0;}
        if (sliderIndex < totalSliders) {
            fragColor = vec4(sliderValues[sliderIndex], hasInteracted[sliderIndex] ? 0.5 : 0.0, 0.0, 1.0);
        } else {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        }

    } else {
        fragColor = vec4(color, 1.0);
    }
#else
    // Random mode - no sliders
    fragColor = vec4(color, 1.0);
#endif
}

