#define COL0 vec2(2.5,0.5)
#define COL1 vec2(3.5,0.5)
#define COL2 vec2(4.5,0.5)
#define HOSE_TARGET vec2(5.5,0.5)
#define MOUSE vec2(6.5,0.5)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if( fragCoord.y > 1. )
        return;

    vec4 hoseData = textureLod(iChannel2, HOSE/iResolution.xy, 0.);
    vec4 attrData = textureLod(iChannel2, ATTRACTOR/iResolution.xy, 0.);
    vec4 col0Data = textureLod(iChannel2, COL0/iResolution.xy, 0.);
    vec4 col1Data = textureLod(iChannel2, COL1/iResolution.xy, 0.);
    vec4 col2Data = textureLod(iChannel2, COL2/iResolution.xy, 0.);
    vec4 hoseTargetData = textureLod(iChannel2, HOSE_TARGET/iResolution.xy, 0.);
    vec4 mouseOld = textureLod(iChannel2, MOUSE/iResolution.xy, 0.);

    bool clickEvent = mouseOld.z <= 0. && iMouse.z > 0.;
    bool curDragging = hoseData.z + attrData.z + col0Data.z + col1Data.z + col2Data.z > 0.;

    if( fragCoord == HOSE )
    {
        if( iFrame == 0 )
            fragColor = vec4(50., 200., 0., 0.);
        else
            fragColor = hoseData;

        vec2 moff = iMouse.xy-fragColor.xy;
        if( iMouse.z <= 0. )
        {
            fragColor.z = 0.;
        }
        else if( (!curDragging && clickEvent && dot(moff,moff)<=11.*11.) || fragColor.z > 0. )
        {
            fragColor.z = 1.;
            fragColor.xy = iMouse.xy;
        }
    }
    else if( fragCoord == ATTRACTOR )
    {
        if( iFrame == 0 )
            fragColor = vec4( 510., 100., 0., 0.);
        else
            fragColor = attrData;

        vec2 moff = iMouse.xy-fragColor.xy;
        if( iMouse.z <= 0. )
        {
            fragColor.z = 0.;
        }
        else if( (!curDragging && clickEvent && dot(moff,moff)<=6.*6.) || fragColor.z > 0. )
        {
            fragColor.z = 1.;
            fragColor.xy = iMouse.xy;
        }
    }
    else if( fragCoord == COL0 )
    {
        if( iFrame == 0 )
            fragColor = vec4( iResolution.xy/2. + vec2(-100.,20.), 0., 0.);
        else
            fragColor = col0Data;

        vec2 moff = iMouse.xy-fragColor.xy;
        if( iMouse.z <= 0. )
        {
            fragColor.z = 0.;
        }
        else if( (!curDragging && clickEvent && dot(moff,moff)<=50.*50.) || fragColor.z > 0. )
        {
            fragColor.z = 1.;
            fragColor.xy = iMouse.xy;
        }
    }
    else if( fragCoord == COL1 )
    {
        if( iFrame == 0 )
            fragColor = vec4( iResolution.xy/2. + vec2(-130.,-100.), 0., 0.);
        else
            fragColor = col1Data;

        vec2 moff = iMouse.xy-fragColor.xy;
        if( iMouse.z <= 0. )
        {
            fragColor.z = 0.;
        }
        else if( (!curDragging && clickEvent && dot(moff,moff)<=50.*50.) || fragColor.z > 0. )
        {
            fragColor.z = 1.;
            fragColor.xy = iMouse.xy;
        }
    }
    else if( fragCoord == COL2 )
    {
        if( iFrame == 0 )
            fragColor = vec4( iResolution.xy/2. + vec2(-20.,-80.), 0., 0.);
        else
            fragColor = col2Data;

        vec2 moff = iMouse.xy-fragColor.xy;
        if( iMouse.z <= 0. )
        {
            fragColor.z = 0.;
        }
        else if( (!curDragging && clickEvent && dot(moff,moff)<=50.*50.) || fragColor.z > 0. )
        {
            fragColor.z = 1.;
            fragColor.xy = iMouse.xy;
        }
    }
    else if( fragCoord == HOSE_TARGET )
    {
        if( iFrame == 0 )
            fragColor = vec4(iResolution.xy/2.,0.,0.);
        else
            fragColor = hoseTargetData;

        if( !curDragging && iMouse.z > 0. && !clickEvent )
            fragColor.xy = iMouse.xy;
    }
    else if( fragCoord == MOUSE )
    {
        fragColor = iMouse;
    }
}

// Buf A is a uniform grid data structure, but storage in each pixel is 1 particle
// (.xy=vel, .zw=pos). For each bucket, searches neighbourhood for newly arriving particle,
// greedily takes first one so particles can annihilate each other if they land in the same
// pixel bucket.

// Flock or perhaps even SPH behavious could probably be implemented on a similar framework.

#define R 5.
#define RESTITUTION .5

// scene data
#define HOSE vec2(.5,.5)/iResolution.xy
#define ATTRACTOR vec2(1.5,0.5)/iResolution.xy
#define COL0 vec2(2.5,0.5)/iResolution.xy
#define COL1 vec2(3.5,0.5)/iResolution.xy
#define COL2 vec2(4.5,0.5)/iResolution.xy
#define HOSE_TARGET vec2(5.5,0.5)/iResolution.xy

vec2 GetVel( vec4 part ) { return part.xy; }
void SetVel( inout vec4 part, vec2 newVel ) { part.xy = newVel; }
vec2 GetPos( vec4 part ) { return part.zw; }
void SetPos( inout vec4 part, vec2 newPos ) { part.zw = newPos; }

vec2 FindArrivingParticle( vec2 arriveCoord, out vec4 partData )
{
    for( float i = -R; i <= R; i++ )
    {
        for( float j = -R; j <= R; j++ )
        {
            vec2 partCoord = arriveCoord + vec2( i, j );

            vec4 part = textureLod( iChannel0, partCoord / iResolution.xy, 0. );

            // particle in this bucket?
            if( dot(part,part) < 0.001 )
                continue;

            // is the particle going to arrive at the current pixel after one timestep?
            vec2 partPos = GetPos( part );
            vec2 partVel = GetVel( part );
            vec2 nextPos = partPos + partVel;
            // arrival means within half a pixel of this bucket
            vec2 off = nextPos - arriveCoord;
            if( abs(off.x)<=.5 && abs(off.y)<=.5 )
            {
                // yes! greedily take this particle.
                // a better algorithm might be to inspect all particles that arrive here
                // and pick the one with the highest velocity.
                partData = part;
                return partCoord;
            }
        }
    }
    // no particle arriving at this bucket.
    return vec2(-1.);
}

void Clip( inout vec4 partData, vec2 col0Pos, vec2 col1Pos, vec2 col2Pos )
{
    vec2 pos = GetPos( partData );
    vec2 vel = GetVel( partData );

    vec2 nextPos = pos + vel;
    if( nextPos.y < 0. ) vel.y *= -RESTITUTION;
    if( nextPos.x < 0. ) vel.x *= -RESTITUTION;
    if( nextPos.y > iResolution.y ) vel.y *= -RESTITUTION;
    if( nextPos.x > iResolution.x ) vel.x *= -RESTITUTION;

    vec2 off; float loff2;
    off = nextPos - col0Pos;
    loff2 = dot(off,off);
    if( loff2 < 50.*50. ) {
        loff2 = sqrt(loff2);
        vec2 n = off/loff2;
        vel -= (1.+RESTITUTION) * dot( vel, n ) * n;
        SetPos( partData, col0Pos + 50.*n );
    }
    off = nextPos - col1Pos;
    loff2 = dot(off,off);
    if( loff2 < 50.*50. ) {
        loff2 = sqrt(loff2);
        vec2 n = off/loff2;
        vel -= (1.+RESTITUTION) * dot( vel, n ) * n;
        SetPos( partData, col1Pos + 50.*n );
    }
    off = nextPos - col2Pos;
    loff2 = dot(off,off);
    if( loff2 < 50.*50. ) {
        loff2 = sqrt(loff2);
        vec2 n = off/loff2;
        vel -= (1.+RESTITUTION) * dot( vel, n ) * n;
        SetPos( partData, col2Pos + 50.*n );
    }

    SetVel( partData, vel );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col0Data = textureLod(iChannel2, COL0, 0.).xyz;
    vec3 col1Data = textureLod(iChannel2, COL1, 0.).xyz;
    vec3 col2Data = textureLod(iChannel2, COL2, 0.).xyz;

    // add loads of particles on first frame
    if( iFrame == 0 )
    {
        fragColor = vec4(0.);
        if( mod(fragCoord,10.) == vec2(2.5) )
        {
            vec4 newPart;
            SetPos( newPart, fragCoord );
            SetVel( newPart, vec2(0.) );
            Clip( newPart, col0Data.xy, col1Data.xy, col2Data.xy );
            fragColor = newPart;
        }
        return;
    }

    // scene data
    vec3 hoseData = textureLod(iChannel2, HOSE, 0.).xyz;
    vec3 attractData = textureLod(iChannel2, ATTRACTOR, 0.).xyz;
    vec2 hoseTarget = textureLod(iChannel2, HOSE_TARGET, 0.).xy;

    float dragCount = hoseData.z + attractData.z + col0Data.z + col1Data.z + col2Data.z;

    // mouse emits
    if( iMouse.z > 0. && length(iMouse.xy-fragCoord.xy) < 9. && dragCount == 0. )
    {
        vec4 newPart;
        SetPos( newPart, fragCoord );
        SetVel( newPart, 3.*normalize(fragCoord.xy-iMouse.xy));
        fragColor = newPart;
        return;
    }

    // hose 0
    if( length(hoseData.xy-fragCoord.xy) < 9. )
    {
        vec4 newPart;
        SetPos( newPart, fragCoord );
        /*vec2 target = iResolution.xy/2.;
        if( iMouse.x*iMouse.y > 0. ) target = iMouse.xy;*/
        SetVel( newPart, 2.8*normalize(hoseTarget-fragCoord.xy) );
        newPart.xy += .15*sin(15.*iTime)*vec2(-newPart.y,newPart.x);
        fragColor = newPart;
        return;
    }

    // look for a particle arriving at the current bucket
    vec4 partData;
    vec2 partCoord = FindArrivingParticle( fragCoord, partData );
    if( partCoord.x < 0. )
    {
        // no particle, empty this bucket
        fragColor = vec4(0.);
        return;
    }

    vec2 pos = GetPos( partData );
    vec2 vel = GetVel( partData );

    // integrate pos using current vel
    SetPos( partData, pos + vel );

    // integrate velocity

    // gravity
    vel += vec2(0.,-.05);

    // attractor
    vec2 attract = attractData.xy-fragCoord;
    float attractStrength = 20./(.1+dot(attract,attract)) - 0.004;
    if( attractStrength > 0. )
    {
        vel += attract*attractStrength;
    }

    // drag
    vel *= .995;
    // turbulence (divergence free curl could work great here but i just want to add a little variation)
    // NOTE this needs mipmapping to be disabled on the texture!
    vel += .125*normalize(textureLod( iChannel1, iTime + 4.*fragCoord/iResolution.xy, 0. ).xy-.5);
    // clamp to the maximum search radius. rsults would benefit from increasing R!
    vel = clamp(vel,-R,R);

    SetVel( partData, vel );

    Clip( partData, col0Data.xy, col1Data.xy, col2Data.xy );

    fragColor = partData;
}

// accumulation buffer for particle trails
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor.zw = vec2(0.);
    fragColor.xy = .75 * texture( iChannel1, uv ).xy;
    vec2 vel = texture(iChannel0,uv).xy;
    if( dot(vel,vel) != 0. ) fragColor += 1.;
}

// final render

// scene data
#define HOSE vec2(.5,.5)/iResolution.xy
#define ATTRACTOR vec2(1.5,0.5)/iResolution.xy
#define COL0 vec2(2.5,0.5)/iResolution.xy
#define COL1 vec2(3.5,0.5)/iResolution.xy
#define COL2 vec2(4.5,0.5)/iResolution.xy
#define HOSE_TARGET vec2(5.5,0.5)/iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // bg
    fragColor = vec4(.22);
    float period = 60.;
    vec2 gridLocal = mod( fragCoord, period );
    gridLocal.x = min( gridLocal.x, period - gridLocal.x );
    gridLocal.y = min( gridLocal.y, period - gridLocal.y );
    if( gridLocal.x < 2. && gridLocal.y < 8. ||
        gridLocal.x < 8. && gridLocal.y < 2. ) fragColor += .07;

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 col0Pos = texture(iChannel2, COL0).xy;
    vec2 col1Pos = texture(iChannel2, COL1).xy;
    vec2 col2Pos = texture(iChannel2, COL2).xy;

    // draw colliders
    vec2 off; float loff2;
    off = fragCoord - col0Pos; loff2 = dot(off,off);
    if( loff2 < 50.*50. ) fragColor = mix( fragColor, vec4(.5,.8,0.5,0.)*(.44 + .1*sign(off.x*off.y)),  smoothstep( 2500., 2400., loff2 ) );
    off = fragCoord - col1Pos; loff2 = dot(off,off);
    if( loff2 < 50.*50. ) fragColor = mix( fragColor, vec4(.5,.8,0.5,0.)*(.44 + .1*sign(off.x*off.y)),  smoothstep( 2500., 2400., loff2 ) );
    off = fragCoord - col2Pos; loff2 = dot(off,off);
    if( loff2 < 50.*50. ) fragColor = mix( fragColor, vec4(.5,.8,0.5,0.)*(.44 + .1*sign(off.x*off.y)),  smoothstep( 2500., 2400., loff2 ) );

    // no reason for the max except it makes the result a little less pixel-y!
    fragColor += max(abs(texture( iChannel1, uv )),abs(texture( iChannel1, uv+1./iResolution.xy )));

    vec2 hoseCoord = texture(iChannel2, HOSE).xy;
    vec2 hoseOff = fragCoord.xy - hoseCoord;
    if( length(hoseOff) < 11. )
    {
        vec2 target = texture(iChannel2, HOSE_TARGET).xy;
        vec2 dir = 2.8*normalize(target-fragCoord.xy);
        dir += .15*sin(15.*iTime)*vec2(-dir.y,dir.x);
        if( dot( hoseOff, dir ) < 0. )
            fragColor = .5*vec4(.5,.8,0.5,0.);
    }

    vec2 attractCoord = texture(iChannel2, ATTRACTOR).xy;
    vec2 attractOff = fragCoord - attractCoord;
    float attractDist = dot( attractOff, attractOff );
    if( attractDist < 6.*6. )
    {
        fragColor.xyz = .2*fragColor.xyz + vec3(0.2,.5,0.2);
    }
    else if( abs(attractDist-50.*50.) < 200. )
    {
        float stroke = fract(2.*atan(attractOff.y,attractOff.x) + 3.142)<.5 ? 1. : 0.;
        fragColor.xyz = mix( fragColor.xyz, .2*fragColor.xyz + vec3(0.2,.5,0.2), stroke );
    }
}

vec3 hsl2rgb( in vec3 c )
{
    float  ct = c.r+c.g+c.b; c.r = ct;c.g=ct;c.b=ct;
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec4 absGradient(sampler2D tex, vec2 uv) // from michael0884. https://shadertoy.com/view/wfK3Rw
{
    vec2 o = 0.5 / iResolution.xy;
    vec4 dX = texture(tex, uv + vec2(o.x, 0.0)) - texture(tex, uv - vec2(o.x, 0.0));
    vec4 dY = texture(tex, uv + vec2(0.0, o.y)) - texture(tex, uv - vec2(0.0, o.y));
    return 20.0*sqrt(dX * dX + dY * dY);
}

vec3 getPC(vec3 c) {
    float t =c.r+c.b+c.g;
    return 0.5 + 0.5*cos(vec3(0,2,4) + t*2.0);
}

vec3 drawSomething2(vec2 uv, vec3 col){ float d = abs(0.5-uv.x) + abs(0.5-uv.y) -0.2; if(d<0.){return col;}else{return mix(col, vec3(1), d*15.) ;} }
vec3 drawSomething(vec2 uv, vec3 col){ float d = distance(uv, vec2(0.5, 0.5)) - 0.2718; if(d<0.){return col;}else{return mix(col, vec3(1), d*15.) ;} }

vec2 getOUV(vec2 suv, vec4 sq){ 
    vec2 nuv = vec2(sq.x + ((sq.z-sq.x)*suv.x), sq.y + ((sq.w-sq.y)*suv.y));
    nuv.x = clamp(nuv.x,sq.x+0.003,sq.z-0.003);
    nuv.y = clamp(nuv.y,sq.y+0.003,sq.w-0.003);

    return nuv;
}

vec2 getSUV(vec2 uv, vec4 sq){  return vec2 ( (uv.x-sq.x) / (sq.z-sq.x) , (uv.y-sq.y) / (sq.w-sq.y) ); }

vec3 getTX(vec2 uv){ return texture(iChannel0, uv, 0.).rgb; }

bool isin(vec2 co, vec4 sq){ return ( co.x >= sq.x && co.x <= sq.z && co.y >= sq.y && co.y <= sq.w ); }

vec3 warp(int wt, float wp, vec2 suv, vec4 sq){
 float rad, angle; vec2 auv;
    switch (wt) {
    case 0:
    return getTX(getOUV(suv, sq)).gbr;
    case 1:
    return getPC(getTX(getOUV(suv, sq)));
    case 2:
    return hsl2rgb(getTX(getOUV(suv, sq)));
    case 3:
    return getTX(getOUV(suv+vec2(0,cos(suv.x*15.)/15.), sq));
    case 4:
    return getTX(getOUV(suv+vec2(sin(suv.y*10.)/10.,0), sq));
    case 5:
    return getTX(getOUV(suv+vec2(sin(suv.y*8.)/8.,sin(suv.x*9.)/9.), sq));
    case 6:
    suv-=vec2(0,0.5);rad = distance(suv, vec2(0))*3.;angle = (atan(suv.x,suv.y)/3.141);
    auv = vec2(rad*cos(angle),rad*sin(angle))-vec2(1,0.3);;return getTX(getOUV(auv, sq));
    case 7:
    suv+=vec2(0.5,0);rad = distance(suv, vec2(0.5,0))*2.;angle = (atan(suv.x,suv.y)/3.141);
    auv = vec2(rad*cos(angle),rad*sin(angle))-vec2(1.,0);return getTX(getOUV(auv, sq));
    case 8:
    return getTX(getOUV(suv, sq))*drawSomething2(suv, vec3(1,0,0));
    case 9:
    return mix(getTX(getOUV(suv, sq)),drawSomething(suv, vec3(0,1,0)), suv.x);
    case 10:
    return absGradient(iChannel0, getOUV(suv, sq)).rgb;
    case 11:
    suv*=2.;
    suv.x = fract(suv.x);suv.y = fract(suv.y);
    return getTX(getOUV(suv, sq));
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int smode = iFrame<2?0:int(ceil(texture(iChannel0, vec2(0), 0.).b));
    int wmode = iFrame<2?-1:int(ceil(texture(iChannel0, vec2(0), 0.).r));


    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float vb = 0.02;
    float hb = vb/ar;
    vec4 sc = vec4(hb,vb, (1./ar)-hb, 1.-vb);
    vec2 ss = vec2( -hb+(1.-((1./ar)))/3., (1.-(vb*5.))/4. );
    vec4  sc1 = vec4(sc.z+hb, vb, sc.z+hb+ss.x, vb+ss.y);
    vec4  sc2 = sc1;  sc2.xz+= (hb+ss.x);
    vec4  sc3 = sc2;  sc3.xz+= (hb+ss.x);
    vec4  sc4 = sc1;  sc4.yw+= (vb+ss.y);
    vec4  sc5 = sc2;  sc5.yw+= (vb+ss.y);
    vec4  sc6 = sc3;  sc6.yw+= (vb+ss.y);
    vec4  sc7 = sc4;  sc7.yw+= (vb+ss.y);
    vec4  sc8 = sc5;  sc8.yw+= (vb+ss.y);
    vec4  sc9 = sc6;  sc9.yw+= (vb+ss.y);
    vec4 sc10 = sc7; sc10.yw+= (vb+ss.y);
    vec4 sc11 = sc8; sc11.yw+= (vb+ss.y);
    vec4 sc12 = sc9; sc12.yw+= (vb+ss.y);
    fragColor = vec4(0.8);
    int wm = -1;
    vec4 scc = sc1;
    if (isin(uv,  sc1)) { wm=0;scc=sc1; }
    if (isin(uv,  sc2)) { wm=1;scc=sc2; }
    if (isin(uv,  sc3)) { wm=2;scc=sc3; }
    if (isin(uv,  sc4)) { wm=3;scc=sc4; }
    if (isin(uv,  sc5)) { wm=4;scc=sc5; }
    if (isin(uv,  sc6)) { wm=5;scc=sc6; }
    if (isin(uv,  sc7)) { wm=6;scc=sc7; }
    if (isin(uv,  sc8)) { wm=7;scc=sc8; }
    if (isin(uv,  sc9)) { wm=8;scc=sc9; }
    if (isin(uv, sc10)) { wm=9;scc=sc10; }
    if (isin(uv, sc11)) { wm=10;scc=sc11; }
    if (isin(uv, sc12)) { wm=11;scc=sc12; }
    if(wm>-1){fragColor = vec4( warp(wm,0.,getSUV(uv, scc), sc) ,1); }

    if(iMouse.z>0.){

    if (isin(iMouse.xy/iResolution.xy,  sc1)) { wmode=0; }
    if (isin(iMouse.xy/iResolution.xy,  sc2)) { wmode=1; }
    if (isin(iMouse.xy/iResolution.xy,  sc3)) { wmode=2; }
    if (isin(iMouse.xy/iResolution.xy,  sc4)) { wmode=3; }
    if (isin(iMouse.xy/iResolution.xy,  sc5)) { wmode=4; }
    if (isin(iMouse.xy/iResolution.xy,  sc6)) { wmode=5; }
    if (isin(iMouse.xy/iResolution.xy,  sc7)) { wmode=6; }
    if (isin(iMouse.xy/iResolution.xy,  sc8)) { wmode=7; }
    if (isin(iMouse.xy/iResolution.xy,  sc9)) { wmode=8; }
    if (isin(iMouse.xy/iResolution.xy, sc10)) { wmode=9; }
    if (isin(iMouse.xy/iResolution.xy, sc11)) { wmode=10; }
    if (isin(iMouse.xy/iResolution.xy, sc12)) { wmode=11; }

    if(smode==0 && wmode>-1){smode=1;}
    if(smode==3 && wmode>-1){smode=1;}
    }

    if (isin(uv, sc)) {
        if(smode==0) {
            fragColor = vec4(drawSomething(getSUV(uv,sc), vec3(0,0.5,1)), 1);
        } else if(smode==1){ 
            fragColor = vec4( warp(wmode,0.,getSUV(uv, sc), sc) ,1);
        } else if(smode>1){ 
            fragColor = vec4(getTX(getOUV(getSUV(uv, sc), sc)),1);
        }
    }
    if(smode==1){smode=2;wmode=-1;}
    if(smode==2 && iMouse.z<0.){smode=3;}
    if(floor(fragCoord) == vec2(0)){fragColor = vec4(float(wmode), 0, float(smode), 1);}

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;
    float radius = 0.4;
    vec2 centre = vec2(0.5);
    vec2 spos = uv-centre;
    float dist = length(spos);
    float ndist = dist/radius;
    float angle = (atan(spos.x,spos.y)+3.1416)/6.2832;

    vec3 col = vec3(1);

    float spirala = angle/2.;
    float spiralb = 0.5 + angle/2.;
    if(abs(ndist-spirala)<0.003){col=vec3(0);}
    if(abs(ndist-spiralb)<0.003){col=vec3(0);}
    if(abs(dist-radius)<0.005){col=vec3(0);}

    fragColor = vec4(col,1.0);
}

// Fork of "Polyhedra by sphere intersection" by Cotterzz. https://shadertoy.com/view/tf2XzV
// 2025-03-17 04:58:15
#define uv2 uv
// remove this define for fix, if you see an truncated icosahedron in the top left.
// It should be a regular icosahedron. See comments
vec3 ar(vec3 p, vec3 a, float r){float c = cos(r);float s = sin(r); p = p-a;return vec3(p.z*c-p.x*s, p.y, p.z*s+p.x*c)+a;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sphereSize = 1.1;
    float polySize = 0.15;
    vec2 uv = fragCoord/iResolution.y;
    float as = iResolution.x/iResolution.y;
    int st = -1; int fi = 32; float ed = 0.002; float ued = 0.0035; bool uie = false;
    #ifndef uv2
    vec2 uv2 = uv;
    #endif
    if(abs(uv.x-0.33*as)<ued ){ uie = true;}
    if(uv.x<0.33*as){
        if(abs(uv.y-0.33)<ued || abs(uv.y-0.66)<ued){ uie = true;}
        if(uv.y>0.66){
            uv2.y-=0.66;
            st = 11;
        } else if(uv.y>0.33){
            uv2.y-=0.33;
            fi = 12;
        } else {st=11;fi=20;}
        uv2*=3.;
        ed = 0.005;
    } else {
        uv2.x-=as*0.1666;
    }
    vec3 v[32];
    vec4 m = vec4(as/2., .5, 0., sphereSize-polySize);
    float z = 10.;
    float bz = -10.;
    v[12] = vec3(1.);v[13] = vec3(-1.,1,1);v[14] = v[13].yxz;v[15] = v[13].yzx;
    for (int i = 0; i<4; i++){
        v[i] = vec3(0., 1.618, 1.);
        v[i+20] = vec3(0., 0.618, 1.618);
        if(i>1){v[i].y = -v[i].y;v[i+20].y = -v[i+20].y;};
