// Fork of "Equation Clock" by Cotterzz. https://shadertoy.com/view/tfGGWc
// 2025-05-30 17:11:06

// so if the time is 10:23:42
// it plots the graph of z=(10x⁴y²)-(23x¹y⁰)+(42x²y³)
// (Or similar - I had to mix the exponents around for best results)
// ----

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = fragCoord/iResolution.y;
    cuv.x += 0.5 - (iResolution.x/iResolution.y)*0.5;

    int sec = int(mod(iDate.w,60.));
    int minute = int(mod(iDate.w/60.,60.));
    int hour = int(mod(iDate.w/3600.,24.));

    if(iMouse.z>0.){
       minute = int(floor((iMouse.y/iResolution.y) * 60.));
       hour = int(floor((iMouse.x/iResolution.x) * 24.));
    }

    float num = float(hour);
    int h1 = int(mod(num / pow(10.0,1.),10.0));
    int h2 = int(mod(num / pow(10.0,0.),10.0));
    num = float(minute);
    int m1 = int(mod(num / pow(10.0,1.),10.0));
    int m2 = int(mod(num / pow(10.0,0.),10.0));
    num = float(sec);
    int s1 = int(mod(num / pow(10.0,1.),10.0));
    int s2 = int(mod(num / pow(10.0,0.),10.0));


    float hours =float(hour);
    float minutes = float(minute);
    float secs = float(sec);
    float dc = 0.5 + distance(uv, vec2(0.5))/3.;

    float zoom = dc / pow(1.0 - dc*dc, 5.5); 

    //zoom = 3.;
    int MS = 8;  // -------------------------------------- If performance is bad, lower this number
    float xp = 1./iResolution.x;
    float yp = 1./iResolution.y;
    int count = 0;
    float result = 0.;float result2 = 0.;
    vec3 rf = vec3(0);
    for( int m=0; m<MS; m++ ){
        for( int n=0; n<MS; n++ ){
            vec2 nuv = cuv;
            nuv.x +=( xp/float(MS))*float(n);
            nuv.y += (yp/float(MS))*float(m);
            vec2 guv = (nuv*zoom)-(zoom/2.);
            float x = guv.x;
            float y = guv.y;
            result = -( p(x,m1) * p(y,s2) *hours )-( p(x,h1) * p(y,s1) * minutes)+( secs * p(x,h2) * p(y,m2) ) ;
            //result = -( p(x,m1) * cos(p(y,s2)) *hours )-sin( p(x,h1) * p(y,s1) * minutes)+( secs * cos(p(x,h2)) * sin(p(y,m2)) ) - p(atan( x/y ), s2)+atan(p(x, s1)/p(y,m2)); // :D
            count+=1;
            vec3 col = mix(getPC(result), getHC(vec3(result, 1.0, 0.5)), 0.35);
            rf+=col;
        }
    }

    rf /= float(count);

    fragColor = vec4(rf,1.0); vec4 fc = fragColor;

    float fdiff = (abs(fc.r-fc.g) + abs(fc.r-fc.b) + abs(fc.g-fc.b))/3.;

    fragColor = mix(vec4(0), fragColor, sqrt(fdiff));


}

#define PI 3.14159265359

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec3 addLight(vec3 position, vec3 normal, vec3 colour, vec3 lcol, vec3 lpos){
    vec3 ref = reflect(vec3(0.0, 0.0, -1.0), normal);
    vec3 diff = lpos - position;
    float ldist = length(diff);
    diff = diff/ldist;
    float specular = pow(max(0.0, dot(diff, ref)), 16.0);
    float diffuse = max(0.0, dot(diff,normal));
    vec3 col1 = colour*0.7;
    vec3 col2 = colour*0.4*(lcol*diffuse);
    col2 = col2+(lcol*specular);
    return col1+col2;
}

