        col = col/4.;
        col += (px(2.,0.) + px(2.,2.) + px(0.,2.) + px(-2.,0.) + px(-2.,2.)+ px(-2.,-2.)+ px(0.,-2.)+ px(2.,-2.))/8.;
    }
