#define limit 20
#define newc vec3(0.5+sin(iTime*7.),0.5+sin(iTime*17.),0.5+sin(iTime*3.))
#define black vec3(0.,0.,0.)
#define white vec3(1.,1.,1.)
vec3 get(in vec2 pix){ return texelFetch(iChannel0, ivec2(pix), 0).rgb;}
vec3 getn(in vec2 pix){ return texelFetch(iChannel1,(ivec2(pix)+iFrame*ivec2(17,113))&1023, 0).rgb;}
float tot(in vec3 c){ return c.r+c.g+c.b;}

void mainImage( out vec4 fragColor, in vec2 coord )
{
    vec2 uv = coord/iResolution.xy;
    vec3 col = black;
    vec2 centre = vec2(0.5, 0.5);
    vec2 point = centre; //iMouse.xy/iResolution.xy;
    float adist = distance(centre, point);
    float pdist = distance(uv, point);
    float cdist = distance(uv, centre);
    float tdist = pdist+cdist;
    float ddist = tdist-adist;
    if(ddist<=0.0001){ddist=0.0001;}

    ivec2 cursor = ivec2(0, 0);
    vec3 noise = getn(coord);
    vec3 old = get(coord);
    bool vertical = true;
    if(abs(uv.x-point.x)>abs(uv.y-point.y)){vertical = false;}
    bool up = true;
    if(uv.y>point.x){up=false;}
    bool right = true;
    if(uv.x>point.x){ right = false;}
    ivec2 dirA = cursor;
    ivec2 dirB = cursor;
    ivec2 dirC = cursor;
    ivec2 dirD = cursor;
    if(vertical&&up&&right){
        dirA = ivec2(0, 1);
        dirB = ivec2(1, 0);
        dirC = ivec2(-1, 0);
        dirD = ivec2(0, -1);
    } else if (vertical&&right){
        dirA = ivec2(0, -1);
        dirB = ivec2(1, 0);
        dirC = ivec2(-1, 0);
        dirD = ivec2(0, 1);
    } else if (vertical&&up){
        dirA = ivec2(0, 1);
        dirB = ivec2(-1, 0);
        dirC = ivec2(1, 0);
        dirD = ivec2(0, -1);
    } else if (vertical){
        dirA = ivec2(0, -1);
        dirB = ivec2(-1, 0);
        dirC = ivec2(1, 0);
        dirD = ivec2(0, 1);
    } else if(up&&right){
        dirA = ivec2(1, 0);
        dirB = ivec2(0, 1);
        dirC = ivec2(0, -1);
        dirD = ivec2(-1, 0);
    } else if (right){
        dirA = ivec2(1, 0);
        dirB = ivec2(0, -1);
        dirC = ivec2(0, 1);
        dirD = ivec2(-1, 0);
    } else if (up){
        dirA = ivec2(-1, 0);
        dirB = ivec2(0, 1);
        dirC = ivec2(0, -1);
        dirD = ivec2(1, 0);
    } else {
        dirA = ivec2(-1, 0);
        dirB = ivec2(0, -1);
        dirC = ivec2(0, 1);
        dirD = ivec2(1, 0);
    }

    float thresh = 1.51;

    if(pdist<0.005){ col = newc;
    } else if(tot(noise)>thresh){
        for(int i=0;i<limit;i++){
            if(tot(getn(coord+vec2(cursor)+vec2(dirA)))>thresh){
                cursor+=dirA; } else if (tot(getn(coord+vec2(cursor)+vec2(dirB)))>thresh){
                cursor+=dirB; } else if (tot(getn(coord+vec2(cursor)+vec2(dirC)))>thresh){
                cursor+=dirC; } else if (tot(getn(coord+vec2(cursor)+vec2(dirD)))>thresh){
                cursor+=dirD; }
            vec3 newcol = get(coord+vec2(cursor));
            if(newcol!=black){
                col = newcol;
                break;
            }
        }
    }
    fragColor = vec4(col, 1.);
}

// ABOUT
// I wanted to make an interactive pumpkin carving shader for halloween.
// This is my first attempt at a basic prototype, and it went a lot further than originally planned.
// Using a drawn texture to postively displace a sphere SDF and then subtracting that from the main pumpkin.
// The texture also provides an easy route to volumetric light.
// --
// It was a lot of fun to make and as a prototype/proof of concept it's a success that turned out better than expected
// However - it has plenty of issues and will benefit from a complete rebuild.
// Performance, compile time, messy code, untidy carve subtraction and the overall scene rendering could all be improved a lot.
// --
// I'm working on the next version.

// LICENSE
// Permission required for use outside shadertoy.


// INSTRUCTIONS
// Click and drag on pumpkin to carve.
// Select mirror mode (second button down on the left) to draw symmetrical patterns.
// Inverse mode to draw/fill pumpkin back in.
// Inverse mirror for both.
// Top button to set back to regualr carving.
// Brush size slider on the left.
// FPS indicator top right, blue/green is good, red is bad, adjust quality settings.
// Quality settings on the bottom right, Antialiasing, volumetric light, smoke and skies.
// up is higher quality but more GPU intensive.
// Watch the FPS indicator, you really need it in the green (55+ frames per second) if possible.
// Right-click and save-as to save image, hit rewind to reset.


// ---- Fast Improved Perlin Noise by TheTurk: https://www.shadertoy.com/view/slB3z3

uint hash(uint x, uint seed) {
    const uint m = 0x5bd1e995U;uint h = seed;uint k = x;k *= m;k ^= k >> 24;
    k *= m;h *= m;h ^= k;h ^= h >> 13;h *= m;h ^= h >> 15;return h;
}

uint hash(uvec3 x, uint seed){
    const uint m = 0x5bd1e995U;
    uint h = seed;uint k = x.x; 
    k *= m;k ^= k >> 24;k *= m;h *= m;h ^= k;
    k = x.y;k *= m;k ^= k >> 24;k *= m;h *= m;h ^= k;
    k = x.z;k *= m;k ^= k >> 24;k *= m;h *= m;h ^= k;
    h ^= h >> 13;h *= m;h ^= h >> 15;
    return h;
}

vec3 gradientDirection(uint hash) {
    vec3 r = vec3(0,1,-1);switch (int(hash) & 15) {
    case 0:return r.yyx;case 1:return r.zxy;
    case 2:return r.yzx;case 3:return r.zzx;
    case 4:return r.yyx;case 5:return r.zxy;
    case 6:return r.yxz;case 7:return r.zxz;
    case 8:return r.xyy;case 9:return r.xzy;
    case 10:return r.xzz;case 11:return r.xyz;
    case 12:return r.yyx;case 13:return r.zxy;
    case 14:return r.xzy;case 15:return r.xzz;
    }
}

float interpolate(float value1, float value2, float value3, float value4, float value5, float value6, float value7, float value8, vec3 t) {
    return mix(mix(mix(value1, value2, t.x), mix(value3, value4, t.x), t.y),mix(mix(value5, value6, t.x), mix(value7, value8, t.x), t.y), t.z);
}

vec3 fade(vec3 t) {return t * t * t * (t * (t * 6.0 - 15.0) + 10.0);}

float perlinNoise(vec3 position, uint seed) {
    vec3 floorPosition = floor(position);
    vec3 fractPosition = position - floorPosition;
    uvec3 cellCoordinates = uvec3(floorPosition);
    float value1 = dot(gradientDirection(hash(cellCoordinates, seed)), fractPosition);
    float value2 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 0, 0)), seed)), fractPosition - vec3(1, 0, 0));
    float value3 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 1, 0)), seed)), fractPosition - vec3(0, 1, 0));
    float value4 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 1, 0)), seed)), fractPosition - vec3(1, 1, 0));
    float value5 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 0, 1)), seed)), fractPosition - vec3(0, 0, 1));
    float value6 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 0, 1)), seed)), fractPosition - vec3(1, 0, 1));
    float value7 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 1, 1)), seed)), fractPosition - vec3(0, 1, 1));
    float value8 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 1, 1)), seed)), fractPosition - vec3(1, 1, 1));
    return interpolate(value1, value2, value3, value4, value5, value6, value7, value8, fade(fractPosition));
}

float perlinNoise(vec3 position, int frequency, int octaveCount, float persistence, float lacunarity, uint seed) {
    float value = 0.0;float amplitude = 1.0;float currentFrequency = float(frequency);uint currentSeed = seed;
    for (int i = 0; i < octaveCount; i++) {
        currentSeed = hash(currentSeed, 0x0U); // create a new seed for each octave
        value += perlinNoise(position * currentFrequency, currentSeed) * amplitude;
        amplitude *= persistence;currentFrequency *= lacunarity;
    }
    return value;
}


// --- Main Shader Code by Cotterzz

int AA = 0;
int VOLUMETRIC = 2;
int SMOKE = 2;
int SKIES = 2;

// store rd and ro for volumetric without AA
vec3 sro;
vec3 srd;

// UI Helper functions
float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float drawSquiggly(vec2 p, bool mirrored) {
    if (mirrored) p.x = -p.x;

    // Smaller squiggly line
    float y = sin(p.x * 190.0) * 0.007;
    float line = abs(p.y - y) - 0.002;
    return line;
}

// Draw mode button - returns color mixing factor
vec3 drawModeButton(vec2 uv, vec2 center, bool invers, bool mirrored, bool selected) {
    // Button color scheme
    vec3 regularColor = vec3(0.5, 0.2, 0.0);
    vec3 inverseColor = vec3(1.0, 1.0, 0.5);

    vec2 p = uv - center;
    float buttonSize = 0.048; // 60% of 0.08

    // Main circle
    float mainCircle = sdCircle(p, buttonSize);

    // Selection highlight
    float highlight = selected ? sdCircle(p, buttonSize + 0.006) : 1.0; // 60% of 0.01

    // Return background if outside button
    if (mainCircle > 0.0 && (!selected || highlight > 0.0)) {
        return vec3(-1.0); // Signal: no button here
    }

    // Selection highlight (blue border)
    if (selected && highlight < 0.0 && mainCircle > 0.0) {
        return vec3(0.3, 0.5, 1.0); // Blue highlight
    }

    // Button interior
    if (mainCircle < 0.0) {

        if (mirrored) {
            // Mirror mode: show two sets of icons with center line

            // Thinner center mirror line
            float centerLine = abs(p.x) - 0.0006; // 60% of 0.001
            if (centerLine < 0.0) {
                return invers ? regularColor : inverseColor; // Mirror line color
            }

            // Left side icons
            vec2 leftOffset = vec2(-0.021, 0.024); // 60% scaling
            float leftSmallCircle = sdCircle(p - leftOffset, 0.007); // 60% of 0.012

            // Left squiggly line - only on left side
            vec2 leftSquigglyPos = p - vec2(-0.021, 0); // 60% of -0.035
            float leftSquiggly = drawSquiggly(leftSquigglyPos.yx, false);
            // Clip squiggly to left side only
            if (leftSquigglyPos.y > 0.021) leftSquiggly = 1.0; // 60% of 0.035

            // Right side icons (mirrored)
            vec2 rightOffset = vec2(0.021, 0.024); // 60% scaling
            float rightSmallCircle = sdCircle(p - rightOffset, 0.007); // 60% of 0.012

            // Right squiggly line - only on right side
            vec2 rightSquigglyPos = p - vec2(0.021, 0); // 60% of 0.035
            float rightSquiggly = drawSquiggly(rightSquigglyPos.yx, true);
            // Clip squiggly to right side only
            if (rightSquigglyPos.y > 0.021) rightSquiggly = 1.0; // 60% of 0.035

            // Check for any icon element
            if (leftSmallCircle < 0.0 || rightSmallCircle < 0.0 || 
                leftSquiggly < 0.0 || rightSquiggly < 0.0) {
                return invers ? regularColor : inverseColor; // Icon color
            }

        } else {
            // Regular mode: single set of icons

            // Small circle
            vec2 smallOffset = vec2(0.018, 0.012); // 60% scaling
            float smallCircle = sdCircle(p - smallOffset, 0.009); // 60% of 0.015

            // Squiggly line
            vec2 squigglyPos = p - vec2(0.0, 0.012); // 60% of 0.02
            float squiggly = drawSquiggly(squigglyPos, false);
            if (squigglyPos.x > 0.021) squiggly = 1.0; // 60% of 0.035
            // Check for icon elements
            if (smallCircle < 0.0 || squiggly < 0.0) {
                return invers ? regularColor : inverseColor; // Icon color
            }
        }

        // Main button background
        return invers ? inverseColor : regularColor;
    }

    return vec3(-1.0); // No button
}

// Draw quality slider - returns color
vec3 drawQualitySlider(vec2 uv, vec2 center, float value, bool selected) {
    vec2 p = uv - center;

    // Slider track (vertical)
    float trackWidth = 0.008;
    float trackHeight = 0.08;
    float track = max(abs(p.x) - trackWidth, abs(p.y) - trackHeight);

    // Slider button (square, positioned based on value 0-4)
    float buttonY = (value / 4.0 - 0.5) * trackHeight * 1.8;
    vec2 buttonPos = vec2(0.0, buttonY);
    float buttonSize = 0.012;
    float button = max(abs(p.x - buttonPos.x) - buttonSize, abs(p.y - buttonPos.y) - buttonSize);

    if (track < 0.0 && button > 0.0) {
        return vec3(0.3, 0.3, 0.3); // Track color
    }

    if (button < 0.0) {
        return selected ? vec3(0.2, 0.6, 1.0) : vec3(0.8, 0.8, 0.8); // Button color
    }

    return vec3(-1.0); // No slider
}

// Draw brush slider - returns color  
vec3 drawBrushSlider(vec2 uv, vec2 center, float brushSize, bool selected) {
    vec2 p = uv - center;

    // Slider track (vertical)
    float trackWidth = 0.008;
    float trackHeight = 0.15;
    float track = max(abs(p.x) - trackWidth, abs(p.y) - trackHeight);

    // Map brush size (0.03 to 0.15) to slider position
    float normalizedSize = (brushSize - 0.03) / (0.15 - 0.03);
    float buttonY = (normalizedSize - 0.5) * trackHeight * 1.8;

    // Round button that scales with brush size
    vec2 buttonPos = vec2(0.0, buttonY);
    float buttonRadius = 0.008 + (normalizedSize * 0.015); // Grows with brush size
    float button = length(p - buttonPos) - buttonRadius;

    // Larger hit area for interaction (but don't show visually)
    float hitRadius = max(buttonRadius, 0.025); // Minimum hit area
    float hitArea = length(p - buttonPos) - hitRadius;

    if (track < 0.0 && button > 0.0) {
        return vec3(0.3, 0.3, 0.3); // Track color
    }

    if (button < 0.0) {
        return selected ? vec3(0.2, 0.6, 1.0) : vec3(0.8, 0.8, 0.8); // Button color
    }

    return vec3(-1.0); // No slider
}

// Draw FPS indicator
vec3 drawFPSIndicator(vec2 uv, vec2 center, float fps) {
    vec2 p = uv - center;
    float size = 0.01;

    if (abs(p.x) < size && abs(p.y) < size) {
        // Color code based on FPS
        if (fps >= 100.0) return vec3(0.4, 0.8, 1.0);      // Light blue
        else if (fps >= 55.0) return vec3(0.0, 1.0, 0.0);  // Green
        else if (fps >= 25.0) return vec3(1.0, 1.0, 0.0);  // Yellow
        else if (fps >= 15.0) return vec3(1.0, 0.5, 0.0);  // Orange
        else return vec3(1.0, 0.0, 0.0);                   // Red
    }

    return vec3(-1.0); // No indicator
}

// Sample the carving texture
float getCarvingDepth(vec3 p) {

    vec3 np = normalize(p);
    float theta = atan(np.z, np.x);
    float phi = acos(clamp(np.y, -1.0, 1.0));
    vec2 uv;
    uv.x = (theta + 3.14159) / (2.0 * 3.14159);
    uv.y = 1.0 - phi / 3.14159;
    return texture(iChannel0, uv).r;
}

// Utility functions
mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,s,-s,c);}
vec2 fold(vec2 p,float s){float a=3.142/s-atan(p.x,p.y),n=6.283/s;p*=rot(floor(a/n)*n);return p;}
float sminCubic(float a,float b,float k){float h=max(k-abs(a-b),0.)/k;return min(a,b)-h*h*h*k*(1./6.);}
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(b-a)/k, 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0-h);
}

// SDF functions
float sdTorus(vec3 p,vec2 t){return length(vec2(length(p.yz)-t.x,p.x))-t.y;}
float sdCube(vec3 p){vec3 q=abs(p)-.5;return length(max(q,0.))+min(max(q.x,max(q.y,q.z)),0.);}
float sdCappedCylinder(vec3 p,float h,float r){vec2 d=abs(vec2(length(p.xz),p.y))-vec2(h,r);return min(max(d.x,d.y),0.)+length(max(d,0.));}
float sdEllipsoid(vec3 p, vec3 r) {
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

// Realistic pumpkin shape with ridges
float sdPumpkin(vec3 p, float r) {
    // Main pumpkin body - flattened sphere
    vec3 bodyScale = vec3(1.0, 0.85, 1.0);
    float body = sdEllipsoid(p, bodyScale * r);

    // Add vertical ridges
    float angle = atan(p.z, p.x);
    float ridgeCount = 12.0;
    float ridgePhase = sin(angle * ridgeCount) * 0.15;

    // Modulate the ridges based on height
    float heightFactor = smoothstep(-0.6, 0.4, p.y);
    ridgePhase *= heightFactor;

    body += ridgePhase * r * 0.1;

    // Add slight indentation at top and bottom
    float topIndent = smoothstep(0.3, 0.5, p.y) * 0.2 * r;
    float bottomIndent = smoothstep(-0.5, -0.3, p.y) * 0.03 * r;

    return body+ topIndent + bottomIndent;
}

// Pumpkin stem
float sdStem(vec3 p) {
    vec3 stemPos = p - vec3(0.0, 0.45, 0.0);

    // Curved stem
    stemPos.x += sin(stemPos.y * 3.0) * 0.1;
    stemPos.z += cos(stemPos.y * 2.5) * 0.08;

    float stem = sdCappedCylinder(stemPos, 0.05, 0.19);

    // Add some bumps to the stem
    float noise = sin(stemPos.y * 15.0) * sin(stemPos.x * 20.0) * 0.01;
    return stem + noise;
}


float sdSphere(vec3 p, float r) {
    return length(p) - r;
}


// Candle flame SDF
float sdFlame(vec3 p) {
    p.x = abs(p.x)*2.5;
    vec3 flamePos = p - vec3(0.45, -0.05, 0.0);
    float it = iTime+p.x;
    // Flame shape - teardrop
    float flameHeight = 0.02;
    float flameWidth = 0.02;

    // Add flickering animation
    float flicker = sin(it * 19.0) * 0.02 + cos(it * 37.0) * 0.015;
    flamePos.y += flicker;
    flamePos.x += sin(it * 13.) * sin(it * 1.9) *0.02;

    // Flame SDF - teardrop shape
    float flame = length(vec2(length(flamePos.xy) - flameHeight * (1.0 - flamePos.y/flameHeight), 
                             flamePos.y)) - flameWidth;
    flame = max(flame, -flamePos.y);
    flame = max(flame, flamePos.y - flameHeight);

    return flame;
}


// Main pumpkin SDF (solid shell only)
float map(vec3 p) {
    float outer = sdPumpkin(p, 0.6);
    float inner = sdSphere(p, 0.5);
    float shell = max(outer, -inner);

    // Enhanced carving system - subtract carved areas
    float carving = getCarvingDepth(p);
    if (carving > 0.3) {
        float carveIntensity = smoothstep(0.3, 1.0, carving);
        float expandedInner = sdSphere(p, 0.5 + carveIntensity * 0.2);
        shell = max(shell, -expandedInner);
    }

    // Add stem
    float stem = sdStem(p);
    shell = smin(shell, stem, 0.02);

    return shell;
}

// Ground plane SDF
float sdGround(vec3 p) {
    return p.y + 0.8; // Ground at y = -0.8
}

// Map function that includes flame and ground for rendering
float mapWithFlame(vec3 p) {
    float pumpkinDist = map(p);
    float flameDist = sdFlame(p);
    float groundDist = sdGround(p);

    // Only render flame if inside pumpkin
    float inner = sdSphere(p, 0.45);
    if (inner < 0.0) {
        return min(min(pumpkinDist, flameDist), groundDist);
    }

    return min(pumpkinDist, groundDist);
}


vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

// Simple raymarch
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;

    for (int i = 0; i < 200; i++) {
        vec3 p = ro + rd * t;
        float d = mapWithFlame(p);

        if (abs(d) < 0.0001) return t;
        if (t > 10.0) break;

        t += d * 0.4;
    }
    return t;
}

// Simple volumetric light extending from pumpkin surface
vec3 renderVolumetricLayers(vec3 ro, vec3 rd, float startDist) {
    vec3 volumetricColor = vec3(0.0);

    // Candle light properties
    vec3 candlePos = vec3(0.0, -0.1, 0.0);
    float flicker = 0.7 + 0.07 * sin(iTime*37.) + 0.06 * sin(iTime * 19.);
    vec3 lightColor = vec3(1.5, 1.0, 0.5) * flicker;

    // Start from pumpkin surface and extend toward camera
     int numSteps = VOLUMETRIC * 12;
    float maxDist = 0.3 + float(VOLUMETRIC)*0.3;
    float stepSize = maxDist / float(numSteps);

    // Go FROM surface TOWARD camera (negative direction)
    float t = startDist - 0.02;

    for (int i = 0; i < numSteps; i++) {
        //if (t <= 0.0) break; // Don't go behind camera

        vec3 p = ro + rd * t;
        float carving = getCarvingDepth(p);

        if (carving > 0.3) {
            float carveIntensity = smoothstep(0.3, 1.0, carving);

            float distToCandle = length(p - candlePos);
            float attenuation = 1.0 / (1.0 + distToCandle * distToCandle * 0.8);

            // Distance from surface (how far light has traveled)
            float distanceFromSurface = startDist - t;
            float falloff = exp(-distanceFromSurface * 1.5);

            // Layer effect - expanding shells that move outward
            float layerSpeed = 2.0;
            float layerSpacing = 0.3;
            float expandingLayers = sin((distanceFromSurface - iTime * layerSpeed) / layerSpacing) * 0.4 + 1.0;

            float contribution = carveIntensity * attenuation * falloff * expandingLayers * 0.06;
            float smoke;
            if (SMOKE>0){
            vec3 sp = p;
            sp+=1.;
            sp.x+=iTime/10.;
            sp*=10.;
            if (SMOKE>1){
            smoke = perlinNoise(sp, 1, (SMOKE*2)-2, 0.5, 2.0, 123456u);
            }else{
            smoke = perlinNoise(sp, 123456u);
            }
            smoke = (smoke + 1.0) * 0.5;
            contribution*=smoke;
            }

            volumetricColor += lightColor * contribution;
        }

        t -= stepSize; // Move toward camera
    }
    if (SMOKE>0){
    volumetricColor*=3.;
    }
    return volumetricColor;
}


// Get subsurface scattering color based on depth from center
vec3 getSubsurfaceColor(float distFromCenter) {
    // Interior glow color - warm candlelight
    vec3 innerGlow = vec3(1.2, 0.8, 0.3);

    // Outer pumpkin color
    vec3 outerColor = vec3(0.8, 0.35, 0.05);

    // Map distance to depth (0 = inside, 1 = outside surface)
    float depth = (distFromCenter - 0.4) / 0.2; // 0.6 to 0.7 range
    depth = clamp(depth, 0.0, 1.0);

    // Create gradient
    vec3 subsurfaceColor = mix(innerGlow, outerColor, depth);

    // Add transmission falloff
    float transmission = exp(-depth * 5.0);

    return subsurfaceColor * (0.5 + transmission * 0.5);
}



void mainImage0(out vec4 fragColor, in vec2 fragCoord, vec4 bg) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Static camera setup - moved pumpkin down slightly (compromise position)
    vec3 ro = vec3(0.0, 0.1, -2.5);
    vec3 rd = normalize(vec3(uv, 1.5));

    // Background
    vec3 col = bg.rgb;

    // Raymarch solid surfaces
    float t = raymarch(ro, rd);

    if (t < 10.0) {
        vec3 p = ro + rd * t;
        vec3 n = calcNormal(p);
        float distFromCenter = length(p);
        float carving = getCarvingDepth(p);

        // Check if we hit the flame
        float flameDist = sdFlame(p);
        float groundDist = sdGround(p);
        bool isFlame = flameDist < 0.01 && sdSphere(p, 0.45) < 0.0;
        bool isGround = groundDist < 0.01;

        if (isFlame) {
            // Render flame
            float flameIntensity = 1.0 - smoothstep(0.0, 0.04, flameDist);
            float flicker = 0.8 + 0.2 * sin(iTime * 37.0) + 0.15 * sin(iTime * 19.0);

            // Flame color gradient from blue at base to yellow-orange at tip
            float heightGradient = (p.y + 0.1) / 0.15;
            vec3 flameColor = mix(
                vec3(1.0, 0.9, 0.8),  // Blue base
                vec3(0.3, 0.1, 0.0),  // Orange tip
                heightGradient
            );

            col = flameColor * flameIntensity * flicker * 3.0;
        } else if (isGround) {
            // Render ground
            vec3 groundColor = vec3(0.3, 0.8, 0.3); // Dark earth tone

            // Key light - strong moonlight from above-behind-side
            vec3 keyLightDir = normalize(vec3(-0.7, 1.2, -0.8));
            vec3 keyLightColor = vec3(0.7, 0.8, 1.0) * 1.5;

            // Simple ground lighting
            float keyDiff = max(dot(n, keyLightDir), 0.0);
            vec3 keyDiffuse = groundColor * keyLightColor * keyDiff * 0.6;

            // Ambient for ground
            vec3 ambient = groundColor * vec3(0.02, 0.03, 0.05);

            // Subtle glow from pumpkin onto ground
            vec3 pumpkinPos = vec3(0.0, 0.0, 0.0);
            float distToPumpkin = length(p - pumpkinPos);
            float pumpkinGlow = exp(-distToPumpkin * 1.5) * 0.1;
            vec3 pumpkinLightColor = vec3(1.0, 0.6, 0.2);

            col = ambient + keyDiffuse + pumpkinLightColor * pumpkinGlow;
        } else {
            // Enhanced lighting system with key light
            vec3 candlePos = vec3(0.0, -0.3, 0.0);
            vec3 candleLight = normalize(candlePos - p);
            vec3 viewDir = normalize(ro - p);

            // Key light - strong moonlight from above-behind-side
            vec3 keyLightDir = normalize(vec3(-0.7, 1.2, -0.8));;//normalize(vec3(0.5, 0.7, 2.5));
            vec3 keyLightColor = vec3(0.8, 0.9, 1.0) * 1.5; // Cool blue moonlight
            // Key light contribution
            float keyDiff = max(dot(n, keyLightDir), 0.0);
            // Flickering candle light intensity
            float flicker = 0.8 + 0.07 * sin(iTime * 37.0) + 0.06 * sin(iTime * 19.0);
            vec3 candleLightColor = vec3(1.2, 0.8, 0.4) * flicker;

            // Are we in a carved area?
            if (carving > 0.6) {
                // Enhanced subsurface scattering for carved areas
                vec3 subsurfaceCol = getSubsurfaceColor(distFromCenter);

                // Distance-based light falloff
                float lightDist = length(candlePos - p);
                float attenuation = 1.5/ (2.0 + lightDist * 2.0);

                // Inner glow effect
                float innerGlow = exp(-lightDist * 3.0);

                col = subsurfaceCol * candleLightColor * (attenuation + innerGlow * 1.5);

                // Enhanced rim lighting
               // float rim = 2.0 - abs(dot(viewDir, n));
                //col += candleLightColor * 4.8 * attenuation * flicker;
                col*=1.2;
            } else {
                // Enhanced pumpkin surface material
                vec3 pumpkinColor = vec3(0.9, 0.4, 0.05);

                // Add surface variation based on ridges
                float angle = atan(p.z, p.x);
                float ridgePhase = sin(angle * 12.0) * 0.4 + 0.5;
                ridgePhase += ridgePhase-keyDiff; ridgePhase*=.55;
                pumpkinColor *= 0.5 + candleLightColor * pow(ridgePhase, 8.);
                pumpkinColor += (pumpkinColor*=pumpkinColor)/5.;
                pumpkinColor +=  vec3(1.0, 0.3, 0.02); 

                if(p.z>0.0){pumpkinColor *=1.3;} else { pumpkinColor *=0.5;}
                // Stem coloring
                if (p.y > 0.38) {
                    pumpkinColor = mix(vec3(0.9, 0.4, 0.05), vec3(0.3, 0.5, 0.2), smoothstep(0.5, 0.7, p.y));
                }


                vec3 keyDiffuse = pumpkinColor * keyLightColor * keyDiff * 0.8;

                // Candle light contribution
                float lightDist = length(candlePos - p);
                float attenuation = 1.0 / (1.0 + lightDist * lightDist * 0.5);
                float candleDiff = max(dot(n, candleLight), 0.0);
                vec3 candleDiffuse = pumpkinColor * candleLightColor * candleDiff * attenuation * 0.6;

                // Ambient lighting
                vec3 ambient = pumpkinColor * vec3(0.05, 0.08, 0.15);

                // Specular highlights from key light
                vec3 keyHalfDir = normalize(keyLightDir + viewDir);
                float keySpec = pow(max(dot(n, keyHalfDir), 0.0), 16.0);
                vec3 keySpecular = keyLightColor * keySpec * 0.3;

                // Specular highlights from candle
                vec3 candleHalfDir = normalize(candleLight + viewDir);
                float candleSpec = pow(max(dot(n, candleHalfDir), 0.0), 8.0);
                vec3 candleSpecular = candleLightColor * candleSpec * 0.3 * attenuation;

                col = ambient + keyDiffuse + candleDiffuse + keySpecular + candleSpecular;
            }
        }
    }


   sro=ro;
   srd=rd;

    // Add UI buttons overlay
    vec2 screenUV = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // UI button positions (moved further left)
    vec2 buttonPos1 = vec2(-0.78, 0.42);   // Regular mode
    vec2 buttonPos2 = vec2(-0.78, 0.31);   // Mirror mode  
    vec2 buttonPos3 = vec2(-0.78, 0.20);   // Inverse mode
    vec2 buttonPos4 = vec2(-0.78, 0.09);   // Mirror + Inverse mode

    // Read current mode state from BufferA
    bool currentInverse = texelFetch(iChannel0, ivec2(38,0), 0).x > 0.5;
    bool currentMirror = texelFetch(iChannel0, ivec2(39,0), 0).x > 0.5;
    int currentMode = (currentMirror ? 2 : 0) + (currentInverse ? 1 : 0);

    // Read quality settings and brush size from BufferA and update global variables
    AA = int(floor(texelFetch(iChannel0, ivec2(40,0), 0).x * 5.0));
    VOLUMETRIC = int(floor(texelFetch(iChannel0, ivec2(41,0), 0).x * 5.0));
    SMOKE = int(floor(texelFetch(iChannel0, ivec2(42,0), 0).x * 5.0));


    float aaQuality = float(AA);
    float volumetricQuality = float(VOLUMETRIC);
    float smokeQuality = float(SMOKE);
    float skiesQuality = float(SKIES);
    float brushSize = 0.03 + texelFetch(iChannel0, ivec2(44,0), 0).x * (0.15 - 0.03);

    // Calculate FPS using stored frame timing data
    //float currentTime = iTime;
    //float lastFrameTime = texelFetch(iChannel0, ivec2(45,0), 0).x;
    float deltaTime = texelFetch(iChannel0, ivec2(47,0), 0).x;
    float fps = (deltaTime > 0.0) ? 1.0 / deltaTime : 60.0;

    // Quality slider positions (moved up from bottom)
    vec2 aaSliderPos = vec2(0.71, -0.37);
    vec2 volumetricSliderPos = vec2(0.76, -0.37);
    vec2 smokeSliderPos = vec2(0.81, -0.37);
    vec2 skiesSliderPos = vec2(0.86, -0.37);

    // Brush slider position (moved down by half its length)
    vec2 brushSliderPos = vec2(-0.78, -0.155);

    // FPS indicator position (between original and current)
    vec2 fpsPos = vec2(0.85, 0.465);

    // Draw mode buttons
    vec3 btn1 = drawModeButton(screenUV, buttonPos1, false, false, currentMode == 0);
    if (btn1.x >= 0.0) col = btn1;

    vec3 btn2 = drawModeButton(screenUV, buttonPos2, false, true, currentMode == 2);
    if (btn2.x >= 0.0) col = btn2;

    vec3 btn3 = drawModeButton(screenUV, buttonPos3, true, false, currentMode == 1);
    if (btn3.x >= 0.0) col = btn3;

    vec3 btn4 = drawModeButton(screenUV, buttonPos4, true, true, currentMode == 3);
    if (btn4.x >= 0.0) col = btn4;

    // Draw quality sliders
    vec3 aaSlider = drawQualitySlider(screenUV, aaSliderPos, aaQuality, false);
    if (aaSlider.x >= 0.0) col = aaSlider;

    vec3 volumetricSlider = drawQualitySlider(screenUV, volumetricSliderPos, volumetricQuality, false);
    if (volumetricSlider.x >= 0.0) col = volumetricSlider;

    vec3 smokeSlider = drawQualitySlider(screenUV, smokeSliderPos, smokeQuality, false);
    if (smokeSlider.x >= 0.0) col = smokeSlider;

    vec3 skiesSlider = drawQualitySlider(screenUV, skiesSliderPos, skiesQuality, false);
    if (skiesSlider.x >= 0.0) col = skiesSlider;

    // Draw brush slider
    vec3 brushSlider = drawBrushSlider(screenUV, brushSliderPos, brushSize, false);
    if (brushSlider.x >= 0.0) col = brushSlider;

    // Draw FPS indicator
    vec3 fpsIndicator = drawFPSIndicator(screenUV, fpsPos, fps);
    if (fpsIndicator.x >= 0.0) col = fpsIndicator;

    fragColor = vec4(col, 1.0);
}

vec4 mainImageBG(vec2 u) {
SKIES = int(floor(texelFetch(iChannel0, ivec2(43,0), 0).x * 5.0));
    float d, // total distance marched
          n, // noise iterator
          i, // raymarch iterator
          s, // signed distance
          t = iTime/6.;
    vec3  p = iResolution;    

    // scale coords
    u = -(u-p.xy/2.)/p.y;

    vec4 o = vec4(0);
    for(; i++<float(SKIES)*24.;) {

        p = vec3(u*d,d+t);

        p.z *= .4;

        p.y += 4.;

        p.y += sin(.2*t+p.x*.2);

        // noise loop start, march
        for (n = .01; n < 1.+float(SKIES)/2.; n += n)
            p += abs(dot(cos(p.x*.1 + p.z *.3+  t + p / n ), vec3(.33))) * n;
            d += s = .2 + .7 * abs(p.y),
            o += 1./s;

    }
    o = vec4(0.2,0.2,0.3,0)*o/1e2;
    o = max(o, vec4(0));
    return o;
}

void mainImage(out vec4 o, vec2 u) 
{ 
