// This is for the glow/bloom effect, main code in Buffer A
vec3 samplef(vec2 tc);
vec3 blur(vec2 tc, float offs);
vec3 highlights(vec3 pixel, float thres);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 tc = fragCoord.xy / iResolution.xy;
	vec3 color = blur(tc, 2.0);
	color += blur(tc, 3.0);
	color += blur(tc, 5.0);
	color += blur(tc, 7.0);
	color /= 4.0;

	color += samplef(tc);



	fragColor.xyz = color;
	fragColor.w = 1.0;
}

vec3 samplef(vec2 tc)
{
	return texture(iChannel0, tc).xyz;
}

vec3 hsample(vec2 tc)
{
	return highlights(samplef(tc), 0.6);
}

vec3 blur(vec2 tc, float offs)
{
	vec4 xoffs = offs * vec4(-2.0, -1.0, 1.0, 2.0) / iResolution.x;
	vec4 yoffs = offs * vec4(-2.0, -1.0, 1.0, 2.0) / iResolution.y;

	vec3 color = vec3(0.0, 0.0, 0.0);
	color += hsample(tc + vec2(xoffs.x, yoffs.x)) * 0.00366;
	color += hsample(tc + vec2(xoffs.y, yoffs.x)) * 0.01465;
	color += hsample(tc + vec2(    0.0, yoffs.x)) * 0.02564;
	color += hsample(tc + vec2(xoffs.z, yoffs.x)) * 0.01465;
	color += hsample(tc + vec2(xoffs.w, yoffs.x)) * 0.00366;

	color += hsample(tc + vec2(xoffs.x, yoffs.y)) * 0.01465;
	color += hsample(tc + vec2(xoffs.y, yoffs.y)) * 0.05861;
	color += hsample(tc + vec2(    0.0, yoffs.y)) * 0.09524;
	color += hsample(tc + vec2(xoffs.z, yoffs.y)) * 0.05861;
	color += hsample(tc + vec2(xoffs.w, yoffs.y)) * 0.01465;

	color += hsample(tc + vec2(xoffs.x, 0.0)) * 0.02564;
	color += hsample(tc + vec2(xoffs.y, 0.0)) * 0.09524;
	color += hsample(tc + vec2(    0.0, 0.0)) * 0.15018;
	color += hsample(tc + vec2(xoffs.z, 0.0)) * 0.09524;
	color += hsample(tc + vec2(xoffs.w, 0.0)) * 0.02564;

	color += hsample(tc + vec2(xoffs.x, yoffs.z)) * 0.01465;
	color += hsample(tc + vec2(xoffs.y, yoffs.z)) * 0.05861;
	color += hsample(tc + vec2(    0.0, yoffs.z)) * 0.09524;
	color += hsample(tc + vec2(xoffs.z, yoffs.z)) * 0.05861;
	color += hsample(tc + vec2(xoffs.w, yoffs.z)) * 0.01465;

	color += hsample(tc + vec2(xoffs.x, yoffs.w)) * 0.00366;
	color += hsample(tc + vec2(xoffs.y, yoffs.w)) * 0.01465;
	color += hsample(tc + vec2(    0.0, yoffs.w)) * 0.02564;
	color += hsample(tc + vec2(xoffs.z, yoffs.w)) * 0.01465;
	color += hsample(tc + vec2(xoffs.w, yoffs.w)) * 0.00366;

	return color;
}

vec3 highlights(vec3 pixel, float thres)
{
	float val = (pixel.x + pixel.y + pixel.z) / 3.0;
	return pixel * smoothstep(thres - 0.1, thres + 0.1, val);
}

void mainImage(out vec4 c,vec2 p) {
    vec2 r = iResolution.xy;
    vec2 h = (p / r * 2. - 1.) * sqrt(r / r.yx);
    vec3 g = vec3(1,h.yx) / (dot(h,h) + 1.) + vec3(-.5,0,.5);
    h = g.xy / dot(g,g);
    h = vec2(atan(h.x,h.y),log(length(h))) / 6.28;
    h += iTime * .1;
    vec2 h2 = h;
    h *= mat2(8,5,-5,8);
    h = fract(h);
    c = vec4(h,h);
}

#define PI 3.14159265359
// Hash function for randomization
float hash(float p) {
    vec2 p2 = fract(vec2(p * 5.3983, p * 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337);
}

float GetCharacter(int cx, int cy, vec2 cuv) {
    cuv = clamp(cuv, vec2(0), vec2(1));
    float tx = (cuv.x/16.) + (float(cx)*(1./16.));
    float ty = (cuv.y/16.) + (float(cy)*(1./16.));
    return -.5 + texture(iChannel0, vec2(tx, ty)).a;
}

float GetCarving2dDistance(const in vec3 vPos, float seed) {
    float frontZ = -2.7;
    vec2 surfaceXY = vPos.xy * (frontZ / vPos.z);
    vec2 uv = surfaceXY * vec2(.2,.4) + vec2(.5,.3);

    // Randomize character based on seed
    int cx = int(mod(hash(seed) * 100.0, 16.0));
    int cy = int(mod(hash(seed + 1.0) * 100.0, 16.0));

    float userCarving = GetCharacter(cx, cy, uv);
    return userCarving;
}

float GetCarvingDistance(const in vec3 vPos, float seed) {
    float fDist = (length(vPos * vec3(1.0, 1.4, 1.0)) - 2.7) / 1.5;
    float fFaceDist = GetCarving2dDistance(vPos, seed);
    float fRearDist = vPos.z;

    if(fRearDist > fFaceDist) {
        fFaceDist = fRearDist;
    }

    if(fFaceDist < fDist) {
        fDist = fFaceDist;
    }

    float fR = length(vPos.xz);
    float fLidDist = dot(vec2(fR, vPos.y), normalize(vec2(1.0, -1.3)));
    fLidDist = abs(fLidDist) - 0.03;

    if(fLidDist < fDist) {
        fDist = fLidDist;
    }

    return fDist;
}

float GetPumpkinDistance(out vec4 vOutUVW_Id, const in vec3 vPos) {
    vec3 vSphereOrigin = vec3(0.0, 0.0, 0.0);
    float fSphereRadius = 3.0;
    vec3 vOffset = vPos - vSphereOrigin;
    float fFirstDist = length(vOffset);

    float fOutDist;
    if(fFirstDist > 3.5) {
        fOutDist = fFirstDist - fSphereRadius;
    } else {
        float fAngle1 = atan(vOffset.x, vOffset.z);
        float fSin = sin(fAngle1 * 5.5);
        fSin = 1.0 - sqrt(abs(fSin));
        vOffset *= 0.95 + fSin * vec3(0.1, 0.02, 0.08);
        vOffset.y *= 1.0 + 0.5 * (fSphereRadius - length(vOffset.xz)) / fSphereRadius;
        fOutDist = length(vOffset) - fSphereRadius;
    }

    vec4 vSphere1UVW_Id = vec4(normalize(vPos - vSphereOrigin), 3.0);
    vOutUVW_Id = vSphere1UVW_Id;

    vec3 vStalkOffset = vPos;
    vStalkOffset.x += -(vStalkOffset.y - fSphereRadius) * 0.1;
    float fDist2d = length(vStalkOffset.xz);
    float fStalkDist = fDist2d - 0.2;
    fStalkDist = max(fStalkDist, vPos.y - 2.5 + vPos.x * 0.25);
    fStalkDist = max(fStalkDist, -vPos.y);

    if(fStalkDist < fOutDist) {
        fOutDist = fStalkDist;
        vOutUVW_Id = vSphere1UVW_Id;
        vOutUVW_Id.w = 2.0;
    }

    return fOutDist;
}

float GetSceneDistance(out vec4 vOutUVW_Id, const in vec3 vPos, float seed) {
    vec3 vPumpkinDomain = vPos;
    vec4 vPumpkinUVW_Id;
    float fPumpkinDist = GetPumpkinDistance(vPumpkinUVW_Id, vPumpkinDomain);

    float fCarvingDist = GetCarvingDistance(vPumpkinDomain, seed);

    if(-fCarvingDist > fPumpkinDist) {
        fPumpkinDist = -fCarvingDist;
        vPumpkinUVW_Id = vec4(4.0);
    }

    vOutUVW_Id = vPumpkinUVW_Id;
    return fPumpkinDist;
}

vec3 GetSceneNormal(const in vec3 vPos, float seed) {
    const float fDelta = 0.001;
    vec3 vDir1 = vec3(1.0, -1.0, -1.0);
    vec3 vDir2 = vec3(-1.0, -1.0, 1.0);
    vec3 vDir3 = vec3(-1.0, 1.0, -1.0);
    vec3 vDir4 = vec3(1.0, 1.0, 1.0);

    vec4 vUnused;
    float f1 = GetSceneDistance(vUnused, vPos + vDir1 * fDelta, seed);
    float f2 = GetSceneDistance(vUnused, vPos + vDir2 * fDelta, seed);
    float f3 = GetSceneDistance(vUnused, vPos + vDir3 * fDelta, seed);
    float f4 = GetSceneDistance(vUnused, vPos + vDir4 * fDelta, seed);

    vec3 vNormal = vDir1 * f1 + vDir2 * f2 + vDir3 * f3 + vDir4 * f4;
    return normalize(vNormal);
}

vec3 mainPC(vec2 uv, float seed, float angle) {
    // Convert UV to window coordinates
    vec2 vWindow = uv * 2.0 - 1.0;
    vec2 cuv = vWindow;
    float sangle =atan(cuv.y,cuv.x);
    float clength = length(cuv);
    cuv.x = cos(sangle+angle)*clength;
    cuv.y = sin(sangle+angle)*clength;
    vWindow = cuv;
    // Fixed camera setup
    float fDist = 6.0;
    float fAngle = 3.141;
    float fHeight = 2.5;

    vec3 vCameraPos = vec3(sin(fAngle) * fDist, fHeight, cos(fAngle) * fDist);
    vec3 vCameraTarget = vec3(0.0, -0.5, 0.0);

    // Camera ray direction
    vec3 vForward = normalize(vCameraTarget - vCameraPos);
    vec3 vRight = normalize(cross(vec3(0.0, 1.0, 0.0), vForward));
    vec3 vUp = normalize(cross(vForward, vRight));
    vec3 vRayDir = normalize(vWindow.x * vRight + vWindow.y * vUp + vForward * 1.5);

    // Raymarch
    vec3 vRayOrigin = vCameraPos;
    vec4 vUVW_Id = vec4(0.0);
    vec3 vPos = vec3(0.0);
    float t = 0.01;

    for(int i = 0; i < 64; i++) {
        vPos = vRayOrigin + vRayDir * t;
        float fDist = GetSceneDistance(vUVW_Id, vPos, seed);
        t += fDist;
        if(abs(fDist) < 0.001) break;
        if(t > 100.0) {
            return vec3(0.0); // Black background instead of sky
        }
    }

    // If we hit something
    if(t < 100.0 && vUVW_Id.w > 0.0) {
        vec3 vNormal = GetSceneNormal(vPos, seed);

        // Simple lighting
        vec3 vLightDir = normalize(vec3(0.5, 1.0, 0.5));
        float fDiffuse = max(0.0, dot(vNormal, vLightDir)) * 0.7 + 0.3;

        // Pumpkin color based on object ID
        vec3 vColor;
        if(vUVW_Id.w == 3.0) {
            // Pumpkin surface
            vColor = vec3(1.0, 0.5, 0.1);
        } else if(vUVW_Id.w == 2.0) {
            // Stalk
            vColor = vec3(0.7, 1.0, 0.4);
        } else if(vUVW_Id.w == 4.0) {
            // Carving (glowing)
            vColor = vec3(1.0, 0.8, 0.3);
            fDiffuse = 01.7; // Make carving brighter
        } else {
            vColor = vec3(0.5);
        }

        return vColor * fDiffuse;
    }

    return vec3(0.0); // Black background
}

void mainImage0(out vec4 c, vec2 p) {



    int pos = int(p.x)%2 + (int(p.y)%2 * 2);
    float ra = texture(iChannel1, p/iResolution.xy, 0.).r;
    float rb = texture(iChannel1, (p+vec2(1,0))/iResolution.xy, 0.).r;
    float rc = texture(iChannel1, (p+vec2(0,1))/iResolution.xy, 0.).r;
    float dx = dFdx(ra);
    float dy = dFdy(ra);
    if(pos == 1 || pos == 3){ dx = rb-ra; }
    if(pos == 2 || pos == 3){ dy = rc-ra; }
    float dz = sqrt((dx*dx) + (dy*dy));
    float a = atan(dx,dy);


    vec2 r = iResolution.xy;
    vec2 h = (p / r * 2. - 1.) * sqrt(r / r.yx);
    vec3 g = vec3(1, h.yx) / (dot(h, h) + 1.) + vec3(-.5, 0, .5);
    h = g.xy / dot(g, g);
    h = vec2(atan(h.x, h.y), log(length(h))) / 6.28;
    h += iTime * .1;

    h *= mat2(8, 5, -5, 8); 
    vec2 cellID = floor(h);
    cellID = mod(cellID, vec2(8,5));// -----thx to chronos for this seam fix
    vec2 cellUV = fract(h);
    float seed = dot(cellID, vec2(127.1, 311.7)); seed = fract(sin(seed) * 43758.5453);
    vec3 col = mainPC(1.-cellUV.yx, seed, 3.14-a);
    c = vec4(col, 1.0);
}


// multisampling
void mainImage(out vec4 o, vec2 u) 
{ 
// this is the numebr of samples - 1. is no multisampling
    float s = 1., k; 
    vec2 j = vec2(.5); 
    o = vec4(0); 
    vec4 c; 
    mainImage0(c, u); 
    for (k = s; k-- > .5; ) { 
        mainImage0(c, u + j - .5); 
        o += c; 
        j = fract(j + vec2(.755, .57).yx); 
    };o /= s;o.a==1.;
}

// ABOUT
// I wanted to make an interactive pumpkin carving shader for halloween.
// This is my first attempt at a basic prototype, and it went a lot further than originally planned.
// Using a drawn texture to postively displace a sphere SDF and then subtracting that from the main pumpkin.
// The texture also provides an easy route to volumetric light.
// --
// It was a lot of fun to make and as a prototype/proof of concept it's a success that turned out better than expected
// However - it has plenty of issues and will benefit from a complete rebuild.
// Performance, compile time, messy code, untidy carve subtraction and the overall scene rendering could all be improved a lot.
// --
// I'm now working on the next version.

