    fragColor = texture(iChannel0, uv , 0.);
}

// Simple hash function
float hash(float seed) {
    return fract(sin(seed * 12.9898) * 43758.5453);
}

// Get 2D sdf data from font texture
float GetCharacter(ivec2 ch, vec2 cuv, float th) {
    cuv = clamp(cuv, vec2(0), vec2(1));
    float tx = (cuv.x/16.) + (float(ch.x)*(1./16.));
    float ty = (cuv.y/16.) + (float(ch.y)*(1./16.));
    return (-.5*th) + texture(iChannel0, vec2(tx, ty)).a;
}

float char2DSDFRotated(vec2 p, vec2 scale, ivec2 ch, float angle, float th) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 rotP = vec2(c * p.x - s * p.y, s * p.x + c * p.y);
    vec2 uv = rotP / scale + 0.5;
    return GetCharacter(ch, uv, th);
}

float eyesSDF(vec2 p, float scale, int index) {
    ivec2 echars = ivec2(0);
    float erotation = 0.;
    vec2 escale = vec2(2,1) * scale;
    vec2 eoffset = vec2(0,-0.4) * scale;
    float ethickness = 1.0;

    switch (index) {
        case 0: echars = ivec2(5,15); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); erotation = -3.141; break;
        case 1: echars = ivec2(14,14); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); erotation = -3.141; break;
        case 2: echars = ivec2(4,15); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); escale = vec2(1,1) * scale; erotation = -3.141; break;
        case 3: echars = ivec2(3,15); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); escale = vec2(1,1) * scale; erotation = -3.141; break;
        case 4: echars = ivec2(12,14); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); escale = vec2(1,1) * scale; erotation = -3.141; break;
        case 5: echars = ivec2(10,13); eoffset += vec2(-0.5,-0.2)*scale; p.x = abs(p.x); erotation = -3.141; break;
        case 6: echars = ivec2(11,13); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); erotation = -3.141; break;
        case 7: echars = ivec2(12,13); eoffset += vec2(-0.4,-0.4)*scale; p.x = abs(p.x); escale = vec2(2) * scale; break;
        case 8: echars = ivec2(5,15); eoffset += vec2(-0.5,0)*scale; p.x = abs(p.x); erotation = -3.141; break;
    }
    return char2DSDFRotated(p + eoffset, escale, echars, erotation, ethickness);
}

float mouthSDF(vec2 p, float scale, int index) {
    ivec2 mchars = ivec2(0);
    float mrotation = 0.;
    vec2 mscale = vec2(2,1) * scale;
    vec2 moffset = vec2(0,0.3) * scale;
    float mthickness = 1.0;

    switch (index) {
        case 0: mchars = ivec2(6,10); p.x = abs(p.x)<0.5?(fract(p.x * 6.)-0.5)/3.:p.x; break;
        case 1: mchars = ivec2(15,6); moffset += vec2(-0.4,0)*scale; p.x = abs(p.x); mrotation = -.2; break;
        case 2: mchars = ivec2(7,8); break;
        case 3: mchars = ivec2(7,10); break;
        case 4: mchars = ivec2(13,10); mrotation = 1.57; mscale = vec2(1.2,1.5) * scale; moffset += vec2(-0.1,0)*scale; break;
        case 5: mchars = ivec2(13,8); mrotation = 1.57; mscale = vec2(1.2,1.5) * scale; moffset += vec2(-0.1,0)*scale; break;
        case 6: mchars = ivec2(9,13); mrotation = 1.57; mscale = vec2(1.2,1.5) * scale; moffset += vec2(-0.1,0)*scale; break;
        case 7: mchars = ivec2(8,13); mrotation = 1.57; mscale = vec2(1.2,1.5) * scale; moffset += vec2(-0.1,0)*scale; break;
        case 8: mchars = ivec2(11,10); mrotation = 1.57; mscale = vec2(1.2,1.5) * scale; moffset += vec2(-0.1,0)*scale; break;
        case 9: mchars = ivec2(6,10); p.x = abs(p.x)<0.35?(fract(p.x * 6.)-0.5)/3.:p.x; break;
        case 10: mchars = ivec2(6,10); p.x = abs(p.x)<0.65?(fract(p.x * 6.)-0.5)/3.:p.x; break;
        case 11: mchars = ivec2(15,5); moffset += vec2(0,0.3)*scale; break;
        case 12: mchars = ivec2(3,15); mscale = vec2(2.5,1) * scale; break;
        case 13: mchars = ivec2(3,15); mscale = vec2(1.5,1.5) * scale; break;
    }

    return char2DSDFRotated(p + moffset, mscale, mchars, mrotation, mthickness);
}

float faceSDF(vec2 p, float scale, float mouthSliderA, float mouthSliderB, float mouthSliderC, float eyeSliderA) {
    int mouthTypes = 10;
    int eyeTypes = 8;

    float mouthA = mouthSDF(p, scale, int(floor(mouthSliderA * float(mouthTypes))));
    float mouthB = mouthSDF(p, scale, 1+int(floor(mouthSliderA * float(mouthTypes))));
    float mouthC = mouthSDF(p, scale, 13);
    float mouthD = mouthSDF(p, scale, 12);
    float mouthE = mouthSDF(p, scale, 11);
    float mouth = mix(mouthA, mouthB, fract(mouthSliderA * float(mouthTypes)));
    mouth -= (0.5-abs(fract(mouthSliderA * float(mouthTypes)) - 0.5))/15.;
    float mouthF = mouthSliderB<0.5 ? mix(mouthC, mouthD, mouthSliderB*2.) : mix(mouthD, mouthE, (mouthSliderB-0.5)*1.6);
    mouth = mix(mouth, mouthF, mouthSliderC);

    float eyesA = eyesSDF(p, scale, int(floor(eyeSliderA * float(eyeTypes))));
    float eyesB = eyesSDF(p, scale, 1+int(floor(eyeSliderA * float(eyeTypes))));  
    float eyes = mix(eyesA, eyesB, fract(eyeSliderA * float(eyeTypes)));

    return min(mouth, eyes);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // Check resolution change from top-left pixel
    vec4 storedData = texture(iChannel1, vec2(0.5) / iResolution.xy);
    float storedWidth = storedData.b * 4096.0;
    float storedHeight = storedData.a * 4096.0;
    bool resolutionChanged = abs(storedWidth - iResolution.x) > 0.5 || 
                            abs(storedHeight - iResolution.y) > 0.5;
    if(fragCoord.x < 1.0 && fragCoord.y < 1.0) {
            fragColor = vec4(0.0, 0.0, iResolution.x / 4096.0, iResolution.y / 4096.0);
            return;
        }
    // Redraw if first 2 frames OR resolution changed
    if(iFrame < 10 || resolutionChanged) {
        // Top-left pixel stores resolution metadata


        vec2 cellCoord = floor(fragCoord / (iResolution.xy / vec2(8.0, 5.0)));
        vec2 cellUV = fract(fragCoord / (iResolution.xy / vec2(8.0, 5.0)));   
        vec2 p = (cellUV * 2.0 - 1.0) * 1.5;
        float sec = mod(iDate.w,3600.);
        float randomSeed = cellCoord.x + (cellCoord.y / 10.0) + sec;
        float mouthSliderA = hash(randomSeed + 0.7);
        float mouthSliderB = hash(randomSeed + 0.8);
        float mouthSliderC = hash(randomSeed + 0.9);
        mouthSliderC *= mouthSliderC*mouthSliderC*mouthSliderC; // bias to lower values
        //mouthSliderC = 1.-mouthSliderC;
        float eyeSliderA = hash(randomSeed + 1.1);
        if(cellCoord==vec2(0)){
            mouthSliderA = 0.6;
            mouthSliderB = 0.7;
            mouthSliderC = 0.5;
            eyeSliderA = 0.37;
        }
        float scale = 1.0;
        float dist = faceSDF(p, scale, mouthSliderA, mouthSliderB, mouthSliderC, eyeSliderA);
        float mask = smoothstep(0.02, -0.02, dist);

        fragColor = vec4(dist * 0.5 + 0.5, mask, 0.0, 1.0);
    } else {
        // Keep previous frame
        fragColor = texture(iChannel1, fragCoord.xy / iResolution.xy);
    }
}

#if HW_PERFORMANCE == 0
#define AA 1
#else
#define AA 3
#endif

// set to 1 to disable antialiasing, higher numbers for smooth gfx at the expense of FPS.

float randomSeed = 0.0;
vec4 guv;
vec2 bguv;
vec2 cellID;
float shadow = 0.;
// Pumpkin shape parameters
float n0, m0, k0, n1, m1, k1, n2, m2, k2;
float g, p, h;

// Lid parameters
float thickness;
float cutSphereRadius;
float cutSphereY;
float lidRaise;
float lidTiltX;
float lidTiltZ;
float lidGap;

// Stalk parameters
float stalkRadius;
int stalkStarN;
float stalkStarM;
float stalkHeight;
float stalkTwist;
float stalkBend;
float stalkTaper;
float stalkBulbHeight;
float stalkBulbRadius;
float stalkHeightOffset;

// Face parameters
float faceScale;
float faceDepth;
float faceZ;
float stemSubtract;
float mouthSliderA;
float mouthSliderB;
float mouthSliderC;
float eyeSliderA;
int mouthTypes = 10;
int eyeTypes = 8;

// Simple hash function
float hash(float seed) {
    return fract(sin(seed * 12.9898) * 43758.5453);
}
vec2 hash2(float n) {
    return vec2(hash(n), hash(n * 1.7));
}
// Parameter mapping functions
float mapParam(float t, float minVal, float maxVal) {
    return mix(minVal, maxVal, t);
}

float mapFloor(float t, float minVal, float maxVal) {
    return floor(mix(minVal, maxVal, clamp(t, 0.0, 1.0)));
}

// SDF Operations
float opOnion(float sdf, float thickness) {
    return abs(sdf) - thickness;
}

float opSubtraction(float d1, float d2) {
    return max(-d1, d2);
}

float opUnion(float d1, float d2) {
    return min(d1, d2);
}

float sphereSDF(vec3 pos, float radius) {
    return length(pos) - radius;
}

// 2D Star SDF
float sdStar(vec2 p, float r, int n, float m) {
    float an = 3.141593 / float(n);
    float en = 3.141593 / m;
    vec2 acs = vec2(cos(an), sin(an));
    vec2 ecs = vec2(cos(en), sin(en));

    float bn = mod(atan(p.y, p.x), 2.0 * an) - an;
    p = length(p) * vec2(cos(bn), abs(sin(bn)));
    p -= r * acs;
    p += ecs * clamp(-dot(p, ecs), 0.0, r * acs.y / ecs.y);
    return length(p) * sign(p.x);
}

float faceSDF(vec2 p, float scale) {
    // Calculate which cell in the atlas to use
    vec2 atlasSize = vec2(8.0, 5.0);
    vec2 atlasCellA = mod(cellID, atlasSize);
    vec2 atlasCellB = mod(vec2(0), atlasSize);
    // Convert p from world space to atlas UV space
    vec2 atlasUVA = (p / scale + 1.0) * 0.5; // -1..1 to 0..1
    atlasUVA = (atlasUVA + atlasCellA) / atlasSize;
     // Convert p from world space to atlas UV space
    vec2 atlasUVB = (p / scale + 1.0) * 0.5; // -1..1 to 0..1
    atlasUVB = (atlasUVB + atlasCellB) / atlasSize;   
    // Sample the pre-rendered face
    vec4 tex = mix(texture(iChannel0, atlasUVB), texture(iChannel0, atlasUVA), guv.w);
    float sdfValue = mix(1.- tex.g, tex.r, guv.w); // Use iChannel1 for Buffer A

    // Convert back from normalized range
    return (sdfValue - 0.5) / 5.0;
}

// Extrude a 2D SDF along Z axis
float extrudeSDF(vec3 pos, float char2D, float halfDepth) {
    vec2 w = vec2(char2D, abs(pos.z) - halfDepth);
    return min(max(w.x, w.y), 0.0) + length(max(w, 0.0));
}

// Twisting operation
vec3 opTwist(vec3 p, float k) {
    float c = cos(k * p.y);
    float s = sin(k * p.y);
    return vec3(p.x * c - p.z * s, p.y, p.x * s + p.z * c);
}

// Bending operation
vec3 opBend(vec3 p, float k) {
    float bendAmount = k * p.y * p.y / stalkHeight;
    return vec3(p.x + bendAmount, p.y, p.z);
}

// Rotation matrices
mat3 rotateX(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(1.0, 0.0, 0.0, 0.0, c, -s, 0.0, s, c);
}

mat3 rotateZ(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(c, -s, 0.0, s, c, 0.0, 0.0, 0.0, 1.0);
}

// Convert Cartesian to spherical coordinates
vec3 cartesianToSpherical(vec3 pos) {
    float rho = length(pos);
    float theta = atan(pos.z, pos.x);
    float phi = acos(pos.y / rho);
    return vec3(rho, theta, phi);
}

float pown(float f, float p){
    return pow(abs(f), p);
}

// Pumpkin equation SDF
float pumpkinSDF(vec3 pos) {
    vec3 spherical = cartesianToSpherical(pos);
    float theta = spherical.y;
    float phi = spherical.z;

    float sin_term0 = sin(n0 * theta + m0);
    float sin_term1 = sin(n1 * theta + m1);
    float sin_term2 = sin(n2 * theta + m2);

    float part1 = (1.0 - pown(sin_term0, 4.0)) * (k0 / n0) +
                  (1.0 - sin_term1) * (k1 / n1) +
                  (1.0 - sin_term2) * (k2 / n2);

    float cos_2phi = cos(2.0 * phi);
    float part2 = pown((1.0 - cos_2phi) / 2.0, 2.0);
    float part3 = exp(-pown(phi - 3.14159 / 2.0, 2.0)) + p;
    float part4 = 1.0 - h * cos_2phi;

    float target_rho = (part1 * part2 + g) * part3 * part4;

    return length(pos) - target_rho;
}

// Stalk SDF
float stalkSDF(vec3 pos) {
    vec3 stalkPos = pos;
    stalkPos.y -= lidRaise;
    stalkPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * stalkPos;
    stalkPos = stalkPos - vec3(0.0, stalkHeightOffset + (g * p * 1.2) - (h/3.), 0.0);

    vec3 twistedPos = opTwist(stalkPos, stalkTwist / stalkHeight);
    vec3 bentPos = opBend(twistedPos, stalkBend / stalkHeight);

    float heightFactor = clamp((bentPos.y) / stalkHeight, 0.0, 1.0);
    float bulbFactor = 1.0 - smoothstep(0.0, stalkBulbHeight, bentPos.y);
    float bulbRadius = mix(stalkRadius, stalkBulbRadius, bulbFactor);
    float taperedRadius = bulbRadius * (1.0 - stalkTaper * heightFactor);

    float starDist = sdStar(bentPos.xz, taperedRadius, stalkStarN, stalkStarM);
    float heightDist = (abs(bentPos.y - stalkHeight * 0.5) - stalkHeight * 0.5);

    vec2 w = vec2(starDist, heightDist);
    float stalkBase = min(max(w.x, w.y), 0.0) + length(max(w, 0.0));

    return opSubtraction(stemSubtract, stalkBase);
}

// Hollow pumpkin with top cut off
float hollowPumpkinSDF(vec3 pos) {
    float pumpkin = pumpkinSDF(pos);
    float hollow = opOnion(pumpkin, thickness);

    vec3 cutSpherePos = pos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius + lidGap);

    return opSubtraction(cuttingSphere, hollow);
}

// Lid SDF
float lidSDF(vec3 pos) {
    vec3 lidPos = pos;
    lidPos.y -= lidRaise;
    lidPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * lidPos;

    float pumpkin = pumpkinSDF(lidPos);
    stemSubtract = pumpkin;

    float hollow = opOnion(pumpkin, thickness);

    vec3 cutSpherePos = lidPos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius);

    return max(hollow, cuttingSphere);
}

// Main scene SDF
float sceneSDF(vec3 pos) {
    float hollowPumpkin = hollowPumpkinSDF(pos);
    float lid = lidSDF(pos);
    float stalk = stalkSDF(pos);

    float faceShape = faceSDF(pos.xy, faceScale*1.4);
    vec3 facePos = pos - vec3(0.0, 0.0, faceZ);
    float faceCutout = extrudeSDF(facePos, faceShape, faceDepth);

    hollowPumpkin = opSubtraction(faceCutout, hollowPumpkin);

    return opUnion(opUnion(hollowPumpkin, lid), stalk);
}

// Raymarching
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 pos = ro + t * rd;
        float d = sceneSDF(pos);
        if (d < 0.0005) break;
        t += d * .9;
        if (t > 50.0) break;
    }
    return t;
}

// Calculate normal
vec3 getNormal(vec3 pos) {
    float eps = 0.001;
    vec3 n = vec3(
        sceneSDF(pos + vec3(eps, 0, 0)) - sceneSDF(pos - vec3(eps, 0, 0)),
        sceneSDF(pos + vec3(0, eps, 0)) - sceneSDF(pos - vec3(0, eps, 0)),
        sceneSDF(pos + vec3(0, 0, eps)) - sceneSDF(pos - vec3(0, 0, eps))
    );
    return normalize(n);
}

// Subsurface scattering
float calculateSubsurface(vec3 pos, vec3 interiorLightPos, float probeDistance) {
    vec3 toLightDir = normalize(interiorLightPos - pos);
    float toLightDist = distance(interiorLightPos , pos);
    vec3 probePos = pos + toLightDir * (probeDistance + 0.15);
    float d = sceneSDF(probePos);

    // Only apply subsurface if we're actually inside the object
    // and not just in free space
    float surfaceDist = sceneSDF(pos);
    if (surfaceDist > 0.001) return 0.0;  // We're not on the surface

    return clamp(max(0., (d*3.)/(toLightDist*toLightDist)), 0.0, 1.0);
}

vec3 lighting(vec3 pos, vec3 normal, vec3 viewDir, float subsurfaceDistance, float subsurfaceAttenuation, mat3 cameraMatrix) {
    float flicker = 0.9 + hash(randomSeed) + 0.4 * sin(iTime * 37.0) + 0.2 * sin(iTime * 71.0) * (0.5 + hash(randomSeed));

    vec3 worldKeyLightDir = normalize( mix(vec3(0,1,0) , vec3(-1.,.7,-1.) , guv.x) );  // Original direction
    vec3 keyLightDir = normalize(cameraMatrix * worldKeyLightDir);  // Transform to camera space
    vec3 keyLightColor = mix(vec3(1) ,vec3(.1, 0.7, 1.0), guv.x);

    vec3 interiorLightPos = vec3(0.0, 0.0, 0.0);
    vec3 interiorLightColor = vec3(1.2, .8, 0.2);
    float interiorLightIntensity = mix( 0. , flicker , guv.w);

    vec3 ambient = vec3(0.7, 0.5, 0.4)-guv.w/2.5;

    float keyDiff = max(dot(normal, keyLightDir), 0.0);
    vec3 keyDiffuse = keyDiff * keyLightColor;

    vec3 keyReflectDir = reflect(-keyLightDir, normal);
    float keySpec = pow(max(dot(viewDir, keyReflectDir), 0.0), 32.0);
    vec3 keySpecular = keySpec * keyLightColor;

    vec3 toInteriorLight = interiorLightPos - pos;
    float interiorLightDist = length(toInteriorLight);
    vec3 interiorLightDir = toInteriorLight / interiorLightDist;

    float attenuation = interiorLightIntensity / (1.0 + 0.3 * interiorLightDist * interiorLightDist);

    float interiorDiff = max(dot(normal, interiorLightDir), 0.0);
    vec3 interiorDiffuse = interiorDiff * interiorLightColor * attenuation * guv.w;

    vec3 diffuse = keyDiffuse + interiorDiffuse;

    float hollowDist = hollowPumpkinSDF(pos);
    float lidDist = lidSDF(pos);
    float stalkDist = stalkSDF(pos);

    vec3 materialColor;
    float fleshSpecular = 1.0;

    if (stalkDist < hollowDist && stalkDist < lidDist) {
        materialColor = vec3(0.36, 0.57, 0.13);
    } else if (lidDist < hollowDist) {
        vec3 lidPos = pos;
        lidPos.y -= lidRaise;
        lidPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * lidPos;

        float originalLidPumpkin = pumpkinSDF(lidPos);
        float fleshDepth = max(0.0, -originalLidPumpkin + thickness);

        if (fleshDepth > 0.001) {
            float depthFactor = clamp(fleshDepth / 0.1, 0.0, 1.0);
            vec3 fleshColor = mix(vec3(1.2, 0.9, 0.4), vec3(0.8, 0.4, 0.1), depthFactor);
            materialColor = fleshColor;
            fleshSpecular = max(0., mix(0.8, 0.1, depthFactor * 20.));
        } else {
            materialColor = vec3(0.8, 0.4, 0.12);
        }
    } else {
        float originalPumpkin = pumpkinSDF(pos);
        float fleshDepth = max(0.0, -originalPumpkin + (thickness));
        shadow = clamp(7.* clamp(-originalPumpkin, 0.01, 10.5) * (1.-guv.w), 0., 0.6);

        if (fleshDepth > 0.001) {
            float depthFactor = clamp(fleshDepth / 0.1, 0.0, 1.0);
            vec3 fleshColor = mix(vec3(1.2, 0.9, 0.4), vec3(0.8, 0.4, 0.1), depthFactor);

            materialColor = fleshColor;
            fleshSpecular = max(0., mix(0.8, 0.1, depthFactor * 20.));
        } else {
            materialColor = vec3(0.9, 0.45, 0.15);
        }
    }

    float subsurfaceDistance_val = calculateSubsurface(pos, interiorLightPos, subsurfaceDistance);
    float subsurfaceFactor = subsurfaceDistance_val > 0.0 ? clamp(subsurfaceDistance_val, 0.0, 1.0) : 0.0;
    vec3 subsurfaceLight = subsurfaceFactor * interiorLightColor * guv.w;

    return (materialColor * (ambient + diffuse + subsurfaceLight - shadow) + fleshSpecular * keySpecular) ;
}

float sinS(float x, float skip_count) {
    float period_length = 2.0 * 3.14159265359;
    float period_number = floor(x / period_length);
    float period_mod = mod(period_number, skip_count);

    if (period_mod >= skip_count - 1.0) {
        return -cos(x);
    } else {
        return -1.0;
    }
}
vec2 simulateCursorOrganic(float time) {
    float changeInterval = 2.0;
    float currentIndex = floor(time / changeInterval);

    // Generate multiple waypoints for more complex paths
    vec2 p0 = hash2(currentIndex - 1.0);
    vec2 p1 = hash2(currentIndex);
    vec2 p2 = hash2(currentIndex + 1.0);
    vec2 p3 = hash2(currentIndex + 2.0);

    float localTime = fract(time / changeInterval);

    // Elastic easing for "darting" with slight overshoot
    float t = localTime;
    if (t < 0.3) {
        // Quick dart phase
        t = pow(t / 0.3, 2.0);
    } else {
        // Settle with slight oscillation
        t = 1.0 - pow(1.0 - (t - 0.3) / 0.7, 3.0) * 0.05 * 
            cos((t - 0.3) * 20.0);
    }

    // Catmull-Rom spline for smoother path
    vec2 cursor = 0.5 * (
        (2.0 * p1) +
        (-p0 + p2) * t +
        (2.0 * p0 - 5.0 * p1 + 4.0 * p2 - p3) * t * t +
        (-p0 + 3.0 * p1 - 3.0 * p2 + p3) * t * t * t
    );

    return clamp(cursor, 0.0, 1.0);
}
vec2 simulateCursorAdvanced(float time, float ji) {
    float jumpInterval = 1.+4.*ji;
    float jumpIndex = floor(time / jumpInterval);
    float jumpProgress = fract(time / jumpInterval);

    // Multiple movement phases:
    // 0.0-0.1: hold at start position
    // 0.1-0.4: dart to new position
    // 0.4-1.0: hold at end position

    float moveProgress;
    if (jumpProgress < 0.1) {
        moveProgress = 0.0; // Hold
    } else if (jumpProgress < 0.4) {
        // Quick dart motion
        float t = (jumpProgress - 0.1) / 0.3;
        moveProgress = 1.0 - pow(1.0 - t, 2.0); // Ease-out
    } else {
        moveProgress = 1.0; // Hold at destination
    }

    vec2 fromPos = hash2(jumpIndex);
    vec2 toPos = hash2(jumpIndex + 1.0);

    // Add some slight wobble at destination
    if (moveProgress >= 1.0) {
        toPos += sin(time * 10.0) * 0.01;
    }

    return mix(fromPos, toPos, moveProgress);
}
vec3 mainImage1(vec2 uv, vec2 id) {
    // Set random seed based on cell
    randomSeed = id.x + (id.y / 10.);

    // Generate all parameters from random seed
    float anim = (2. + randomSeed / 2.)*3.;
    float anims = 10.;
    float tiltPhase = 1. - fract(  ( (iTime - 0.1 + (anim * 3.)) * anims /15. ) / (2.0 * 3.14159265359)  );
    tiltPhase *=tiltPhase;
    lidRaise = mix(0., mapParam((sinS(   ((iTime + (anim * 3.)) * anims *2. ) , 30. ) + 1.), 0.03, 0.3)  , guv.w) ;
    lidTiltX = mix(0., mapParam((sin(((iTime + anim * 3.) * anim)) + 1.) / 2., -0.2, 0.2)  , guv.w) * tiltPhase; 
    lidTiltX += lidRaise * 2.*(hash(randomSeed +3.)-0.5);
    lidTiltZ = mix(0., mapParam((cos(((iTime + anim * 3.) * anim)) + 1.) / 2., -0.15, 0.15)  , guv.w) * tiltPhase;
    lidTiltZ += lidRaise * 2.*(hash(randomSeed)-0.5);
    uv.y -= guv.w * (sinS(((iTime + anim * 3.) * anims), 15.) + 1.)/10.;


    //uv.y -= tiltPhase;
    cutSphereY = mapParam(hash(randomSeed + 0.3), 0.5, 0.8);
    cutSphereRadius = mapParam(hash(randomSeed + 0.4), 0.45, 0.6);
    thickness = mapParam(hash(randomSeed + 0.5), 0.05, 0.06);
    lidGap = mapParam(hash(randomSeed + 0.6), 0.0, 0.05);

    mouthSliderA = hash(randomSeed + 0.7);
    mouthSliderB = hash(randomSeed + 0.8);
    mouthSliderC = hash(randomSeed + 0.9);
    eyeSliderA = hash(randomSeed + 1.1);

    float subsurfaceDistance = mapParam(hash(randomSeed + 1.2), 0.05, 0.08);
