    stalkHeight = mapParam(hash(randomSeed + 2.3), 0.3, 0.5);
    stalkTwist = mapParam(hash(randomSeed + 2.4), 0.0, 1.5);
    stalkBend = mapParam(hash(randomSeed + 2.5), 0.0, 0.4);
    stalkTaper = mapParam(hash(randomSeed + 2.6), -0.5, 0.3);
    stalkBulbHeight = mapParam(hash(randomSeed + 2.7), 0.1, 0.3);

    // Pumpkin shape parameters
    k2 = mapParam(hash(randomSeed + 2.8), 0.02, 0.3);
    m2 = mapParam(hash(randomSeed + 2.9), 0.0, 6.28);
    n2 = mapFloor(hash(randomSeed + 3.0), 4.0, 12.0);
    k1 = mapParam(hash(randomSeed + 3.1), 0.02, 0.3);
    m1 = mapParam(hash(randomSeed + 3.2), 0.0, 6.28);
    n1 = mapFloor(hash(randomSeed + 3.3), 5.0, 30.0);
    k0 = mapParam(hash(randomSeed + 3.4), 0.02, 0.3);
    m0 = mapParam(hash(randomSeed + 3.5), 0.0, 3.14);
    n0 = mapFloor(hash(randomSeed + 3.6), 6.0, 60.0);
    h = mapParam( mix( 0.35, hash(randomSeed + 3.7), guv.w ), -0.3, 0.05) + guv.w * (sinS(((iTime - 0.25 + (anim * 3.)) *anims* 2.), 30.) + 1.)/15.;
    p = mapParam( mix( 0.5, hash(randomSeed + 3.8), guv.w ), 0.5, 0.8);
    g = mapParam( mix( 0.5, hash(randomSeed + 3.9), guv.w ), 0.5, 0.7);

    // Derived parameters
    stalkBulbRadius = cutSphereRadius * 0.6;
    stalkHeightOffset = 0.0;
    faceScale = g * (p + .3) * ((h + 1.) / 3.) * mapParam(hash(randomSeed + 1.0), 3.25, 4.5);
    faceDepth = 1.3;
    faceZ = 1.3;

    baseCameraDist /= pow(max(0.5, focal), 2.);
    screenMag /= pow(max(0.5, focal), 2.);

    vec2 uvScene = uv / screenMag;

    // Camera setup
    vec3 target = vec3(0.0, 0.35, 0.0);
    vec3 dir = vec3(cos(pitch) * cos(yaw), sin(pitch), cos(pitch) * sin(yaw));
    vec3 ro = target - dir * baseCameraDist;

    vec3 forward = normalize(target - ro);
    vec3 upRef = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(forward, upRef));
    vec3 up = cross(right, forward);
// Camera to world matrix (inverse of view matrix rotation part)
mat3 cameraMatrix = mat3(right, up, -forward);
    vec3 rd = normalize(forward + uvScene.x * right + uvScene.y * up);

    vec3 color = mix(vec3(0.9,0.9,1.2), vec3(0.0), guv.y);
    color = pow(1.2*mix(color, color* texture(iChannel1, bguv, 0.).rgb, guv.y), vec3(2.));

    float t = raymarch(ro, rd);

    if (t < 50.0) {
        vec3 pos = ro + t * rd;
        vec3 normal = getNormal(pos);
        vec3 viewDir = normalize(ro - pos);
       color = lighting(pos, normal, viewDir, subsurfaceDistance, subsurfaceAttenuation, cameraMatrix);
    }

    return color;
}

void compute_things(in vec2 p, out vec2 h, out vec2 ddx, out vec2 ddy) {
    const float TAU = 6.28318530718;
    const mat2 M = mat2(8.0, 5.0, -5.0, 8.0);

    vec2 r = iResolution.xy;
    vec2 s = sqrt(r / r.yx);
    vec2 a = 2.0 / r * s;
    vec2 u = a * p - s;

    float S = dot(u, u) + 1.0;
    float invS = 1.0 / S;
    float invS2 = invS * invS;

    vec3 g = vec3(1.0, u.yx) * invS + vec3(-0.5, 0.0, 0.5);

    float T = dot(g, g);
    float invT = 1.0 / T;
    float invT2 = invT * invT;

    vec3 dg_dux = vec3(-2.0 * u.x * invS2, -2.0 * u.x * u.y * invS2, invS - 2.0 * u.x * u.x * invS2);
    vec3 dg_duy = vec3(-2.0 * u.y * invS2, invS - 2.0 * u.y * u.y * invS2, -2.0 * u.x * u.y * invS2);

    vec3 dg_dx = dg_dux * a.x;
    vec3 dg_dy = dg_duy * a.y;

    vec2 q = g.xy * invT;

    float dTdx = 2.0 * dot(g, dg_dx);
    float dTdy = 2.0 * dot(g, dg_dy);

    vec2 dq_dx = dg_dx.xy * invT - g.xy * dTdx * invT2;
    vec2 dq_dy = dg_dy.xy * invT - g.xy * dTdy * invT2;

    float denom = dot(q, q);
    float scale = 1.0 / (TAU * denom);

    ddx = vec2(dq_dx.x * q.y - dq_dx.y * q.x, dot(dq_dx, q)) * scale;
    ddy = vec2(dq_dy.x * q.y - dq_dy.y * q.x, dot(dq_dy, q)) * scale;

    h = vec2(atan(q.x, q.y), log(length(q))) / TAU;
    h += iTime * 0.04;

    h *= M;
    ddx *= M;
    ddy *= M;
}

mat2 rotate(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage0(out vec4 c, vec2 p) {

    vec2 h, ddx, ddy;
