// ---------- Editable parameters ----------
vec4 colour_1 = vec4(2,2,3, 1.0);  // replace with desired RGBA (0..1)
vec4 colour_2 = vec4(0,0,0, 1.0);
vec4 colour_3 = vec4(.0,0,.3, 1.0);

float contrast = 1.0;      // tweak 0..2 (approx)
float spin_amount = 1.0;   // 0..1
float spin_time_mul = 1.5; // multiply iTime if you want spin to run faster/slower

// ---------- Internal constants ----------
#define PIXEL_SIZE_FAC 700.0
#define SPIN_EASE 0.5

float resLen(vec2 r){ return length(r); }

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 bguv = fragCoord.xy/iResolution.xy;
    vec4 bgtex=texture(iChannel0, bguv, 0.);

    fragCoord.x-=iResolution.x/4.;
    vec2 screen_coords = fragCoord;
    vec2 love_ScreenSize = iResolution.xy;

    float pixel_size = resLen(love_ScreenSize) / PIXEL_SIZE_FAC;
    vec2 uv = (floor(screen_coords * (1.0 / pixel_size)) * pixel_size - 0.5 * love_ScreenSize) 
              / resLen(love_ScreenSize) - vec2(0.12, 0.0);
    float uv_len = length(uv);

    float speed = (spin_time_mul * iTime * SPIN_EASE * 0.2) + 302.2;
    float new_pixel_angle = atan(uv.y, uv.x) + speed 
                            - SPIN_EASE * 20.0 * (spin_amount * uv_len + (1.0 - spin_amount));
    vec2 mid = (love_ScreenSize / resLen(love_ScreenSize)) / 2.0;
    uv = vec2(uv_len * cos(new_pixel_angle) + mid.x,
              uv_len * sin(new_pixel_angle) + mid.y) - mid;

    uv *= 30.0;

    speed = iTime * 2.0;
    vec2 uv2 = vec2(uv.x + uv.y);

    for(int i = 0; i < 5; i++) {
        uv2 += sin(max(uv.x, uv.y)) + uv;
        uv  += 0.5 * vec2(cos(5.1123314 + 0.353 * uv2.y + speed * 0.131121), 
                          sin(uv2.x - 0.113 * speed));
        uv  -= cos(uv.x + uv.y) - sin(uv.x * 0.711 - uv.y);
    }

    float contrast_mod = (0.25 * contrast + 0.5 * spin_amount + 1.2);
    float paint_res = min(2.0, max(0.0, length(uv) * 0.035 * contrast_mod));
    float c1p = max(0.0, 1.0 - contrast_mod * abs(1.0 - paint_res));
    float c2p = max(0.0, 1.0 - contrast_mod * abs(paint_res));
    float c3p = 1.0 - min(1.0, c1p + c2p);

    vec4 ret_col = (0.3 / contrast) * colour_1 
                 + (1.0 - 0.3 / contrast) * (colour_1 * c1p 
                 + colour_2 * c2p 
                 + vec4(c3p * colour_3.rgb, c3p * colour_1.a));


    fragColor = (bgtex*20. +  ret_col)/21.;
}

