//pause between reset
#define PAUSE 8192

#define STEP 128
#define MAX_STEPS 8
#define PERIOD (MAX_STEPS * STEP)
#define LOD_OFFSET 0.0
#define MIN_BLOCK_SIZE 8u

#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0

//flip lowest n bits
int xor_n(int x, int n)
{
    return x ^ ( (1<<(n+1)) - 1 );
}

int imod(int a, float b)
{
    return a - int(b*floor(float(a)/b));
}

uint pcg(uint v)
{
    uint state = v * 747796405u + 2891336453u;
    uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
    return (word >> 22u) ^ word;
}

uint rand_int(uint seed, uint max_value)
{
    return pcg(seed) % max_value;
}

uint xor_swap(uint idx, uint n, uint seed)
{
    uint xor_seed = rand_int(seed, n);
    uint xor_idx = idx ^ xor_seed;
    uint max_idx = max(idx, xor_idx);
    uint min_idx = min(idx, xor_idx);
    bool swap = rand_int(min_idx * 451u + seed, 2u) == 0u;
    return (swap && (max_idx < n)) ? xor_idx : idx;
}

uint reverse(uint idx, uint n)
{
    return n - 1u - idx;
}

uint shuffle(uint idx, uint n, uint seed, uint iters)
{
    for(uint i = 0u; i < iters; i++)
    {
        idx = xor_swap(idx, n, seed + i);
        idx = reverse(idx, n);
    }
    return idx;
}

uint unshuffle(uint idx, uint n, uint seed, uint iters)
{
    for (uint i = iters; i-- > 0u; )
    {
        idx = reverse(idx, n);                 
        idx = xor_swap(idx, n, seed + i);
    }
    return idx;
}

uint getcompanion(uint idx, uint n, uint seed, uint iters)
{
    uint shuffled = shuffle(idx, n, seed, iters);
    uint shuffled_companion = clamp(shuffled ^ 1u, 0u, n - 1u);
    return unshuffle(shuffled_companion, n, seed, iters);
}

uvec2 blockSize;
uvec2 blockOffset;

uvec2 i2xy(uint id)
{
    return uvec2(id %blockSize.x, id/blockSize.x);
}

uint xy2i(uvec2 xy)
{
    return xy.x + xy.y*blockSize.x;
}


vec4 gaussianBlur5x5Lod(sampler2D image, vec2 uv, float lod)
{
    const float w[5] = float[5](1.0, 4.0, 6.0, 4.0, 1.0);
    vec2 stepSize = exp2(lod) / iResolution.xy;
    vec4 sum = vec4(0.0);
    float weightSum = 0.0;
    for(int y = 0; y < 5; ++y)
    {
        float wy = w[y];
        for(int x = 0; x < 5; ++x)
        {
            vec2 offset = vec2(float(x) - 1.5, float(y) - 1.5) * stepSize;
            float weight = wy * w[x];
            weightSum += weight;
            sum += textureLod(image, uv + offset, lod) * weight;
        }
    }
    return sum / weightSum;
}

void mainImage( out vec4 U, in vec2 p )
{  
    int smode = int(texture(iChannel1, vec2(0), 0.).g);
    vec2 fragCoord = p;
    if(smode == 2 ){
    int sFrame = int(texture(iChannel1, vec2(0), 0.).r);
    int iter0 = (iFrame-sFrame) % (PERIOD + PAUSE);
    uint iter1 = uint(MAX_STEPS) - (uint(clamp(iter0, 0, PERIOD)) / uint(STEP));
    blockSize = min(uvec2(MIN_BLOCK_SIZE << iter1), uvec2(iResolution.xy));
    blockOffset = (((iFrame-sFrame) & 1) == 0) ? blockSize/2u : uvec2(0u);
    float LOD = float(iter1) + LOD_OFFSET;
    p = floor(p);

    //reset state
    if(iter0 == 0)
    {
        U = texture(iChannel1, p / iResolution.xy);
        return;
    }

    //select blocks of pixels
    uvec2 pu = uvec2(p);
    uvec2 block = ((pu + blockOffset) / blockSize) * blockSize - blockOffset;
    uvec2 bPos = pu - block;

    //random pair of 2 unique pixels, this pixel and its random partner (symmetric)
    uint bid0 = xy2i(bPos);
    uint bid1 = getcompanion(bid0, blockSize.x*blockSize.y, uint((iFrame-sFrame)), 12u); 

    //the pair positions 
    uvec2 p0 = block + i2xy(bid0);
    uvec2 p1 = block + i2xy(bid1);

    //current values
    vec4 c0 = texel(ch0, vec2(p0));
    vec4 c1 = texel(ch0, vec2(p1));

    if(any(greaterThan(p1, uvec2(iResolution.xy - 1.0)))) { U = c0; return; } //invalid pair

    //target blurred values
    vec4 bc0 = gaussianBlur5x5Lod(iChannel0, vec2(p0)/iResolution.xy, LOD);
    vec4 bc1 = gaussianBlur5x5Lod(iChannel0, vec2(p1)/iResolution.xy, LOD);

    //flip the pixels so that its closer to blurred 
    float score0 = distance(c0, bc0) + distance(c1, bc1);
    float score1 = distance(c1, bc0) + distance(c0, bc1);
    U = (score0 < score1) ? c0 : c1;

    } else {
        U = texture(iChannel1, fragCoord/iResolution.xy, 0.);
    }
}

