// Hash functions for subdivision
uint hash(uint x) {
    x += x << 10u; x ^= x >> 6u;
    x += x << 3u;  x ^= x >> 11u;
    x += x << 15u; return x;
}
uint hash(uvec2 v) { return hash(v.x ^ hash(v.y)); }
uint hash(uvec3 v) { return hash(v.x ^ hash(v.y) ^ hash(v.z)); }

void initSeed(vec2 v)  { gSeed = hash(floatBitsToUint(v)); }
void initSeed(vec3 v)  { gSeed = hash(floatBitsToUint(v)); }

float floatConstruct(uint m) {
    const uint M = 0x007FFFFFu;
    const uint O = 0x3F800000u;
    return uintBitsToFloat((m & M) | O) - 1.0;
}

float random() { gSeed = hash(gSeed); return floatConstruct(gSeed); }

mat2 rot2D(float a) { float s = sin(a), c = cos(a); return mat2(c, -s, s, c); }

vec3 hsl2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return c.z + c.y * (rgb - 0.5) * (1.0 - abs(2.0 * c.z - 1.0));
}

float hash11(float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec3 hash31(float p) {
    vec3 p3 = fract(vec3(p) * vec3(0.1031, 0.1030, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xxy + p3.yzz) * p3.zyx);
}

vec3 fold(vec3 p, vec3 normal, float offset) {
    float d = dot(p, normal) - offset;
    return p - 2.0 * min(d, 0.0) * normal;
}

vec3 primitiveColor;
float primitive(vec3 p, vec3 size, float round) {
    vec3 d = abs(p) - size;
    p = clamp(p, vec3(0, 0.5, 0.4), vec3(2, 0.85, 0.6));
    primitiveColor = hsl2rgb(p);
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0) - round;
}

vec3 orbitTrap;
float kifs(vec3 p, float seed) {
    orbitTrap = vec3(1e20);
    int maxIter = 3 + int(hash11(seed) * 8.0);
    vec3 primSize = vec3(0.3, 0.5, 0.7) * (0.5 + hash31(seed + 100.0));
    float primRound = hash11(seed + 200.0) * 0.3;
    float scale = 1.0;
    const float BAILOUT = 100.0;
    const float MAX_SCALE = 1e6;
    float iterSeed = seed;

    for(int i = 0; i < 8; i++) {
        if(i >= maxIter) break;
        if(length(p) > BAILOUT) break;
        if(scale > MAX_SCALE) break;
        orbitTrap = min(orbitTrap, abs(p));

        iterSeed = hash11(iterSeed + 42.0) > 0.5 ? iterSeed + float(i) * 17.31 : iterSeed;
        p = abs(p);

        float foldType = hash11(iterSeed + 50.0);
        if(foldType < 0.33) {
            vec3 c = hash31(iterSeed + 51.0) * 0.3;
            p = abs(p - c) - c * 0.5;
        } else if(foldType < 0.66) {
            if(hash11(iterSeed + 52.0) < 0.5) p = p.yzx;
            else p = p.zxy;
        }

        int folds = 1 + int(hash11(iterSeed) * 3.0);
        for(int j = 0; j < 3; j++) {
            if(j >= folds) break;
            vec3 n = normalize(hash31(iterSeed + float(j) * 7.77) * 2.0 - 1.0);
            p = fold(p, n, 0.0);
        }

        vec3 translation = hash31(iterSeed + 1.0) * 0.3;
        float iterScale = 1.6 + hash11(iterSeed + 2.0) * 0.6;
        p = p * iterScale - translation;
        scale *= iterScale;
    }

    return primitive(p, primSize, primRound) / scale;
}

float map(vec3 p) {
    return kifs(p, currentKifsSeed);
}

float rayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);
        if(abs(d) < SURF_DIST) break;
        if(t > MAX_DIST) break;
        t += d * STEP_MUL;
    }
    return t;
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(0.0003, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}

float getAO(vec3 p, vec3 n) {
    float ao = 0.0, w = 1.0;
    for(int i = 1; i <= 5; i++) {
        float d = float(i) * 0.08;
        ao += w * (d - map(p + n * d));
        w *= 0.5;
    }
    return 1.0 - clamp(ao * 2.5, 0.0, 1.0);
}

float getShadow(vec3 ro, vec3 rd, float tmax) {
    float res = 1.0, t = 0.02;
    for(int i = 0; i < 48; i++) {
        float d = map(ro + rd * t);
        if(d < 0.0005) return 0.0;
        res = min(res, 10.0 * d / t);
        t += clamp(d, 0.01, 0.15);
        if(t > tmax) break;
    }
    return clamp(res, 0.0, 1.0);
}

vec3 shade(vec3 p, vec3 rd, float t) {
    vec3 n = getNormal(p);

    vec3 lightDir = normalize(vec3(1.0, 2.0, 1.5));
    vec3 lightDir2 = normalize(vec3(-1.0, 0.5, -1.0));
    vec3 viewDir = -rd;
    vec3 halfVec = normalize(lightDir + viewDir);

    float diff = max(dot(n, lightDir), 0.0);
    float diff2 = max(dot(n, lightDir2), 0.0);
    float spec = pow(max(dot(n, halfVec), 0.0), 32.0);
    float fresnel = pow(1.0 - max(dot(n, viewDir), 0.0), 3.0);

    float ao = getAO(p, n);
    float shadow = getShadow(p + n * 0.002, lightDir, 8.0);

    vec3 matCol = vec3(
        0.5 + 0.5 * sin(orbitTrap.x * 3.0),
        0.5 + 0.5 * sin(orbitTrap.y * 3.0 + 2.0),
        0.5 + 0.5 * sin(orbitTrap.z * 3.0 + 4.0)
    );
    matCol = mix(primitiveColor,matCol, ao);

    vec3 col = matCol * 0.08;
    col += matCol * diff * shadow * 0.65;
    col += vec3(0.2, 0.3, 0.4) * diff2 * 0.25;
    col += vec3(1.0, 0.95, 0.9) * spec * shadow * 0.6;
    col += vec3(0.3, 0.4, 0.5) * fresnel * 0.15;
    col *= ao;

    col = mix(col, vec3(cellBG), min(1.0, max(0.0, (t - sqrt(FOG)) / 25.0)));
    return col;
}

vec3 contrast(vec3 color, float value) { return 0.5 + value * (color - 0.5); }

vec4 applySubd(vec2 uv, float timeIndex) {
    cellId = vec2(0);
    vec4 cell = vec4(0, 0, 1, 1);

    // Moderate settings
    const float MIN_SPLITS = 1.0;
    const float MAX_SPLITS = 8.0;
    const float COMPLEXITY_MIN = 2.0;
    const float COMPLEXITY_RANGE = 2.5;

    initSeed(vec2(timeIndex, 777.0));
    float baseComplexity = COMPLEXITY_MIN + random() * COMPLEXITY_RANGE;

    for(float i = 0.0; i < MAX_SPLITS; i++) {
        initSeed(vec3(cellId, timeIndex));
        float r = mix(0.35, 0.65, random());

        if(i >= MIN_SPLITS) {
            float stopProb = (i - MIN_SPLITS + 0.5) / baseComplexity;
            stopProb = clamp(stopProb * stopProb, 0.0, 0.8);
            if(random() < stopProb) break;
        }

        if(random() > 0.5) {
            float split = cell.x + cell.z * r;
            if(uv.x > split) {
                cell.x += cell.z * r;
                cell.z *= 1.0 - r;
                cellId.x += exp2(i);
            } else {
                cell.z *= r;
            }
        } else {
            float split = cell.y + cell.w * r;
            if(uv.y > split) {
                cell.y += cell.w * r;
                cell.w *= 1.0 - r;
                cellId.y += exp2(i);
            } else {
                cell.w *= r;
            }
        }
    }

    return cell;
}

