// Using function-scoped variables instead of defines

bool hitRootRS = false;

vec3 tex3DRS( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - 0.2)*7., 0.001);
    n /= (n.x + n.y + n.z );  

	return (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
}


float sminRS(float a, float b, float k){
   float f = max(0., 0.5 - abs(b - a)/k);
   return min(a, b) - k*.25*f*f;
}


float fractalRS(vec3 p) {
    float s,w,l;

    p.y *= .4;
    p += cos(p.yzx*12.)*.07;
    p.x -= 1.6;
    for (s=0.,w=.7; s++ < 7.; p *= l, w *= l )
        p  = abs(sin(p))-1.,
        l = 1.5/dot(p,p);

    return length(p)/w-.0005;
}


vec3 PRS(float z) {
    return vec3((cos(z * .2) * .3) * 10., 
                (cos(z * .15) * .3) * 10., z);
}

mat2 RRS(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float lightRS(vec3 p) {
    // Simplified static light source instead of moving one
    // Remove path dependency and make light position fixed
    vec3 lightPos = vec3(0.0, 0.5, 2.0); // Fixed light position
    return length(p - lightPos) - 0.25;
}

float rootRS(vec3 p){
    p.xy *= RRS(sin(p.z*2.)*.1 + sin(p.z*1.)*.3);
    p = mod(p, 1.) - .5;
    return .6*length(p.xy) - .01;
}

float mapRS(vec3 p) {
    float s, r;
    r = rootRS(p);

    // Remove the path movement - make the landscape static
    // p.xy -= PRS(p.z).xy;  // This was causing the movement
    s = sminRS(fractalRS(p), 2. - abs(p.y), .8);
    hitRootRS = r < s;
    s = min(s, r);
    return s;
}

// @iq - Rootsanctum version
float AORS(in vec3 pos, in vec3 nor) {
	float sca = 1.9, occ = 0.0;
    for( int i=0; i<5; i++ ){
        float hr = 0.01 + float(i)*0.5/4.0;        
        float dd = mapRS(nor * hr + pos);
        occ += (hr - dd)*sca;
        sca *= 0.7;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

void mainImageRoot(out vec4 o, in vec2 u, float pumpkinRot, float pumpkinDist) {
    float s=.1,d=0.,i=0.;
    vec3  res = iResolution;
    u = (u-res.xy/2.)/res.y;
    vec3  e = vec3(.001,0,0);

    // Convert pumpkin camera position to rootsanctum world coordinates
    // Pumpkin camera at (dist*cos(rot), dist, dist*sin(rot))
    vec3 pumpkinCamPos = vec3(pumpkinDist * cos(pumpkinRot), pumpkinDist, pumpkinDist * sin(pumpkinRot));

    vec3 rootWorldPos = pumpkinCamPos;

    // Use the converted position as camera origin
    vec3 ro = rootWorldPos;

    // Create a simple camera system instead of following the complex path
    vec3 target = vec3(0.0, 0.0, 0.0); // Look toward center
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(forward, up));
    up = cross(right, forward);

    vec3 D = normalize(forward + u.x * right + u.y * up);

    o = vec4(0);
    for(;i++ < 128.;){
        vec3 p = ro + D * d * 0.75;
        d += s = mapRS(p*1.5);
        o += 4e1*vec4(6,9,1,0) / max(s, .001);
        }

    vec3 hitPos = ro + D * d;
    vec3 normal = normalize(mapRS(hitPos) - vec3(mapRS(hitPos-e.xyy), 
                                                 mapRS(hitPos-e.yxy), 
                                                 mapRS(hitPos-e.yyx)));

    o.rgb *= tex3DRS(iChannel0, hitPos*.5, normal);
    o *= o;
    o *= hitRootRS ? .2*vec4(4,3,2,0) : vec4(1);
    o *= AORS(hitPos, normal);
    o = tanh(sqrt(d * o / pow(abs(lightRS(hitPos)), 7.) / 5e9 ));
}


// Based on complex spherical equation from Desmos 3D
// https://www.desmos.com/3d/afwrvhoa9n

// Adjustable parameters for the pumpkin equation
// Adjust these values to modify the shape
const float n0 = 20.0;   // Primary ridge frequency (higher = more ridges)
const float m0 = 0.0;    // Primary ridge offset
const float k0 = 0.1;    // Primary ridge amplitude

const float n1 = 20.0;    // Secondary ridge frequency
const float m1 = 0.0;    // Secondary ridge offset  
const float k1 = 0.15;   // Secondary ridge amplitude

const float n2 = 3.0;    // Tertiary ridge frequency
const float m2 = 0.0;    // Tertiary ridge offset
const float k2 = 0.1;    // Tertiary ridge amplitude

const float g = 0.5;     // Global size offset
const float p = 0.7;     // Vertical squash factor
const float h = 0.01;    // Top/bottom flattening

// Additional parameters for hollowing and lid
const float thickness = 0.03;    // Wall thickness for hollow effect
const float cutSphereRadius = 0.6; // Size of sphere to cut from top
const float cutSphereY = 0.3;    // Height of cutting sphere center
const float lidRaise = 0.5;      // How much to raise the lid
const float lidTiltX = 0.3;      // Tilt angle around X axis
const float lidTiltZ = 0.1;      // Tilt angle around Z axis

// SDF Operations
float opOnion(float sdf, float thickness) {
    return abs(sdf) - thickness;
}

float opSubtraction(float d1, float d2) {
    return max(-d1, d2);
}

float opUnion(float d1, float d2) {
    return min(d1, d2);
}

// Sphere SDF
float sphereSDF(vec3 pos, float radius) {
    return length(pos) - radius;
}

// Rotation matrices
mat3 rotateX(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(1.0, 0.0, 0.0,
                0.0, c, -s,
                0.0, s, c);
}

mat3 rotateZ(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(c, -s, 0.0,
                s, c, 0.0,
                0.0, 0.0, 1.0);
}

// Convert Cartesian to spherical coordinates
vec3 cartesianToSpherical(vec3 pos) {
    float rho = length(pos);
    float theta = atan(pos.z, pos.x);
    float phi = acos(pos.y / rho);
    return vec3(rho, theta, phi);
}

// The complex pumpkin equation converted to SDF
float pumpkinSDF(vec3 pos) {
    vec3 spherical = cartesianToSpherical(pos);
    float theta = spherical.y;
    float phi = spherical.z;

    // Use the constant parameters directly

    // Calculate the three sin terms
    float sin_term0 = sin(n0 * theta + m0);
    float sin_term1 = sin(n1 * theta + m1);
    float sin_term2 = sin(n2 * theta + m2);

    // First part: sum of the three weighted sin terms
    float part1 = (1.0 - pow(sin_term0, 4.0)) * (k0 / n0) +
                  (1.0 - sin_term1) * (k1 / n1) +
                  (1.0 - sin_term2) * (k2 / n2);

    // Second part: phi modulation
    float cos_2phi = cos(2.0 * phi);
    float part2 = pow((1.0 - cos_2phi) / 2.0, 2.0);

    // Third part: exponential and constant term
    float part3 = exp(-pow(phi - 3.14159 / 2.0, 2.0)) + p;

    // Fourth part: cosine modulation
    float part4 = 1.0 - h * cos_2phi;

    // Combine all parts to get the target radius
    float target_rho = (part1 * part2 + g) * part3 * part4;

    // Return SDF: current distance - target distance
    return length(pos) - target_rho;
}

// Hollow pumpkin with top cut off
float hollowPumpkinSDF(vec3 pos) {
    // Get the base pumpkin shape
    float pumpkin = pumpkinSDF(pos);

    // Make it hollow using onion operation
    float hollow = opOnion(pumpkin, thickness);

    // Create cutting sphere at the top
    vec3 cutSpherePos = pos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius);

    // Subtract the sphere from the hollow pumpkin
    return opSubtraction(cuttingSphere, hollow);
}

// Lid (the cut-off part) - raised and tilted
float lidSDF(vec3 pos) {
    // Transform position for the lid
    vec3 lidPos = pos;

    // Move down to original position, apply rotations, then raise up
    lidPos.y -= lidRaise;
    lidPos = rotateX(lidTiltX) * rotateZ(lidTiltZ) * lidPos;

    // Get the base pumpkin shape
    float pumpkin = pumpkinSDF(lidPos);

    // Make it hollow
    float hollow = opOnion(pumpkin, thickness);

    // Create cutting sphere - we want only the part that was cut off
    vec3 cutSpherePos = lidPos - vec3(0.0, cutSphereY, 0.0);
    float cuttingSphere = sphereSDF(cutSpherePos, cutSphereRadius);

    // Return only the intersection (the part that was inside the cutting sphere)
    // We want the hollow pumpkin AND inside the cutting sphere
    return max(hollow, cuttingSphere);
}

// Main scene SDF
float sceneSDF(vec3 pos) {
    float hollowPumpkin = hollowPumpkinSDF(pos);
    float lid = lidSDF(pos);

    return opUnion(hollowPumpkin, lid);
}

// Ray-plane intersection for debug visualization (camera-relative)
float rayPlaneIntersect(vec3 ro, vec3 rd, vec3 forward, float planeDistance) {
    float denom = dot(rd, forward);
    if (abs(denom) < 0.001) return -1.0; // Ray parallel to plane
    return planeDistance / denom;
}

// Raymarching function
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        vec3 pos = ro + t * rd;
        float d = sceneSDF(pos);
        if (d < 0.001) break;
        t += d * 0.8; // Use smaller step for complex shapes
        if (t > 20.0) break;
    }
    return t;
}

// Calculate normal using finite differences
vec3 getNormal(vec3 pos) {
    float eps = 0.001;
    vec3 n = vec3(
        sceneSDF(pos + vec3(eps, 0, 0)) - sceneSDF(pos - vec3(eps, 0, 0)),
        sceneSDF(pos + vec3(0, eps, 0)) - sceneSDF(pos - vec3(0, eps, 0)),
        sceneSDF(pos + vec3(0, 0, eps)) - sceneSDF(pos - vec3(0, 0, eps))
    );
    return normalize(n);
}

// Enhanced lighting function with material detection
vec3 lighting(vec3 pos, vec3 normal, vec3 viewDir) {
    vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 lightColor = vec3(1.0, 0.9, 0.8);

    // Ambient
    vec3 ambient = vec3(0.1, 0.15, 0.2);

    // Diffuse
    float diff = max(dot(normal, lightDir), 0.0);
    vec3 diffuse = diff * lightColor;

    // Specular
    vec3 reflectDir = reflect(-lightDir, normal);
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), 32.0);
    vec3 specular = spec * lightColor;

    // Determine material based on which part we're on
    float hollowDist = hollowPumpkinSDF(pos);
    float lidDist = lidSDF(pos);

    vec3 materialColor;
    if (lidDist < hollowDist) {
        // Lid material - slightly different color
        materialColor = vec3(0.9, 0.5, 0.15);
    } else {
        // Main pumpkin body
        materialColor = vec3(1.0, 0.6, 0.2);
    }

    return materialColor * (ambient + diffuse) + specular;
}

// Main function with split screen
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    bool rightHalf = fragCoord.x > iResolution.x * 0.5;

    float rot = (iMouse.x/iResolution.x*4.)-2.0;
    float dist = (iMouse.y/iResolution.y)*3.;

    if (rightHalf) {
        // Right side: Rootsanctum shader
        vec4 rootResult;
        mainImageRoot(rootResult, fragCoord, rot, dist);
        fragColor = rootResult;
    } else {
        // Left side: Pumpkin shader
        vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

        vec3 ro = vec3(dist * cos(rot), dist, dist * sin(rot));
        vec3 target = vec3(0.0, 0.0, 0.0);
        vec3 up = vec3(0.0, 1.0, 0.0);

        vec3 forward = normalize(target - ro);
        vec3 right = normalize(cross(forward, up));
        up = cross(right, forward);

        vec3 rd = normalize(forward + uv.x * right + uv.y * up);

        vec3 color = vec3(0.0);

        // Regular raymarch
        float t = raymarch(ro, rd);

        // Render based on what we hit
        if (t < 20.0) {
            vec3 pos = ro + t * rd;
            vec3 normal = getNormal(pos);
            vec3 viewDir = normalize(ro - pos);
            color = lighting(pos, normal, viewDir);

            // Add atmospheric perspective
            float fog = 1.0 - exp(-t * 0.1);
            color = mix(color, vec3(0.5, 0.7, 1.0), fog * 0.3);
        } else {
            // Background gradient
            color = mix(vec3(0.2, 0.3, 0.5), vec3(0.5, 0.7, 1.0), uv.y * 0.5 + 0.5);
        }

        // Gamma correction
        color = pow(color, vec2(1.0/2.2).xxx);
        fragColor = vec4(color, 1.0);
    }


}

// Created with SDF Editor: https://www.sleditor.com/SDFeditor.html
// Code output pasted straight into Shadertoy

// SDF Primitives and Operations
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float sdCylinder(vec3 p, float r, float h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdPlane(vec3 p, float h) {
    return p.y - h;
}

float sdPyramid(vec3 p, float h) {
    float m2 = h * h + 0.25;
    p.xz = abs(p.xz);
    if (p.z > p.x) {
        p.xz = p.zx;
    }
    p.xz -= vec2(0.5);
    vec3 q = vec3(p.z, h * p.y - 0.5 * p.x, h * p.x + 0.5 * p.y);
    float s = max(-q.x, 0.0);
    float t = clamp((q.y - 0.5 * p.z) / (m2 + 0.25), 0.0, 1.0);
    float a = m2 * (q.x + s) * (q.x + s) + q.y * q.y;
    float b = m2 * (q.x + 0.5 * t) * (q.x + 0.5 * t) + (q.y - m2 * t) * (q.y - m2 * t);
    float d2 = (min(q.y, -q.x * m2 - 0.5 * q.y) > 0.0) ? 0.0 : min(a, b);
    return sqrt((d2 + q.z * q.z) / m2) * sign(max(q.z, -p.y));
}

float sdVesicaSegment(vec3 p, vec3 a, vec3 b, float w) {
    vec3 c = (a + b) * 0.5;
    vec3 ba = b - a;
    float l = length(ba);
    vec3 v = ba / max(l, 0.0001);
    vec3 rel = p - c;
    float y = dot(rel, v);
    vec2 q = vec2(length(rel - y * v), abs(y));
    float r = 0.5 * l;
    float ww = max(w, 0.0001);
    float d = 0.5 * (r * r - ww * ww) / ww;
    vec3 h = (r * q.x < d * (q.y - r)) ? vec3(0.0, r, 0.0) : vec3(-d, 0.0, d + ww);
    return length(q - h.xy) - h.z;
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

float opSmoothIntersection(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

float opXor(float d1, float d2) {
    return max(min(d1, d2), -max(d1, d2));
}

mat3 rotateX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1, 0, 0, 0, c, -s, 0, s, c);
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0, s, 0, 1, 0, -s, 0, c);
}

mat3 rotateZ(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, -s, 0, s, c, 0, 0, 0, 1);
}


