vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        sceneSDF(p + e.xyy) - sceneSDF(p - e.xyy),
        sceneSDF(p + e.yxy) - sceneSDF(p - e.yxy),
        sceneSDF(p + e.yyx) - sceneSDF(p - e.yyx)
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 3.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = 0.0;
    vec3 col = vec3(1.0);

    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        if (d < 0.001) {
            vec3 n = calcNormal(p);
            vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0));
            float diff = max(dot(n, lightDir), 0.0);
            float amb = 0.3;
            col = vec3(0.85, 0.75, 0.7) * (diff + amb);
            break;
        }

        if (t > 20.0) break;
        t += d;
    }

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 data = texture(iChannel0, fragCoord.xy/iResolution.xy, 0.);

    vec4 data1 = texture(iChannel0, (fragCoord.xy+vec2(0,1))/iResolution.xy, 0.);
    vec4 data2 = texture(iChannel0, (fragCoord.xy+vec2(1,0))/iResolution.xy, 0.);
    vec4 data3 = texture(iChannel0, (fragCoord.xy+vec2(1,1))/iResolution.xy, 0.);

    float d = data.a;
    float d0=d;
    float d1 = data1.a;
    float d2 = data2.a;
    float d3 = data3.a;
    d *= 3.0;
    vec3 col = (d > 0.0) ? vec3(0.9, 0.6, 0.3) : vec3(0.65, 0.85, 1.0);
    col *= 1.0 - exp(-6.0 * abs(d));
    col *= 0.8 + 0.2 * cos(150.0 * d);
    col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0, 0.005, abs(d)));
    fragColor = vec4(col, 1.0);

    float pc = 0.7/iResolution.x;

    if(abs(d0-d2)<pc&&abs(d0-d1)<pc&&abs(d0-d3)<pc){fragColor = vec4(1.0);}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tex = texture(iChannel0, fragCoord.xy/iResolution.xy, 0.);
    float inside = 0.02;
    float dist = tex.r;
    float newDist;

    if(iMouse.z > 0.0)
    {
        newDist = (distance(iMouse.xy, fragCoord.xy)/iResolution.x) - inside;

        if(tex.b == 0.1) {
            newDist = min(dist, newDist);
        }

        // Store distance in R, final corrected in G, state in B
        fragColor = vec4(newDist, tex.g, 0.1, 1.0);
    } 
    else if(tex.b != 0.1) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } 
    else {
        fragColor = tex;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 pixelSize = 1.0/iResolution.xy;

    vec4 drawn = texture(iChannel1, uv, 0.);
    vec4 prev = texture(iChannel0, uv, 0.);

    // If mouse is pressed, keep working with drawn data but preserve corrected
    if(iMouse.z > 0.0) {
        // Pass through drawn distance, but keep the corrected value
        fragColor = vec4(drawn.r, prev.g, drawn.b, 1.0);
        fragColor.a = (fragColor.r<0.) ? prev.g == 0.0? drawn.r:prev.g : fragColor.r;
        return;
    }

    float currentDist = prev.r;
    float correctedDist = prev.g; // Final corrected distance
    float state = prev.b;

    // Pixel-relative boundary threshold
    float boundaryThreshold = pixelSize.x;

    // Initialize: mark pixels near boundary as correct
    if(state == 0.1 && abs(currentDist) < boundaryThreshold) {
        state = 0.5;
        correctedDist = currentDist;
    }

    // If this pixel needs fixing (not yet corrected)
    if(state == 0.1 && currentDist < 0.0) {

        float bestDist = currentDist;
        bool foundCorrect = false;

        // Larger kernel for faster initial propagation
        const int kernelSize = 5; // Adjust this: 1=3x3, 2=5x5, 4=9x9, 8=17x17

        for(int y = -kernelSize; y <= kernelSize; y++) {
            for(int x = -kernelSize; x <= kernelSize; x++) {
                if(x == 0 && y == 0) continue;

                vec2 offset = vec2(x, y) * pixelSize;
                vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                if(neighbor.b >= 0.5) {
                    float nDist = neighbor.g; // Use corrected distance from neighbor
                    float stepDist = length(vec2(x, y)) * pixelSize.x;
                    float proposedDist = nDist - stepDist;

                    if(!foundCorrect || proposedDist > bestDist) {
                        bestDist = proposedDist;
                        foundCorrect = true;
                    }
                }
            }
        }

        if(foundCorrect) {
            correctedDist = bestDist;
            state = 0.5;
        }
    }

    // Corrected pixels: refine distance and apply smoothing
    else if(state >= 0.5 && state < 1.0 && currentDist < 0.0) {

        float bestDist = correctedDist;

        // Still use larger kernel for refinement (or reduce to 1 for accuracy)
        const int refineKernel = 2; // 5x5 for refinement phase

        for(int y = -refineKernel; y <= refineKernel; y++) {
            for(int x = -refineKernel; x <= refineKernel; x++) {
                if(x == 0 && y == 0) continue;

                vec2 offset = vec2(x, y) * pixelSize;
                vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                if(neighbor.b >= 0.5) {
                    float nDist = neighbor.g; // Use corrected distance
                    float stepDist = length(vec2(x, y)) * pixelSize.x;
                    float proposedDist = nDist - stepDist;

                    if(proposedDist > bestDist) {
                        bestDist = proposedDist;
                    }
                }
            }
        }

        correctedDist = bestDist;

        // Apply smoothing
        if(state > 0.5 && state < 0.9) {
            float sumDist = 0.0;
            float sumWeight = 0.0;

            for(int y = -1; y <= 1; y++) {
                for(int x = -1; x <= 1; x++) {
                    vec2 offset = vec2(x, y) * pixelSize;
                    vec4 neighbor = texture(iChannel0, uv + offset, 0.);

                    if(neighbor.b >= 0.5) {
                        float nDist = neighbor.g; // Use corrected distance
                        float weight = 1.0 / (1.0 + length(vec2(x, y)));
                        sumDist += nDist * weight;
                        sumWeight += weight;
                    }
                }
            }

            if(sumWeight > 0.0) {
                float smoothedDist = sumDist / sumWeight;
                float smoothStrength = 0.3 * (1.0 - (state - 0.5) / 0.4);
                correctedDist = mix(correctedDist, smoothedDist, smoothStrength);
            }
        }

        state = min(state + 0.009, 1.0);
    }

    // Store: R = current/working distance, G = corrected distance, B = state
    fragColor = vec4(currentDist, correctedDist, state, 1.0);
    fragColor.a = (fragColor.r<0.) ? fragColor.g : fragColor.r;
}

vec3 addLight(vec3 pos, vec3 norm, vec3 scol, vec3 lcol, vec3 lpos){
    vec3 ref = reflect(vec3(0.0, 0.0, -1.0), norm);
    vec3 diff = lpos - pos;
    float ldist = length(diff);
    diff = diff/ldist;
    float specular = pow(max(0.0, dot(diff, ref)), 16.0);
    float diffuse = max(0.0, dot(diff, norm));
    vec3 col1 = scol*0.5;
    vec3 col2 = scol*0.5*(lcol*diffuse);
    col2 = col2+(lcol*specular);
    return col1+col2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.y;

    vec2 mouse = iMouse.xy/iResolution.y;

    vec3 sphere = vec3(mouse.x,mouse.y, 0.08);
    vec2 distov = uv;// - sphere.xy;
    vec3 col = vec3(1);

    float uvx = 120.;

    sphere.z += sin(distov.x*30.)/ uvx;
    sphere.z += sin(distov.y*40.)/ uvx;
    sphere.x += sin(distov.x*30.)/ uvx;
    sphere.x += sin(distov.y*50.)/ uvx;
    sphere.y += sin(distov.x*20.)/ uvx;
    sphere.y += sin(distov.x*20.)/ uvx;
    sphere.z += sin(distov.y*30.)/ uvx;
    sphere.z += sin(distov.x*40.)/ uvx;
    float dist = distance(sphere.xy, uv);
    vec2 distv = uv - sphere.xy;
    float height = sqrt((sphere.z*sphere.z)-(dist*dist));
    vec3 normal = vec3(distv.x, distv.y, height)/sphere.z;
    if(dist<sphere.z){
        col = vec3(1,0.6,0.5);
        col = addLight(vec3(sphere.xy, height), normal, col, vec3(1), vec3(3.,1., 0));

    }


    fragColor = vec4(col,1.0);
}

