    lights[3] = light(vec3(0.3,1.0,0.3),vec3(ar*1.04, 0.5 ,0.05),vec3(0.0),vec3(0.0),vec2(0.0),0.0, 0.025, true, false);

    lights[2] = light(vec3(0.2,0.2,0.9),vec3(0.0),vec3(0.0),vec3(0.0),vec2(0.0),0.0, 0.0015, false, false);

    lights[2].position = Robs[tractee].position;
    lights[2].position.z = 0.001;
    lights[2].ison = tracting;
    if(uv.x>=ar/2.0){
        lights[1] =  lights[3]; lights[1].ison = true;
    }
        vec3 tcol = vec3(0.2,0.2,0.6);
    if(tracter<6){tcol = vec3(0.5,0.2,0.2);}else{tcol = vec3(0.2,0.5,0.2);}
    lights[2].colour = tcol;

    if(courtprox.w>0.0 && courtprox.w*bar>courtprox.z && courtprox.w*bar>courtprox.x){
        bgcol = pgrey;
        bgnormal = vec3(0.0,-1.0,0.0);
        ZoneWall = true;
        height = courtprox.w * wallheightfactor;
    } else if(courtprox.y>0.0 && courtprox.y*bar>courtprox.z && courtprox.y*bar>courtprox.x){
        bgcol = pgrey;
        bgnormal = vec3(0.0,1.0,0.0);
        ZoneWall = true;
        height = courtprox.y *  wallheightfactor;
    } else if(goalprox>0.0 && courtprox.x>0.0 || courtprox.x>(border*bar)/2.0){
        bgcol = pgrey;
        bgnormal = vec3(1.0,0.0,0.0);
        ZoneWall = true; ZoneGoalWall = true;
        float cph = courtprox.x/2.2;
        if(goalprox<cph && courtprox.x<(border*bar)/2.0 ){
            ZoneInsideWall = true;
            height = goalprox * wallheightfactor;
            if(uv.y<0.5){bgnormal = vec3(0.0,1.0,0.0);}else{bgnormal = vec3(0.0,-1.0,0.0);} 
        } else {
            height = courtprox.x * wallheightfactor;      
        }
    } else if(goalprox>0.0 && courtprox.z>0.0 || courtprox.z>(border*bar)/2.0){
        bgcol = pgrey;
        bgnormal = vec3(-1.0,0.0,0.0);
        ZoneWall = true; ZoneGoalWall = true;
        if(goalprox<courtprox.z/2.2 && courtprox.z<(border*bar)/2.0 ){
            ZoneInsideWall = true;
            height = goalprox * wallheightfactor;
            if(uv.y<0.5){bgnormal = vec3(0.0,1.0,0.0);}else{bgnormal = vec3(0.0,-1.0,0.0);}
        } else {
            height = courtprox.z * wallheightfactor;

        }
    } else {
        ZoneGround = true;
        bgcol = getGroundColour();
        bgnormal = vec3(0.0,0.0,1.0);
    }

    float limA = courtyard.x;
    float limB = courtprox.x;

    if(uv.x>ar/2.0){ limA = courtyard.z; limB = courtprox.z; }

    float lightATG = (goaltop-lights[1].position.y)/(lights[1].position.x-limA);
    float lightATGdist = ( (lights[1].position.x - uv.x) * lightATG) -  (uv.y-lights[1].position.y);
    float lightABG = (goalbottom-lights[1].position.y)/(lights[1].position.x-limA);
    float lightABGdist =    (uv.y-lights[1].position.y)-( (lights[1].position.x - uv.x) * lightABG);
    float lightAdist = lightABGdist*lightATGdist;

    lights[1].shadow.x = 1.0;

    if(goalprox<0.0||ZoneInsideWall){lights[1].shadow.x = 0.0;} else if(lightAdist>0.0){
        lights[1].shadow.x = 0.0;
        f = smoothstep(0.0, 0.2*-limB, lightAdist);
        lights[1].shadow.x = mix(1.0, 0.0, f);
    }

    lights[1].shadow.y = lights[1].shadow.x;

    for(int i=0;i<numrobs;i++){
        if(Robs[i].ison){
        float bdist = distance(uv, Robs[i].position.xy);
        if(bdist<dist){dist=bdist;}
        float edgedist = distance(uv, Robs[i].position.xy) + (Robs[i].position.z);
        if(bdist<Robs[i].radius){
            dist=bdist;
            radius = Robs[i].radius;
            height = sqrt((radius*radius)-(dist*dist));
            ZoneRob = true;
            rindex = i;
            if(i>5){flatTop=true;}
            if(bdist<Robs[i].radius-pixel){ZoneGround = false;}
            robambient.y *= 1.0 - (0.5 * pow(0.8/(edgedist/Robs[i].radius),3.0));
        } else {
            robambient *= 1.0 - (0.5 * pow(0.8/(edgedist/Robs[i].radius),3.0));
        }
      }
    }

    if(ZoneRob){
        fgcol = Robs[rindex].colourA;
        centre = vec2(Robs[rindex].position.x, Robs[rindex].position.y);
        distv = uv - centre;
        normal = vec3(distv.x, distv.y, height)/radius;
        height+=Robs[rindex].position.z;

        if(flatTop){
            float tradius = radius/2.5;
            theight = sqrt((radius*radius)-(tradius*tradius));
            if(height>theight){
                height=theight;
                normal = vec3(0.0, 1.0, 0.0);
            }
            f = smoothstep(theight-0.01, theight, height);
            normal = mix(normal,vec3(0.0, 0.0, 1.0),  f);
            height = mix(height,theight,  f);
        }

        robambient.x *= (0.6 + normal.z/3.0);
        float rotation = Robs[rindex].rotation;
        if(rindex>0){
            float SpFoffset = radius*2.0;
            float SpBoffset = radius/1.0;
            float SpFheight = radius*-1.0;
            float SpBheight = radius*1.4;
            float SpWidth = radius/1.4;
            float SpLength = SpFoffset+SpBoffset;
            float SpHeight = SpBheight-SpFheight;
            vec3 SpFc = vec3(centre.x + SpFoffset * cos(rotation),  centre.y + SpFoffset * sin(rotation), 0.0);
            vec3 SpFl = SpFc; SpFl.x += (SpWidth * sin(rotation)); SpFl.y -= (SpWidth * cos(rotation));
            vec3 SpFr = SpFc; SpFr.x -= (SpWidth * sin(rotation)); SpFr.y += (SpWidth * cos(rotation));
            vec3 SpBc = vec3(centre.x - SpBoffset * cos(rotation), centre.y - SpBoffset * sin(rotation), 0.0);
            vec3 SpBl = SpBc; SpBl.x += (SpWidth * sin(rotation)); SpBl.y -= (SpWidth * cos(rotation));
            vec3 SpBr = SpBc; SpBr.x -= (SpWidth * sin(rotation)); SpBr.y += (SpWidth * cos(rotation));
            float SpFD = linePointLength( vec3(uv.x, uv.y, 0.0), vec3(SpFl.x, SpFl.y, 0.0), vec3(SpFr.x, SpFr.y, 0.0) );
            float SpH = SpFheight + (SpHeight*(SpFD/SpLength));
            float inTri = sdTriangle(uv, SpFc.xy, SpBr.xy, SpBl.xy);

            float lineDist = linePointLength( vec3(uv.x, uv.y, 0.0) , vec3(SpBc.x, SpBc.y, 0.0)  , vec3(SpFc.x, SpFc.y, 0.0)  );

            float linewid =  radius*0.05;
            float linemid = abs((radius*0.45)-lineDist);
            if(linemid<linewid){fgcol=Robs[rindex].colourB;}
            if(linemid<linewid+pixel){
                f = smoothstep(linewid,linewid+pixel, linemid);
                fgcol = mix(Robs[rindex].colourB, fgcol, f);
            }
            if(rindex>5){
                if(SpFD<radius*2.0){
                    float linewid =  radius*0.05;
                    float linemid = abs((radius*+0.45)-dist);
                    if(linemid<linewid){fgcol=Robs[rindex].colourB;} else if(dist>(radius*+0.45)) {fgcol=Robs[rindex].colourA;}
                    if(linemid<linewid+pixel){
                        f = smoothstep(linewid,linewid+pixel, linemid);
                        fgcol = mix(Robs[rindex].colourB, fgcol, f);
                    }
                }
            } else {
                if(SpFD>radius*2.5&&lineDist<(radius*0.5)+pixel){
                    float linewid =  radius*0.05;
                    float linemid = abs((radius*+0.75)-dist);
                    if(linemid<linewid){fgcol=Robs[rindex].colourB;} else if(dist>(radius*+0.75)) {fgcol=Robs[rindex].colourA;}
                    if(linemid<linewid+pixel){
                        f = smoothstep(linewid,linewid+pixel, linemid);
                        fgcol = mix(Robs[rindex].colourB, fgcol, f);
                    }
                }
            }

            if(SpH>height){
                float face = 0.7; 
                if(inTri<0.0&&dist<radius*0.9){
                    if(dist<radius*0.7){
                        f = smoothstep(0.0, pixel*5.0, SpH-height);
                        height=mix(height, SpH,  f);
                        normal=mix(normal,vec3( face * cos(rotation) , face * sin(rotation), 0.7), f);
                    } else {
                        f = smoothstep(radius*0.75, radius*0.95, dist);
                        height=mix( SpH, height, f);
                        normal=mix(vec3( face * cos(rotation) , face * sin(rotation), 0.7),normal, f);
                    }
                } 
            }

        }
        normal = normal/length(normal);
        /*    // ----  Objects casting shadows on other objects
        for(int i=0;i<numrobs;i++){
            if(i!=rindex&&Robs[i].ison){
                for(int j=0;j<numlights;j++){
                   if(lights[j].casts&&lights[j].ison){
                     if(distance(Robs[i].position, lights[j].position) < distance(Robs[rindex].position, lights[j].position)){
                        vec3 tempBall = Robs[i].position;
                        tempBall.z*=1.2;
                        float beam = linePointLength(tempBall, lights[j].position, vec3(uv.x, uv.y, height)); 
                        float cradius = Robs[i].radius*1.2; // cheating and making the ball bigger so shadow seen more easily
                        float blur = distance(Robs[i].position, vec3(uv.x, uv.y, height))/5.0;
                        float shadow = 0.0;
                        if (beam<=(cradius-blur)){shadow = 1.0;} else {
                            f = smoothstep(cradius-blur, cradius, beam);
                            shadow = mix(1.0,0.0, f);
                        }
                            lights[j].shadow.y = max(shadow, lights[j].shadow.y);
                            }
                        }
                    }
                }
            }
        }
        // ----  Lots of GPU for small improvement, Uncomment to use
        */ 
    }

    if(ZoneGround||ZoneWall){
        float shadowheight = 0.0;
        if(ZoneWall){shadowheight = height;};
        for(int i=0;i<numrobs;i++){
            if(Robs[i].ison){
            for(int j=0;j<numlights;j++){
            if(lights[j].casts&&lights[j].ison){
                float beam = linePointLength(Robs[i].position, lights[j].position, vec3(uv.x, uv.y, shadowheight)); 
                float cradius = Robs[i].radius;
                float blur = 0.;
                blur=distance(Robs[i].position, vec3(uv.x, uv.y, shadowheight))/8.0;
                float shadow = 0.0;
                if (beam<=(cradius-blur)){shadow = 1.0;} else {
                    f = smoothstep(cradius-blur, cradius, beam);
                    shadow = mix(1.0,0.0, f);
                }
                lights[j].shadow.x = max(shadow, lights[j].shadow.x);
            }
            }
            }
        }
    }

    vec2 ambientmulti = vec2((min(0.5+(height*3.0), 1.0)),0.5);
    float ambiA = 1.0;float ambiB = 1.0; float ambiC = 0.5;
    ambientmulti *= sqrt((min(ambiC-(courtprox.w*ambiA), ambiB))*(min(ambiC-courtprox.z*ambiA, ambiB))*(min(ambiC-courtprox.y*ambiA, ambiB))*(min(ambiC-courtprox.x*ambiA, ambiB)));

    vec3 position = vec3(uv, height);
    vec3 bgtot = vec3(0.0);
    vec3 fgtot = vec3(0.0);
    vec3 sptot = vec3(0.0);
    for(int j=0;j<numlights;j++){
   if(lights[j].ison){
        lights[j].difference = lights[j].position - position;
        lights[j].dist = length(lights[j].difference);
        lights[j].difference = lights[j].difference/lights[j].dist;
        lights[j].amount.x = max(0.0, dot(lights[j].difference, normal));
        lights[j].amount.x = lights[j].amount.x/(lights[j].dist*lights[j].dist);
        lights[j].amount.y = max(0.0, dot(lights[j].difference, bgnormal));
        lights[j].amount.y = lights[j].amount.y/(lights[j].dist*lights[j].dist);
        lights[j].amount.y *= lights[j].intensity * (1.0-lights[j].shadow.x);
        lights[j].amount.x *= lights[j].intensity * (1.0-lights[j].shadow.y);
        bgtot += lights[j].colour*lights[j].amount.y;
        fgtot += lights[j].colour*lights[j].amount.x;
        }
    }

    bgcol*=(robambient.y*ambientmulti.y)+bgtot;
    if(ZoneRob){
        vec3 incident = vec3(0.0, 0.0, -1.0);
        vec3 reflection = reflect(incident, normal);
        for(int j=0;j<numlights;j++){
        if(lights[j].ison){
            lights[j].amount.z = pow(max(0.0, dot(lights[j].difference, reflection)),32.0);
            lights[j].amount.z = lights[j].amount.z/(lights[j].dist*lights[j].dist);
            lights[j].amount.z *= lights[j].intensity * (1.0-lights[j].shadow.y);
            sptot += lights[j].colour*lights[j].amount.z;
            }
        }

        fgcol *= (robambient.x*ambientmulti.x)+fgtot;
        fgcol += sptot;

        if(ZoneGround){
            f = smoothstep(radius-pixel, radius, dist);
            col = mix(fgcol,bgcol, f);
        } else {
            col = fgcol;
        }
    } else {
       col = bgcol;
    }

    if(tracting){
        float tdist = dradius*0.5;
        float tdistf = distance(Robs[tracter].position.xy, Robs[tractee].position.xy)/2.0;
        float cosr = cos(Robs[tracter].rotation);
        float sinr = sin(Robs[tracter].rotation);
        vec2 tpA = vec2(Robs[tracter].position.x + (tdist * cosr), Robs[tracter].position.y + (tdist * sinr));
        vec2 tpB = vec2(Robs[tracter].position.x + (tdistf * cosr), Robs[tracter].position.y + (tdistf * sinr));
        float tpdist = distance(uv, Robs[tractee].position.xy);
        float tqdist = distance(uv, tpA);
        float tractor = sdBezier(uv, tpA, tpB, Robs[tractee].position.xy);
        float lv = 0.01*tqdist;
        float lw = (0.6*tqdist)+sin((mTime*80.0)+(tqdist*200.0))/80.0;
        vec3 lcol = tcol/2.;
        if(tractor<lw){f = smoothstep(lv, lw, tractor);col = mix(col+lcol,col, f);}

        if(rindex==tractee){
            float llw = 0.4;
            vec3 lnorm = vec3(sin(mTime*3.),cos(mTime*11.),cos(mTime*7.));
            float ncola = sin(dot(normal, lnorm));
            float ncolb = sin(dot(normal, lnorm.yzx));
            float ncolc = sin(dot(normal, lnorm.zxy));
            if(ncolc>sin(ncolc*5.) && ncolc<sin(ncolc*5.)+llw){col += tcol;}
        }
    }

    col = contrast(col, 1.2);
    col = gammaCorrection(col, .95);
    if(outputMode){col = outputColour;}
    fragColor = vec4(col,1.0);
}

#define numrobs 11
#define numlights 4

#define white vec3(1.)
#define pink vec3(.95,.75,.75)
#define pgreen vec3(.7,.9,.7)
#define pblack vec3(.6,.6,.6)
#define black vec3(.0)
#define pgrey vec3(0.85)
#define pred vec3(1., .5, .5)

float pixel = 0.0;
vec4 courtprox = vec4(0.0);
vec2 uv = vec2(0.0);
float ar = 0.0;
float f = 0.0; 
float border = 0.05;
float bar = 1.0;

struct light
{
    vec3 colour;
    vec3 position;
    vec3 difference;
    vec3 amount;
    vec2 shadow;
    float dist;
    float intensity;
    bool casts;
    bool ison;
};

struct Rob
{
    vec3 position;
    vec3 colourA;
    vec3 colourB;
    float rotation;
    float radius;
    bool ison;
};

vec3 getGroundColour(){
    float linewidth = 0.01;
    float hlinewidth = linewidth/2.0;
    float rhlinewidth = hlinewidth + pixel/2.0;
    vec3 rcol = pgrey;
    if(courtprox.x>-linewidth&&courtprox.x<=0.0||abs(courtprox.y)<linewidth||courtprox.z>-linewidth&&courtprox.z<=0.0||abs(courtprox.w)<linewidth){
        rcol = white;
    }
    float crad = 0.2;
    float dfc = distance(uv, vec2(ar/2.0, 0.5));
    float crdiff = dfc-crad;
    if(crdiff<rhlinewidth ){
        f = smoothstep(rhlinewidth-pixel,rhlinewidth , abs(crdiff));
        rcol = mix(white, pgrey, f);
    }

    if(crdiff>=hlinewidth-pixel&&abs(uv.x - (ar/2.0))<=hlinewidth){
        rcol = white;
    }
    dfc = distance(uv, vec2(border*bar, 0.5));
    crdiff = dfc-crad;
    if(crdiff<rhlinewidth ){
        f = smoothstep(rhlinewidth-pixel,rhlinewidth , abs(crdiff));
        rcol = mix(white, rcol, f);
    }
    dfc = distance(uv, vec2(ar-(border*bar), 0.5));
    crdiff = dfc-crad;
    if(abs(crdiff)<rhlinewidth ){
        f = smoothstep(rhlinewidth-pixel,rhlinewidth , abs(crdiff));
        rcol = mix(white, rcol, f);
    }
    return rcol;
}

bool outputMode = false; vec3 outputColour;
void outputValue (in float value){
    outputMode = true;
    if ( value >= 0.0 && value <= 1.0 )
        { outputColour = vec3(value, value, value); }
        else if ( value < 0.0 && value > -1.0 )
        { outputColour = vec3(0.0, 0.0, -value ); }
        else if ( value > 1.0 )
        { outputColour = vec3(1.0-(1.0/value), 1.0, 0.0); }
        else if ( value < -1.0 )
        { outputColour = vec3(1.0-(1.0/-value), 0.0, 1.0); }
} 
void outputValue (in vec3 value){outputMode = true;outputColour = value;}

vec3 contrast(vec3 color, float value) {
  return 0.5 + value * (color - 0.5);
}

vec3 gammaCorrection (vec3 colour, float gamma) {
  return pow(colour, vec3(1. / gamma));
}

float linePointLength( in vec3 P, in vec3 A, in vec3 B ){
    vec3 AB = B-A;
    float lenAB = length(AB);
    vec3 D = AB/lenAB;
    vec3 AP = P-A;
    float d = dot(D, AP);
    vec3 X = A + D * dot(P-A, D);
    if(X.z<0.0){return 10.0;} else {return length(X-P);}
}

vec3 linePoint( in vec3 P, in vec3 A, in vec3 B ){
    vec3 AB = B-A;
    float lenAB = length(AB);
    vec3 D = AB/lenAB;
    vec3 AP = P-A;
    float d = dot(D, AP);
    vec3 X = A + D * dot(P-A, D);
    return X;
}

// SD Functions from https://iquilezles.org/articles/distfunctions2d/

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 ){
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}
float dot2( vec2 v ) { return dot(v,v); }
float sdBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      
    float res = 0.0;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx-3.0*ky) + kz;
    float h = q*q + 4.0*p3;
    if( h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        res = dot2(d + (c + b*t)*t);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp(vec3(m+m,-n-m,n-m)*z-kx,0.0,1.0);
        res = min( dot2(d+(c+b*t.x)*t.x),
                   dot2(d+(c+b*t.y)*t.y) );
    }
    return sqrt( res );
}

// Model created with SDF Editor: https://www.sleditor.com/SDFeditor.html
// Enhanced in sleditor using Claude Sonnet 4.5

// SLAI request: Could you improve this sdf spider? maybe add a head with eyes, some colour, anything else you think appropriate.

// Response: head, eyes, better body definition, colors, and ambient occlusion
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdCylinder(vec3 p, float r, float h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdVesicaSegment(vec3 p, vec3 a, vec3 b, float w) {
    vec3 c = (a + b) * 0.5;
    vec3 ba = b - a;
    float l = length(ba);
    vec3 v = ba / max(l, 0.0001);
    vec3 rel = p - c;
    float y = dot(rel, v);
    vec2 q = vec2(length(rel - y * v), abs(y));
    float r = 0.5 * l;
    float ww = max(w, 0.0001);
    float d = 0.5 * (r * r - ww * ww) / ww;
    vec3 h = (r * q.x < d * (q.y - r)) ? vec3(0.0, r, 0.0) : vec3(-d, 0.0, d + ww);
    return length(q - h.xy) - h.z;
}

float sdEllipsoid(vec3 p, vec3 r) {
    float k0 = length(p / r);
    float k1 = length(p / (r * r));
    return k0 * (k0 - 1.0) / k1;
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

float opSmoothIntersection(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

mat3 rotateX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1, 0, 0, 0, c, -s, 0, s, c);
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0, s, 0, 1, 0, -s, 0, c);
}

mat3 rotateZ(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, -s, 0, s, c, 0, 0, 0, 1);
}

// Material ID tracking
float gMaterialID = 0.0;

float sceneSDF(vec3 p) {
    vec3 d_node_29_p = p;
    d_node_29_p -= vec3(0.000, -0.2, 0.000);
    d_node_29_p *= rotateY(-0.5+(iMouse.z!=0.?iMouse.x*5./iResolution.x:iTime * 0.5));
    d_node_29_p *= rotateZ(0.);
    float d_node_29_scale = max(0.850, 0.0001);
    d_node_29_p /= d_node_29_scale;

    // BODY SECTION
    vec3 d_node_26_p = d_node_29_p;
    d_node_26_p -= vec3(0.000, 0.000, -0.500);
    d_node_26_p *= rotateY(-0.0349);
    float d_node_26_scale = max(1.000, 0.0001);
    d_node_26_p /= d_node_26_scale;
    vec3 d_node_24_p = d_node_26_p;
    d_node_24_p -= vec3(0.000, 0.600, -0.200);
    float d_node_24_scale = max(1.000, 0.0001);
    d_node_24_p /= d_node_24_scale;
    float d_node_24 = sdSphere(d_node_24_p, 0.500);
    d_node_24 *= d_node_24_scale;
    vec3 d_node_28_p = d_node_26_p;
    d_node_28_p -= vec3(0.000, 0.000, 0.000);
    float d_node_28_scale = max(1.000, 0.0001);
    d_node_28_p /= d_node_28_scale;
    vec3 d_node_25_p = d_node_28_p;
    d_node_25_p -= vec3(0.000, 0.400, 0.000);
    float d_node_25_scale = max(0.400, 0.0001);
    d_node_25_p /= d_node_25_scale;
    float d_node_25 = sdBox(d_node_25_p, vec3(0.350, 0.200, 1.600));
    d_node_25 *= d_node_25_scale;
    float d_node_28 = d_node_25 - 0.110;
    d_node_28 *= d_node_28_scale;
    float d_node_26 = opSmoothUnion(d_node_24, d_node_28, 0.410);
    d_node_26 *= d_node_26_scale;

    // LEGS SECTION
    vec3 d_node_23_p = d_node_29_p;
    d_node_23_p -= vec3(0.000, 0.100, -0.200);
    d_node_23_p *= rotateY(-0.1047);
    float d_node_23_scale = max(1.000, 0.0001);
    d_node_23_p /= d_node_23_scale;
    vec3 d_node_23_warp = d_node_23_p;
    d_node_23_warp.x = abs(d_node_23_warp.x);
    vec3 d_node_22_p = d_node_23_warp;
    d_node_22_p -= vec3(0.300, 0.000, 0.000);
    d_node_22_p *= rotateY(-1.3439);
    float d_node_22_scale = max(0.750, 0.0001);
    d_node_22_p /= d_node_22_scale;
    vec3 d_node_22_warp = d_node_22_p;
    d_node_22_warp.x = abs(d_node_22_warp.x);
    vec3 d_node_21_p = d_node_22_warp;
    d_node_21_p -= vec3(0.200, 0.000, 0.000);
    d_node_21_p *= rotateX(0.0873);
    d_node_21_p *= rotateY(-0.1920);
    float d_node_21_scale = max(1.000, 0.0001);
    d_node_21_p /= d_node_21_scale;
    vec3 d_node_21_warp = d_node_21_p;
    d_node_21_warp.x = abs(d_node_21_warp.x);
    vec3 d_node_13_p = d_node_21_warp;
    d_node_13_p -= vec3(0.100, 0.100, 0.100);
    d_node_13_p *= rotateX(0.0349);
    d_node_13_p *= rotateY(-1.6930);
    d_node_13_p *= rotateZ(-0.1047);
    float d_node_13_scale = max(0.550, 0.0001);
    d_node_13_p /= d_node_13_scale;
    vec3 d_node_14_p = d_node_13_p;
    d_node_14_p -= vec3(0.400, 0.000, 0.000);
    d_node_14_p *= rotateZ(0.1920);
    float d_node_14_scale = max(1.000, 0.0001);
    d_node_14_p /= d_node_14_scale;
    float d_node_14 = sdCylinder(d_node_14_p, 0.100, 0.300);
    d_node_14 *= d_node_14_scale;
    vec3 d_node_15_p = d_node_13_p;
    d_node_15_p -= vec3(0.000, 0.000, 0.000);
    float d_node_15_scale = max(1.000, 0.0001);
    d_node_15_p /= d_node_15_scale;
    vec3 d_node_12_p = d_node_15_p;
    d_node_12_p -= vec3(0.000, 0.000, 0.000);
    d_node_12_p *= rotateZ(0.5411);
    float d_node_12_scale = max(1.050, 0.0001);
    d_node_12_p /= d_node_12_scale;
    vec3 d_node_11_p = d_node_12_p;
    d_node_11_p -= vec3(0.400, 0.000, 0.000);
    float d_node_11_scale = max(1.000, 0.0001);
    d_node_11_p /= d_node_11_scale;
    float d_node_11 = sdSphere(d_node_11_p, 0.150);
    d_node_11 *= d_node_11_scale;
    vec3 d_node_10_p = d_node_12_p;
    d_node_10_p -= vec3(-0.200, 0.000, 0.000);
    float d_node_10_scale = max(1.350, 0.0001);
    d_node_10_p /= d_node_10_scale;
    float d_node_10 = sdVesicaSegment(d_node_10_p, vec3(-1.100, 0.000, 0.000), vec3(0.500, 0.000, 0.000), 0.120);
    d_node_10 *= d_node_10_scale;
    float d_node_12 = opSmoothUnion(d_node_11, d_node_10, 0.120);
    d_node_12 *= d_node_12_scale;
    vec3 d_node_17_p = d_node_15_p;
    d_node_17_p -= vec3(0.000, 0.000, 0.000);
    float d_node_17_scale = max(1.000, 0.0001);
    d_node_17_p /= d_node_17_scale;
    vec3 d_node_16_p = d_node_17_p;
    d_node_16_p -= vec3(-1.800, 0.400, 0.000);
    d_node_16_p *= rotateZ(-0.9948);
    float d_node_16_scale = max(1.000, 0.0001);
    d_node_16_p /= d_node_16_scale;
    float d_node_16 = sdVesicaSegment(d_node_16_p, vec3(-1.400, 0.000, 0.000), vec3(0.500, 0.000, 0.000), 0.160);
    d_node_16 *= d_node_16_scale;
    vec3 d_node_18_p = d_node_17_p;
    d_node_18_p -= vec3(-2.800, -0.900, 0.000);
    d_node_18_p *= rotateZ(-0.5061);
    float d_node_18_scale = max(0.450, 0.0001);
    d_node_18_p /= d_node_18_scale;
    float d_node_18 = sdVesicaSegment(d_node_18_p, vec3(-1.500, 0.000, 0.000), vec3(0.500, 0.000, 0.000), 0.170);
    d_node_18 *= d_node_18_scale;
    float d_node_17 = opSmoothUnion(d_node_16, d_node_18, 0.550);
    d_node_17 *= d_node_17_scale;
    float d_node_15 = opSmoothUnion(d_node_12, d_node_17, 0.560);
    d_node_15 *= d_node_15_scale;
    float d_node_13 = min(d_node_14, d_node_15);
    d_node_13 *= d_node_13_scale;
    float d_node_21 = d_node_13;
    d_node_21 *= d_node_21_scale;
    float d_node_22 = d_node_21;
    d_node_22 *= d_node_22_scale;
    float d_node_23 = d_node_22;
    d_node_23 *= d_node_23_scale;

    float bodyAndLegs = min(d_node_26, d_node_23);

    // HEAD SECTION - positioned in front of body
    vec3 headPos = d_node_29_p;
    headPos -= vec3(0.0, 0.4, 0.8);
    float head = sdEllipsoid(headPos, vec3(0.35, 0.3, 0.4));

    // EYES - two large spider eyes
    vec3 eyeL = d_node_29_p;
    eyeL -= vec3(-0.18, 0.45, 1.0);
    float eyeLeftOuter = sdSphere(eyeL, 0.15);

    vec3 eyeR = d_node_29_p;
    eyeR -= vec3(0.18, 0.45, 1.0);
    float eyeRightOuter = sdSphere(eyeR, 0.15);

    // Eye pupils (for material ID)
    vec3 pupilL = d_node_29_p;
    pupilL -= vec3(-0.18, 0.45, 1.12);
    float pupilLeft = sdSphere(pupilL, 0.08);

    vec3 pupilR = d_node_29_p;
    pupilR -= vec3(0.18, 0.45, 1.12);
    float pupilRight = sdSphere(pupilR, 0.08);

    // Smaller side eyes
    vec3 sideEyeL = d_node_29_p;
    sideEyeL -= vec3(-0.32, 0.35, 0.95);
    float sideEyeLeft = sdSphere(sideEyeL, 0.08);

    vec3 sideEyeR = d_node_29_p;
    sideEyeR -= vec3(0.32, 0.35, 0.95);
    float sideEyeRight = sdSphere(sideEyeR, 0.08);

    // Combine eyes with head
    float eyes = min(eyeLeftOuter, eyeRightOuter);
    eyes = min(eyes, min(sideEyeLeft, sideEyeRight));

    // Add fangs
    vec3 fangL = d_node_29_p;
    fangL -= vec3(-0.12, 0.2, 1.05);
    fangL *= rotateX(0.3);
    float fangLeft = sdCylinder(fangL, 0.03, 0.15);

    vec3 fangR = d_node_29_p;
    fangR -= vec3(0.12, 0.2, 1.05);
    fangR *= rotateX(0.3);
    float fangRight = sdCylinder(fangR, 0.03, 0.15);

    float fangs = min(fangLeft, fangRight);

    // Combine head features
    float headComplete = opSmoothUnion(head, eyes, 0.05);
    headComplete = min(headComplete, fangs);

    // Combine everything
    float spider = opSmoothUnion(bodyAndLegs, headComplete, 0.3);
    spider *= d_node_29_scale;

    // Track material - check if we hit eyes
    float pupils = min(pupilLeft, pupilRight);
    pupils *= d_node_29_scale;

    if (pupils < spider) {
        gMaterialID = 2.0; // Pupils
        return pupils;
    } else if (eyes * d_node_29_scale < spider) {
        gMaterialID = 1.0; // Eyes
        return eyes * d_node_29_scale;
    } else {
        gMaterialID = 0.0; // Body
        return spider;
    }
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        sceneSDF(p + e.xyy) - sceneSDF(p - e.xyy),
        sceneSDF(p + e.yxy) - sceneSDF(p - e.yxy),
        sceneSDF(p + e.yyx) - sceneSDF(p - e.yyx)
    ));
}

// Calculate ambient occlusion
float calcAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for(int i = 0; i < 5; i++) {
        float h = 0.01 + 0.12 * float(i) / 4.0;
        float d = sceneSDF(p + h * n);
        occ += (h - d) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera with slight movement
    float camAngle = sin(iTime * 0.2) * 0.3;
    vec3 ro = vec3(sin(camAngle) * 3.0, 0.3, cos(camAngle) * 3.0);
    vec3 target = vec3(0.0, 0.0, 0.0);
    vec3 forward = normalize(target - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    float t = 0.0;
    vec3 col = vec3(0.15, 0.2, 0.3); // Dark blue background

    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        if (d < 0.001) {
            vec3 n = calcNormal(p);

            // Lighting setup
            vec3 lightDir1 = normalize(vec3(1.0, 1.0, 1.0));
            vec3 lightDir2 = normalize(vec3(-0.5, 0.5, 0.5));

            float diff1 = max(dot(n, lightDir1), 0.0);
            float diff2 = max(dot(n, lightDir2), 0.0) * 0.5;

            // Specular
            vec3 viewDir = normalize(ro - p);
            vec3 reflectDir = reflect(-lightDir1, n);
            float spec = pow(max(dot(viewDir, reflectDir), 0.0), 32.0);

            // Ambient occlusion
            float ao = calcAO(p, n);

            // Material colors based on ID
            vec3 baseColor;
            if (gMaterialID > 1.5) {
                // Pupils - black
                baseColor = vec3(0.05, 0.05, 0.1);
                spec *= 2.0;
            } else if (gMaterialID > 0.5) {
                // Eyes - glossy white/yellow
                baseColor = vec3(0.95, 0.92, 0.7);
                spec *= 3.0;
            } else {
                // Body - dark brown/black with orange markings
                float pattern = sin(p.x * 10.0) * sin(p.y * 10.0) * sin(p.z * 10.0);
                vec3 color1 = vec3(0.15, 0.1, 0.08); // Dark brown
                vec3 color2 = vec3(0.6, 0.3, 0.1); // Orange
                baseColor = mix(color1, color2, smoothstep(-0.3, 0.3, pattern) * 0.3);
            }

            // Combine lighting
            float amb = 0.2;
            col = baseColor * (diff1 + diff2 + amb) * ao;
            col += vec3(1.0, 0.9, 0.8) * spec * 0.5;

            // Rim lighting for depth
            float rim = 1.0 - max(dot(viewDir, n), 0.0);
            rim = pow(rim, 3.0);
            col += vec3(0.3, 0.2, 0.4) * rim * 0.5;

            break;
        }

        if (t > 20.0) break;
        t += d;
    }

    // Vignette
    float vignette = 1.0 - length(uv) * 0.3;
    col *= vignette;

    // Tone mapping and gamma correction
    col = col / (col + vec3(1.0));
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

void mainB1(out vec4 color, in vec2 coord) {
    vec4 colA = texture(iChannel0, coord/iResolution.xy);
    vec4 colB = texelFetch(iChannel1, ivec2(coord), 0);
    if(iMouse.z>0.){
        colA = thresh(colA);
    }
    vec3 colx = colA.rgb+colB.rgb;
    if(colx.r>=1.){color.r = 1.0;} else {color.r = 0.;}
    if(colx.g>=1.){color.g = 1.0;} else {color.g = 0.;}
    if(colx.b>=1.){color.b = 1.0;} else {color.b = 0.;}
}

void mainB2(out vec4 color, in vec2 coord) {
    bool xodd = true; if (mod(coord.x, 2.) < 1.){xodd = false;}
    bool yodd = true; if (mod(coord.y, 2.) < 1.){yodd = false;}
    vec2 coords[4];
    int current = 0;
    coords[0] = coord;
    coords[1] = coord;
    coords[2] = coord;
    coords[3] = coord;
    if(yodd&&xodd){
        coords[1].x += 1.;
        coords[2].x += 1.;
        coords[2].y += 1.;
        coords[3].y += 1.;
    } else if(yodd){
        current = 1;
        coords[0].x -= 1.;
        coords[2].y += 1.;
        coords[3].y += 1.;
        coords[3].x -= 1.;
    } else if(xodd){
        current = 3;
        coords[0].y -= 1.;
        coords[1].y -= 1.;
        coords[1].x += 1.;
        coords[2].x += 1.;
    } else {
        current = 2;
        coords[0].y -= 1.;
        coords[0].x -= 1.;
        coords[1].y -= 1.;
        coords[3].x -= 1.;
    }
    vec4 cols[4];
    cols[0] = texture(iChannel0, coords[0]/iResolution.xy);
    cols[1] = texture(iChannel0, coords[1]/iResolution.xy);
    cols[2] = texture(iChannel0, coords[2]/iResolution.xy);
    cols[3] = texture(iChannel0, coords[3]/iResolution.xy);

    if(iMouse.z>0.){
        cols[0] = thresh(cols[0]);
        cols[1] = thresh(cols[1]);
        cols[2] = thresh(cols[2]);
        cols[3] = thresh(cols[3]);
    }

    vec4 colB = texelFetch(iChannel1, ivec2(coords[current]), 0);

    vec4 outputs[4];

    outputs[0] = getunits(cols[0]+colB);
    outputs[1] = getunits(cols[1]+getrem(cols[0]+colB));
    outputs[2] = getunits(cols[2]+getrem(cols[1]+getrem(cols[0]+colB)));
    outputs[3] = getunits(cols[3]+getrem(cols[2]+getrem(cols[1]+getrem(cols[0]+colB))));

    color = outputs[current];
    color.a = 1.;

}

void mainImage(out vec4 color, in vec2 coord) {
    if(coord.x<iResolution.x/2.){
        mainB1(color, coord);
    }else{
        mainB2(color, coord);
    }
}

