vec4 thresh(in vec4 col){
        float t = 0.07;
        float tu = 0.92;
        if(col.r<t&&col.g<t&&col.b<t){col=vec4(0.,0.,0.,1.);}
        if(col.r>tu&&col.g>tu&&col.b>tu){col=vec4(1.,1.,1.,1.);}
        return col;
}

vec4 getunits(in vec4 col){
    vec4 ocol = vec4(1.);
    if(col.r>=1.){ocol.r = 1.0;} else {ocol.r = 0.;}
    if(col.g>=1.){ocol.g = 1.0;} else {ocol.g = 0.;}
    if(col.b>=1.){ocol.b = 1.0;} else {ocol.b = 0.;}
    return ocol;
}

vec4 getrem(in vec4 col){
    vec4 ocol = vec4(1.);
    ocol.r = mod(col.r, 1.);
    ocol.b = mod(col.b, 1.);
    ocol.g = mod(col.g, 1.);
    return ocol;
}

void mainB1(out vec4 color, in vec2 coord) {
    vec4 colA = texture(source, coord/iResolution.xy);
    vec4 colB = texelFetch(iChannel2, ivec2(coord), 0);
    colA = thresh(colA);
    vec3 colx = colA.rgb+colB.rgb;
    if(colx.r>=1.){color.r = 1.0;} else {color.r = 0.;}
    if(colx.g>=1.){color.g = 1.0;} else {color.g = 0.;}
