}

void mainB2(out vec4 color, in vec2 coord) {
    bool xodd = true; if (mod(coord.x, 2.) < 1.){xodd = false;}
    bool yodd = true; if (mod(coord.y, 2.) < 1.){yodd = false;}
    vec2 coords[4];
    int current = 0;
    coords[0] = coord;
    coords[1] = coord;
    coords[2] = coord;
    coords[3] = coord;
    if(yodd&&xodd){
        coords[1].x += 1.;
        coords[2].x += 1.;
        coords[2].y += 1.;
        coords[3].y += 1.;
    } else if(yodd){
        current = 1;
        coords[0].x -= 1.;
        coords[2].y += 1.;
        coords[3].y += 1.;
        coords[3].x -= 1.;
    } else if(xodd){
        current = 3;
        coords[0].y -= 1.;
        coords[1].y -= 1.;
        coords[1].x += 1.;
        coords[2].x += 1.;
    } else {
        current = 2;
        coords[0].y -= 1.;
        coords[0].x -= 1.;
        coords[1].y -= 1.;
        coords[3].x -= 1.;
    }
    vec4 cols[4];
    cols[0] = texture(source, coords[0]/iResolution.xy);
    cols[1] = texture(source, coords[1]/iResolution.xy);
    cols[2] = texture(source, coords[2]/iResolution.xy);
    cols[3] = texture(source, coords[3]/iResolution.xy);
        cols[0] = thresh(cols[0]);
        cols[1] = thresh(cols[1]);
        cols[2] = thresh(cols[2]);
        cols[3] = thresh(cols[3]);

    vec4 colB = texelFetch(iChannel1, ivec2(coords[current]), 0);

    vec4 outputs[4];

    outputs[0] = getunits(cols[0]+colB);
    outputs[1] = getunits(cols[1]+getrem(cols[0]+colB));
    outputs[2] = getunits(cols[2]+getrem(cols[1]+getrem(cols[0]+colB)));
    outputs[3] = getunits(cols[3]+getrem(cols[2]+getrem(cols[1]+getrem(cols[0]+colB))));

    color = outputs[current];

}

