// Fork of "The Monolith at Sunset" by keaganladds. https://shadertoy.com/view/3X2Szt
// And     "Sunset [283]"           by Xor.          https://shadertoy.com/view/wXjSRt
// 2025-05-05 16:01:36

// as suggested by diatribes
vec4 altNoise(out vec4 O, vec2 I) {
    float t = iTime*.15,i,z,d,s;
    for(O*=i; i++<1e2; ) {
        vec3 p = z * normalize(vec3(I+I,0) - iResolution.xyy );
        for (d = .2; d < 2.;
            p += abs(dot(sin(t+p * d * 32.), vec3(.008))) / d,
            d += d);
        z += d = .005 + max(s=.3-abs(p.y), -s)*.2;
        O += cos(d) / d;
    }
    O *= 0.1 + (I.x/iResolution.x/1.4);
    return O / 1e4;
}

// "Sunset" by @XorDev Based on my tweet shader: https://x.com/XorDev/status/1918764164153049480
vec4 sunset283(vec4 O, vec2 I)
{
    //Time for animation
    //Raymarch iterator
    float t = iTime/5.;
    float aTime = 5.;
    if(t<aTime){

        float i,//Raymarch depth
        z,//Step distance
        d,//Signed distance
        s;//Clear fragcolor and raymarch with 100 iterations
        for(O*=i; i++<1e2; )
        {
            //Compute raymarch sample point
            vec3 p = z * normalize( vec3(I+I,0) - iResolution.xyy );
            //Turbulence loop
            //https://www.shadertoy.com/view/3XXSWS
            for(d=5.; d<2e2; d+=d)
                p += .6*sin(p.yzx*d - .2*t) / d;    
            //Compute distance (smaller steps in clouds when s is negative)
            z += d = .005 + max(s=.3-abs(p.y), -s*.2)/4.;
            //Coloring with sine wave using cloud depth and x-coordinate
            O += (cos(s/.07+p.x+.5*-((min(t,20.)-20.)/2.)-vec4(0,1,2,3)-3.) + 1.5) * exp(s/.1) / d;
            O/=1.+min(t,20.)/1000.;
        }
        //Tanh tonemapping: https://www.shadertoy.com/view/ms3BD7
        O = tanh(O*O / 4e8);

        return mix(O, altNoise(O,I), t/aTime);
    } else {
        return altNoise(O,I);
    }

}



// "The Monolith at Sunset"
#define MAX_DIST 100.0
#define MIN_DIST 0.001

float hash(vec3 p)
{
    float n = p.x + p.y*157.0 + 113.0*p.z;
    return fract(sin(n)*753.5453123);
}


struct SdfObject {
	float d;
	int index;
};

float sdSphere(vec3 p, float r) {
	return length(p) - r;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

SdfObject opU(SdfObject a, SdfObject b) {
    if (a.d < b.d) return a;

    return b;

}

mat2 rot2D(float angle) {
	float s = sin(angle);
	float c = cos(angle);

	return mat2(c, -s, s, c);
}

vec4 noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 w = fract(x);

    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-1.0)+2.0);

    float a = hash( p+vec3(0,0,0) );
    float b = hash( p+vec3(1,0,0) );
    float c = hash( p+vec3(0,1,0) );
    float d = hash( p+vec3(1,1,0) );
    float e = hash( p+vec3(0,0,1) );
    float f = hash( p+vec3(1,0,1) );
    float g = hash( p+vec3(0,1,1) );
    float h = hash( p+vec3(1,1,1) );

    float k0 =   a;
    float k1 =   b - a;
    float k2 =   c - a;
    float k3 =   e - a;
    float k4 =   a - b - c + d;
    float k5 =   a - c - e + g;
    float k6 =   a - b - e + f;
    float k7 = - a + b + c - d + e - f - g + h;

    return vec4( -1.0+2.0*(k0 + k1*u.x + k2*u.y + k3*u.z + k4*u.x*u.y + k5*u.y*u.z + k6*u.z*u.x + k7*u.x*u.y*u.z),
                 2.0*du *vec3( k1 + k4*u.y + k6*u.z + k7*u.y*u.z,
                               k2 + k5*u.z + k4*u.x + k7*u.z*u.x,
                               k3 + k6*u.x + k5*u.y + k7*u.x*u.y ) );
}

float fbm( in vec3 x, in float H, int numOctaves )
{    
    float G = exp2(-H);
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    for( int i=0; i<numOctaves; i++ )
    {
        t += a*noise(f*x).x;
        f *= 2.0;
        a *= G;
    }
    return t;

    return t;
}


SdfObject Terrain(vec3 p) {
	float d = fbm(vec3(p.x, 0., p.z), 1., 9)/4.;
	d = (p.y) + d;

    return SdfObject(d , 3);
}

SdfObject map(vec3 p) {

	vec3 p1 = p;
	p1.xz *= rot2D(radians(27.));

	SdfObject sphere1 = SdfObject(sdBox(p1 + vec3(0,-1,0), vec3(0.5, 1.5, 0.5)), 1) ;
	SdfObject ground = SdfObject(Terrain(p).d, 3);
	SdfObject water = SdfObject(p.y + 0.25, 4);


	return  opU(opU(ground, sphere1), water);
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float w )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<80 && t<maxt; i++ )
    {
        float h = map(ro + t*rd).d;
        res = min( res, h/(w*t) );
        t += clamp(h, 0.005, 0.50);
        if( res<-1.0 || t>maxt ) break;
    }
    res = max(res,-1.0);
    return 0.25*(1.0+res)*(1.0+res)*(2.0-res);
}

SdfObject raymarch(vec3 ro, vec3 rd) 
{
	float t = 0.;
	int objectId = 0;
	for(int i = 0; i < 80; i++) {
		vec3 p = ro + rd * t;
		SdfObject m = map(p);
		float d = m.d;

		t += d;

		objectId = m.index;

		if (d < MIN_DIST) break;
		if (t > MAX_DIST) break;
	}


	return SdfObject(t, objectId);
}

vec3 getNormal(vec3 p) {
  vec3 e = vec3(.001, 0, 0);

  vec3 n = map(p).d - vec3(
    map(p-e.xyy).d,
    map(p-e.yxy).d,
    map(p-e.yyx).d);

  return normalize(n);
}

float phaseMie(float cosTheta, float g)
{
    float g2 = g * g;
    float denom = 1.0 + g2 - 2.0 * g * cosTheta;
    return (1.0 - g2) / (4.0 * 3.14159 * pow(denom, 1.5));
}

vec3 getSunColor(vec3 sunDir) {
	float sunFade = clamp(1.0 - sunDir.y, 0.0, 1.0); // More red when sun is low
	return mix(vec3(1.0, 1.0, 1.0), vec3(1, 0.5, 0.2), pow(sunFade, 6.0));
}

vec3 getSkyColor(vec3 viewDir, vec3 sunDir, vec4 O, vec2 I, bool clouds)
{
    // Normalize inputs
    viewDir = normalize(viewDir);
    sunDir = normalize(sunDir);

    vec3 sunColor = getSunColor(sunDir)*1.2;

    // Rayleigh phase function approximation
    float mu = dot(viewDir, sunDir); // Cosine of angle between view and sun
    float rayleigh = 1.0 + mu * mu;

    // Mie phase function
    float g = 0.85; // Anisotropy factor, higher = tighter glow
    float miePhase = phaseMie(mu, g);

    // Rayleigh scattering coefficient (RGB) — blue scatters more
    vec3 betaRayleigh = vec3(5.8e-6, 13.5e-6, 33.1e-6); // Arbitrary scale for visual use
	vec3 betaMie = 0.25f * sunColor; // Almost white, tweak for sunset

    // Sun intensity (tweak as needed)
    float sunIntensity = 35000.;

    // Air density / depth factor — fade to space
    float viewHeight = clamp(viewDir.y, 0., 1.); // Look up = 1, down = 0
    float extinction = exp(-viewHeight * 2.);     // More fog/haze near horizon

 	vec3 color = (rayleigh * betaRayleigh);
    // Approximate color
    vec3 sky = (color *sunIntensity );
    sky *= extinction; // Fade toward horizon
    sky = clamp(sky, 0.0, 1.0);

    sky += (betaMie * miePhase );
    if(clouds){
        sky = max(sky,sunset283(O, I).rgb*1.5);
    }

    return sky * sunColor;
}



vec3 GetSunDirection(float elevation, float rotation)
{
    float y = sin(elevation);
    float radius = cos(elevation); // radius of the horizontal circle

    float x = sin(rotation) * radius;
    float z = cos(rotation) * radius;

    return normalize(vec3(x, y, z));
}

float calcAO(vec3 p, vec3 n)
{
    float ao = 0.0;
    float sca = 1.0;

    const int steps = 5;
    const float stepSize = 0.05; 

    for (int i = 1; i <= steps; i++)
    {
        float dist = float(i) * stepSize;
        vec3 samplePos = p + n * dist;

        float d = map(samplePos).d; 
        ao += (dist - d) * sca;

        sca *= 0.7; 
    }

    return clamp(1.0 - ao, 0., 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0f - iResolution.xy)/iResolution.x;


    vec3 col = vec3(0,0,0);

    float aspect = 1.0;
    // Ray origin / camera position


    float fov = radians(90.); // Horizontal FOV in radians
	vec3 ro = vec3(0, 1.1, -5); // Camera position


	// Calculate scale based on FOV
	float z = 1.0 / tan(fov * 0.5); // Vertical focal length
	vec3 rd = normalize(vec3(uv.x, uv.y, z * aspect));



    SdfObject rm = raymarch(ro, rd);

    float d  = rm.d;
    int objectId = rm.index;

    float sun = 5.0 * exp(iTime * -0.05f);

    vec3 viewDir = normalize(rd);
	vec3 sunDir = GetSunDirection(radians(sun), radians(40.0-sun));
    vec3 skyColor = getSkyColor(viewDir, sunDir, fragColor, fragCoord, false);
	vec3 lightPos = 1000000.0*sunDir;

	if (d < MAX_DIST) {
		vec3 baseColor = vec3(1,1,1);
		vec3 sunColor = getSunColor(sunDir);
		vec3 p = ro + rd * d;
    	vec3 normal = getNormal(p);
    	vec3 lightDir = normalize(lightPos - p);
    	float ao = calcAO(p, normal);



		if (objectId ==1) {
		 baseColor = 0.1*vec3(1,1,1);

		 vec3 reflection = rd - 2.0*dot(normal, rd)/length(normal) * (normal);
		 vec3 reflectedColor = getSkyColor(reflection, lightDir, fragColor, fragCoord, false);

		 baseColor += 0.75*reflectedColor;


		} else if (objectId == 2) {
			baseColor = vec3(1,1,1);
		} else if (objectId == 3) {
			baseColor = 0.1*vec3(0.45,.30,0.15);
			float n1 = fbm(p*3.14888555, 0.1, 1);
			float n2 = fbm(p*2.2, 0.1, 1);

			baseColor = mix(baseColor, vec3(1.,1.,1.), smoothstep(0.01,0.5, p.y));
			baseColor = mix(baseColor, baseColor*0.75, n1);

			vec3 treecol = 0.05*mix(vec3(0.569, 0.902, 0.231), vec3(0.478, 0.769, 0.047), n2);
			baseColor = mix(baseColor, treecol, smoothstep(0.1, -0.2, p.y));

			// Slope Angle
			float a = 1.-dot(normal, vec3(0.,1.,0.));


			float factorA = smoothstep(0.3,0.6, a);
			float factorB = smoothstep(-0.2,0.2, p.y);

			baseColor =mix(baseColor, 0.75*vec3(1,1,1), min(factorA, factorB));
		} else if (objectId == 4) {

			float freq = 250.0;
			vec3 normalOffset =(0.15 * (vec3(fbm(freq*p.xyz, 1.0,2), fbm(freq*p.yxz, 0.5,2), fbm(freq*p.yxz, 0.5,2)) - 0.5));


            vec3 waterNormals = normalize(normal + normalOffset);
		 	vec3 reflection = rd - 2.0*dot(waterNormals, rd)/length(waterNormals) * (waterNormals);
			vec3 reflectedColor = getSkyColor(reflection, lightDir, fragColor, fragCoord, false);
			baseColor = 0.01*vec3(0,0,1);
			baseColor += 0.2*reflectedColor;
		}

		float diffuse = 5.0*max(dot(normal, lightDir), 0.0) ;

    	float shadow = softshadow(p, lightDir, 0.1, 100.0, 0.1);

    	float ambient = 0.1 * clamp(0.5+0.5*-normal.x,0.0,1.0);
		vec3 litColor = baseColor * (ambient + diffuse * sunColor * shadow );

		col = litColor * ao;

		float fogDistance = d; // Distance along the ray (from raymarching)
		float fogDensity = 0.05; // Controls how thick the fog is

		// Compute fog factor
		float fogFactor = exp(-fogDensity * fogDistance);
		fogFactor = clamp(fogFactor, 0.0, 1.0);

		// Blend with fog color
		col = mix(skyColor, col, fogFactor);
		//col = ao;
	} else {

		col = getSkyColor(viewDir, sunDir, fragColor, fragCoord, true);
	}



	col = pow( col, vec3(1.2/2.0) );

    fragColor = vec4(col, 1.0f);

}

