vec2 accel(vec2 pi, vec2 pj) {
    vec2 r = pj - pi;
    float k = dot(r, r);
    float invDist3 = inversesqrt(k * k * k);
    return G * r * invDist3;
}

vec2[3] acceleration(vec2[3] positions) {
    vec2 a[3];
    a[0] =
        accel(positions[0], positions[1]) + accel(positions[0], positions[2]);
    a[1] =
        accel(positions[1], positions[0]) + accel(positions[1], positions[2]);
    a[2] =
        accel(positions[2], positions[0]) + accel(positions[2], positions[1]);
    return a;
}


void RK4(inout vec2[3] p, inout vec2[3] v) {
    const float dt = 0.01;
    vec2[3] k1v = acceleration(p);
    vec2[3] k1p = v;

    vec2[3] p2, v2;
    for (int i = 0; i < 3; i++) {
        p2[i] = p[i] + 0.5 * dt * k1p[i];
        v2[i] = v[i] + 0.5 * dt * k1v[i];
    }
    vec2[3] k2v = acceleration(p2), k2p = v2;

    vec2[3] p3, v3;
    for (int i = 0; i < 3; i++) {
        p3[i] = p[i] + 0.5 * dt * k2p[i];
        v3[i] = v[i] + 0.5 * dt * k2v[i];
    }
    vec2[3] k3v = acceleration(p3), k3p = v3;

    vec2[3] p4, v4;
    for (int i = 0; i < 3; i++) {
        p4[i] = p[i] + dt * k3p[i];
        v4[i] = v[i] + dt * k3v[i];
    }
    vec2[3] k4v = acceleration(p4), k4p = v4;

    for (int i = 0; i < 3; i++) {
        p[i] += dt * (k1p[i] + 2. * k2p[i] + 2. * k3p[i] + k4p[i]) / 6.0;
        v[i] += dt * (k1v[i] + 2. * k2v[i] + 2. * k3v[i] + k4v[i]) / 6.0;
    }
}


// suggested by @rreusser and code taken from his notebook
// https://observablehq.com/@rreusser/magnetic-pendulum#drawField
void RK45(inout vec2[3] p, inout vec2[3] v, inout float dt) {
    const float safety = 0.95;
    const float maxDecrease = 0.2;
    const float maxIncrease = 5.0;
    const float tol2 = 1e-8;

    vec2[3] k1p = v;
    vec2[3] k1v = acceleration(p);

    vec2[3] p2, v2;
    for (int i = 0; i < 3; i++) {
        p2[i] = p[i] + dt * 0.2 * k1p[i];
        v2[i] = v[i] + dt * 0.2 * k1v[i];
    }
    vec2[3] k2p = v2, k2v = acceleration(p2);

    vec2[3] p3, v3;
    for (int i = 0; i < 3; i++) {
        p3[i] = p[i] + dt * (0.075 * k1p[i] + 0.225 * k2p[i]);
        v3[i] = v[i] + dt * (0.075 * k1v[i] + 0.225 * k2v[i]);
    }
    vec2[3] k3p = v3, k3v = acceleration(p3);

    vec2[3] p4, v4;
    for (int i = 0; i < 3; i++) {
        p4[i] = p[i] + dt * (0.3 * k1p[i] - 0.9 * k2p[i] + 1.2 * k3p[i]);
        v4[i] = v[i] + dt * (0.3 * k1v[i] - 0.9 * k2v[i] + 1.2 * k3v[i]);
    }
    vec2[3] k4p = v4, k4v = acceleration(p4);

    vec2[3] p5, v5;
    for (int i = 0; i < 3; i++) {
        p5[i] = p[i] + dt * (-0.2037037 * k1p[i] + 2.5 * k2p[i] - 2.5925926 * k3p[i] + 1.2962963 * k4p[i]);
        v5[i] = v[i] + dt * (-0.2037037 * k1v[i] + 2.5 * k2v[i] - 2.5925926 * k3v[i] + 1.2962963 * k4v[i]);
    }
    vec2[3] k5p = v5, k5v = acceleration(p5);

    vec2[3] p6, v6;
    for (int i = 0; i < 3; i++) {
        p6[i] = p[i] + dt * (0.0294958 * k1p[i] + 0.3417969 * k2p[i] + 0.0415943 * k3p[i] + 0.4003454 * k4p[i] + 0.0617676 * k5p[i]);
        v6[i] = v[i] + dt * (0.0294958 * k1v[i] + 0.3417969 * k2v[i] + 0.0415943 * k3v[i] + 0.4003454 * k4v[i] + 0.0617676 * k5v[i]);
    }
    vec2[3] k6p = v6, k6v = acceleration(p6);
    float err2 = 0.0;
    for (int i = 0; i < 3; i++) {
        vec2 err = dt * (
            0.00429377 * k1p[i]
            - 0.0186686 * k3p[i]
            + 0.0341550 * k4p[i]
            + 0.0193220 * k5p[i]
            - 0.0391022 * k6p[i]
        );
        err2 += dot(err, err);
    }

    bool accept = err2 <= tol2;

    if (accept) {
        for (int i = 0; i < 3; i++) {
            p[i] += dt * (0.0978836 * k1p[i] + 0.4025765 * k3p[i] + 0.2104377 * k4p[i] + 0.2891022 * k6p[i]);
            v[i] += dt * (0.0978836 * k1v[i] + 0.4025765 * k3v[i] + 0.2104377 * k4v[i] + 0.2891022 * k6v[i]);
        }
    }
    float scale = clamp(safety * pow(tol2 / (err2 + 1e-12), accept ? 0.125 : 0.1), maxDecrease, maxIncrease);
    dt *= scale;
}


void adaptiveRK45(inout vec2[3] p, inout vec2[3] v) {
    float t = 0.0;
    float totalDt = 0.01;
    float dt = totalDt / float(MaxIterEachStep);

    for (int i = 0; i < MaxIterEachStep; i++) {
        if (t + dt > totalDt) {
            dt = totalDt - t;
        }

        vec2[3] pTemp = p;
        vec2[3] vTemp = v;
        float dtTemp = dt;

        RK45(pTemp, vTemp, dtTemp);

        if (dtTemp >= dt * 0.99) {
            p = pTemp;
            v = vTemp;
            t += dt;
        }

        dt = dtTemp;

        if (t >= totalDt - 1e-6) break;
    }
}


