void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 uv = ivec2(fragCoord);
    int objIdx = uv.y / 3;
    int localIdx = uv.y % 3;

    vec2[3] p, v;

    if (uv.x == 0 && uv.y < 18) {
        if (iFrame == 0) {
            initState(objIdx, p, v);
        } else {
            for (int i = 0; i < 3; i++) {
                vec4 data = texelFetch(iChannel0, ivec2(0, objIdx * 3 + i), 0);
                p[i] = data.xy;
                v[i] = data.zw;
            }
            #ifdef USE_ADAPTIVE_RK45
                adaptiveRK45(p, v);
            #else
                RK4(p,v);
            #endif
        }
        fragColor = vec4(p[localIdx], v[localIdx]);
    } else if (uv.x > 0 && uv.x < NTRAIL && uv.y < 18) {
        vec4 prev = texelFetch(iChannel0, ivec2(uv.x - 1, uv.y), 0);
        fragColor = vec4(prev.xy, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0);
    }
}

/**
Periodic solutions of the three body problem (equal mass)

Use adaptive RK45 with a fixed number of max iterations

*/

#define get(n, i) texelFetch(iChannel0, ivec2(i, n), 0).xy

const vec3 col[3] = vec3[3](
    vec3(0.1, 0.9, 0.1),
    vec3(0.9, 0.05, 0.05),
    vec3(0.02, 0.02, 0.8));
const vec3 weights = vec3(0.299, 0.587, 0.114);

const float lum[3] = float[3](dot(col[0], weights),
                              dot(col[1], weights),
                              dot(col[2], weights));

const float scales[6] = float[6](2.1, 2.8, 3.4, 2.0, 1.7, 1.9);

const float fade = 0.025;
const float trail_strength = 0.008;
const float particle_strength = 0.04;

vec3 glowPoint(vec2 p, vec2 center, vec3 col, float lum, float strength) {
    float d = max(abs(length(p - center)), 1e-5);
    d = pow(strength / d, 4.);
    d /= lum*30.;
    return 1.0 - exp(-d * col);
}

vec2 sdSegment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float d = length(pa - ba * h);
    d = max(abs(d), 1e-5);
    return vec2(d, h);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    ivec2 grid = ivec2(3, 2);
    ivec2 cell = ivec2(uv * vec2(grid));
    int simIndex = cell.y * grid.x + cell.x;

    vec2 cellCenter = (vec2(cell) + 0.5) / vec2(grid);
    vec2 shift = vec2(0);

    vec2 localUV = (uv - cellCenter) * vec2(grid);
    float aspect =
        (iResolution.x / float(grid.x)) / (iResolution.y / float(grid.y));
    localUV.x *= aspect;

    float zoom = scales[simIndex];
    localUV *= zoom;

    if (simIndex == 4)
        shift = vec2(-0.15, 0.1);
    else if (simIndex == 5)
        shift = vec2(0.1, -0.1);
    localUV -= shift;

    vec3 color = vec3(0.0);


    for (int j = 0; j < 3; j++) {
        int idx = simIndex * 3 + j;
        vec2 end = get(idx, 0);
        color += glowPoint(localUV, end, col[j], lum[j], particle_strength);
    }

    fragColor = texture(iChannel1, uv)*0.998;

    fragColor +=
        vec4(pow(clamp(color, 0.0, 1.0), vec3(0.4545)), 1.0)*0.04;

}

