    }  else if(dist > (radius-pixel)) {
        Cmost *=80.0;
        f = smoothstep(0.4 + (dist-radius)*5.0, 0.0, Cmost);
        gcol = mix(gcol, gcol+(lcolC*lcolCinc*1.2), f);
    }

}

