vec3 colourGround = vec3(0.820,0.820,0.820);
vec3 colourPaint  = vec3(0.420,0.420,0.420);
vec3 colourTeamA  = vec3(0.839,0.000,0.000);
vec3 colourTeamB  = vec3(0.212,0.800,0.000);
vec3 colourLight = vec3(1.000,1.000,1.000);
vec3 colourAmbient = vec3(0.188,0.188,0.188);
vec2 centre;
float radius;
float pixel;

vec2 tanLineDist( in vec2 C, in float r, in vec2 P, in vec2 F){
    C.y = -C.y;
    vec2 d = P-C;
    vec2 dr = vec2(-d.y, d.x);
    float dl = length(d);
    //if(dl>=r){
        float rho = r/dl;
        float ad = rho*rho;
        float bd = rho*sqrt(1.0-ad);//*(1.0-rho)); // (1-rho)*(1-rho) ?
        vec2 T1 = vec2(C.x + ad*d.x + bd*dr.x, C.y + ad*d.y + bd*dr.y);
        vec2 T2 = vec2(C.x + ad*d.x - bd*dr.x, C.y + ad*d.y - bd*dr.y);

    //}
    float ax = T1.x-P.x;
    float by = P.y-T1.y;
    float c = (T1.y*P.x)-(T1.x*P.y);

    float ax2 = T2.x-P.x;
    float by2 = P.y-T2.y;
    float c2 = (T2.y*P.x)-(T2.x*P.y);

    return  vec2((F.x*-ax)- (F.y*-by)-c, (F.x*-ax2)- (F.y*-by2)-c2);
}

vec3 getGroundTexture( in vec2 p, in float ar , in float pmult)
{
    float gpixel = pixel*pmult*1.2;
    float lineWidth = 1.0/180.0;
    float f ;
    vec2 pn = vec2((p.x-(0.5*ar)), p.y-0.5);
    vec3 gcol = colourGround;

    float lpn = length(pn);
    float bdist = sdBox(pn, vec2(ar/2.0, 0.5));
    if(bdist<=0.0-lineWidth){
        if(bdist >= -lineWidth*2.0){
            gcol = colourPaint;
        }

        if(lpn>(0.2-gpixel)-lineWidth){
            if(lpn<0.2){
                f = smoothstep((0.2-gpixel), .2 , lpn);
                gcol = mix(colourPaint,colourGround,  f);
                f = smoothstep(0.2-lineWidth, (0.2-gpixel)-lineWidth , lpn);
                gcol = mix(gcol,colourGround,  f);
            }
        if(abs(pn.x)<=lineWidth/2.0 && lpn>=0.19){
                gcol = colourPaint;
            }
        }

    }

    gcol = gcol*colourAmbient;
    float TAdist = sdBox(vec2(pn.x+(0.5*ar), pn.y), vec2(0.0, 0.15));
    gcol = gcol*(1.0+(colourTeamA/sqrt(TAdist*50.0)));

    float modRadius = radius;//*(1.0/pow((pn.x+(0.5*ar)), 2.0));

    vec2 isInLight = tanLineDist(centre, modRadius, vec2(ar, -0.65), -p.yx);
    vec2 isInLight2 = tanLineDist(centre, modRadius, vec2(ar, -0.35), -p.yx);

    float TBdist = sdBox(vec2(pn.x-(0.5*ar), pn.y), vec2(0.0, 0.15));
    float inLight= 0.0;

    if(isInLight.y>=0.0 || isInLight2.y<=0.0){
        inLight=1.0;} else {
            inLight= 1.0/(abs(isInLight2.y)/abs(isInLight.y) +1.0);
        }

    float inLight2= 0.0;

    if(isInLight.x>=0.0 || isInLight2.x<=0.0){
        inLight2=1.0;} else {
            inLight2= 1.0/(abs(isInLight.x)/abs(isInLight2.x) +1.0);
        } 

    if(isInLight.y>=0.0 && isInLight2.x<=0.0){ inLight=0.0;}
    if(isInLight.y<=0.0 && isInLight2.x>=0.0){ 
        float inLightCross = (inLight+inLight2);
        inLight=inLight2 = inLightCross;



    }

    inLight = min(inLight, inLight2);
    if(isInLight.y>0.0 && isInLight2.x<=0.0  && (isInLight.x>0.0 || isInLight2.y<0.0)){inLight=1.0;}
    f = smoothstep(0.0, 1.0,inLight);
    gcol = mix(gcol,gcol*( 1.0+(colourTeamB/sqrt(TBdist*50.0))),  f);


    if(p.x<0.0||p.y<0.0||p.x>1.0*ar||p.y>1.0){gcol = vec3(0.0,0.0,0.0);}
    gcol = gcol*(2.0+(colourLight/(sqrt(lpn+0.2)/1.2)));
    //gcol.b *= pn.x*ar;
    return gcol;

}

