float toneMap(float a, float b, float x){
    return 1.-(a*x + b) / (x*x + (a+b) * x + b);
}

vec4 toneMap(float a, float b, vec4 x){
    return 1.-(a*x + b) / (x*x + (a+b) * x + b);
}

