float getApollonianHeight(vec2 uv, vec2 interactionPoint) {
    vec2 z = uv;

    float N = 3.;
    float a = 3.14159/N;
    float D = 1./cos(a);
    float T = tan(a);
    float K = D - T;

    float maxDepth = 20.;
    float depth = 0.;
    vec2 t = vec2(0);
    float s = T*T;

    float outerBound = (D + T) / 3.77;
    if (length(uv) > outerBound) {
        return 0.0;
    }

    if (length(interactionPoint) < outerBound && length(interactionPoint) > 0.01) {
        s = dot(interactionPoint, interactionPoint);
        vec2 invCenter = interactionPoint / s;
        float invScale = 1./s - 1.;
        z = (z - invCenter) / dot(z - invCenter, z - invCenter) * invScale + invCenter;
    }

    s = T*T;

    for (; depth < maxDepth && length(z) > K; depth++) {
        z *= -K / dot(z,z);

        for (float j = 0.; j++ < N;) {
            t = D * sin(6.28*j/N + vec2(0,1.57));
            if (length(z-t) < T) {
                z = (z-t) / dot(z-t,z-t) * s + t;
                j = N;
            }
        }
    }

    float alpha = 0.0;
    if (length(z) < K) alpha = 1.0;

    // Calculate distance to nearest circle boundary
    float minDist = length(z) - K;  // Distance to center circle boundary

    for (float j = 0.; j < N; j++) {
        vec2 center = D * sin(6.28*j/N + vec2(0,1.57));
        float dist = abs(length(z - center) - T);  // Distance to this circle's boundary
        minDist = min(minDist, dist);
        if (length(z - center) < T) alpha = 1.0;
    }

 if (alpha < 0.05 || depth > 19.9 || (ang>0.3&&ang<1.6)) {
        return 0.0;
    }

    // Convert distance to height - closer to boundary = raised edge
    // Use a smooth falloff for the circular ridges
    float height = exp(-minDist * 8.0) * (1.0 + depth * 0.1);

    return height;
}

