                //float heightU = getApollonianHeight((diskUV + vec2(0, epsilon)) * uvScale, interactionPoint);

                // Calculate gradients (slopes) from height differences
                vec2 gradient = vec2(heightR - heightC, heightR - heightC) / epsilon;

                // Base normal (radial from torus center)
                vec3 baseNormal = normalize(vec3(p.x, 0, p.z));

                // Create tangent space basis aligned with disk surface
                vec3 tangent = normalize(cross(baseNormal, vec3(0, 1, 0)));
                vec3 bitangent = normalize(cross(baseNormal, tangent));

