void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float yoffset = 0.5+sin(iTime)/2.;

    // Get the buffer contents
    vec3 col = texture(iChannel0, uv).rgb;
    uv+=0.1;
    // Highlighted slice position (at 0.5)
    float highlightPos = yoffset;

    // Draw highlighted slice with matching isometric transformation
    vec2 centerUV = (uv - vec2(0.5, 0.5)) * 2.5;

    // Isometric projection for highlighted slice
    float isoX = highlightPos * 0.6;  // Move right
    float isoY = highlightPos * 0.9;  // Move up

    // Calculate x position in waveform from screen position
    float waveX = ((centerUV.x - isoX) / 1.2) + 0.5;

    if(waveX >= 0.0 && waveX <= 1.0) {
        float waveValue = wavetable(vec2(waveX, highlightPos), iChannel1, iTime);

        // y position: wave amplitude + isometric offset
        float posY = waveValue * 0.25 + isoY;

        // Distance from current pixel
        float dist = abs(centerUV.y - posY);
        float lineThickness = 0.012;
        float glowRadius = 0.04;

        if(dist < glowRadius) {
            float intensity = 0.0;

            // Core line
            if(dist < lineThickness) {
                intensity = 1.0;
            } else {
                // Glow
                intensity = 1.0 - (dist - lineThickness) / (glowRadius - lineThickness);
                intensity *= 0.6;
            }

            col += vec3(0.7, 1.0, 0.7) * intensity;
        }
    }

    fragColor = vec4(col, 1.0);
}

// Wavetable functions
float wavetabletest(vec2 uv) {
    float x = uv.x;  // Phase (0-1)
    float y = uv.y;  // Table position (0-1)

    // Ensure continuity at boundaries
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.5) {
        // Interpolate between sine and square
        float t = y * 2.0;  // 0 to 1 for first half

        // Sine wave
        float sine = sin(phase);

        // Square wave (bandlimited with a few harmonics)
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        return mix(sine, square, t);
    } else {
        // Interpolate between square and complex wave
        float t = (y - 0.5) * 4.0;  // 0 to 1 for second half

        // Square wave
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        // Complex wave (multiple harmonics)
        float complex = sin(phase) * 0.5 +           // Fundamental
                       sin(phase * 2.0) * 0.3 +      // 2nd harmonic
                       sin(phase * 3.0) * 0.15 +     // 3rd harmonic
                       sin(phase * 5.0) * 0.05;      // 5th harmonic

        return mix(square, complex, t);
    }
}

float wavetable_metallic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start harmonic, become increasingly inharmonic
    float wave = sin(phase);

    // Add inharmonic partials
    wave += sin(phase * 2.1) * 0.5 * y;
    wave += sin(phase * 3.3) * 0.3 * y;
    wave += sin(phase * 4.7) * 0.2 * y;
    wave += sin(phase * 6.2) * 0.15 * y;
    wave += sin(phase * 7.9) * 0.1 * y;

    return wave / (1.0 + y);
}

float wavetable_fold(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with sine
    float wave = sin(phase);

    // Apply wave folding - increases with y
    float foldAmount = 1.0 + y * 4.0;
    wave *= foldAmount;

    // Fold the wave
    wave = abs(fract(wave * 0.25 + 0.5) * 2.0 - 1.0) * 2.0 - 1.0;

    return wave;
}

float wavetableTex(vec2 uv, sampler2D bmp) {
    vec4 tex = texture(bmp, uv, 0.);
   return ((tex.r +tex.g +tex.b)*.6666)-1.;
}
float wavetable_bitcrush(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with clean sine
    float wave = sin(phase);

    // Reduce bit depth as y increases
    float bits = 5.0 - y * 7.0; // From 16-bit to 1-bit
    float steps = pow(2.0, bits);

    wave = floor(wave * steps) / steps;

    return wave;
}
float wavetable_vowel(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Different formant frequencies for vowel sounds
    float wave = 0.0;

    if (y < 0.25) {
        // A to E
        float t = y * 4.0;
        // A: 730, 1090, 2440
        float a = sin(phase) + 0.5*sin(phase*1.5) + 0.3*sin(phase*3.3);
        // E: 530, 1840, 2480
        float e = sin(phase) + 0.4*sin(phase*3.5) + 0.3*sin(phase*4.7);
        wave = mix(a, e, t);

    } else if (y < 0.5) {
        // E to I
        float t = (y - 0.25) * 4.0;
        float e = sin(phase) + 0.4*sin(phase*3.5) + 0.3*sin(phase*4.7);
        // I: 270, 2290, 3010
        float i = sin(phase) + 0.3*sin(phase*8.5) + 0.2*sin(phase*11.2);
        wave = mix(e, i, t);

    } else if (y < 0.75) {
        // I to O
        float t = (y - 0.5) * 4.0;
        float i = sin(phase) + 0.3*sin(phase*8.5) + 0.2*sin(phase*11.2);
        // O: 570, 840, 2410
        float o = sin(phase) + 0.5*sin(phase*1.5) + 0.2*sin(phase*4.2);
        wave = mix(i, o, t);

    } else {
        // O to U
        float t = (y - 0.75) * 4.0;
        float o = sin(phase) + 0.5*sin(phase*1.5) + 0.2*sin(phase*4.2);
        // U: 440, 1020, 2240
        float u = sin(phase) + 0.4*sin(phase*2.3) + 0.2*sin(phase*5.1);
        wave = mix(o, u, t);
    }

    return wave * 0.6; // Normalize
}
float wavetable_morph(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.333) {
        // Sine to Triangle
        float t = y * 3.0;
        float sine = sin(phase);
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        return mix(sine, triangle, t);

    } else if (y < 0.666) {
        // Triangle to Sawtooth
        float t = (y - 0.333) * 3.0;
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        float saw = (fract(x) * 2.0 - 1.0);
        return mix(triangle, saw, t);

    } else {
        // Sawtooth to Square
        float t = (y - 0.666) * 3.0;
        float saw = (fract(x) * 2.0 - 1.0);

        // Square wave (bandlimited)
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        return mix(saw, square, t);
    }
}
float wavetable_harmonic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with sine, add harmonics as y increases
    float wave = sin(phase);

    // Add harmonics progressively
    int maxHarmonics = int(1.0 + y * 15.0);
    for(int i = 2; i <= 16; i++) {
        if(i <= maxHarmonics) {
            float amplitude = 1.0 / float(i);
            wave += sin(phase * float(i)) * amplitude;
        }
    }

    // Normalize
    return wave / (1.0 + y * 2.0);
}
float wavetable_harmonic2(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    float outp = sin(phase) * 0.5;  // Fundamental always present

    // Gradually add harmonics based on table position
    int numHarmonics = int(y * 12.0) + 1;

    for(int i = 2; i <= numHarmonics && i <= 12; i++) {
        float harmAmp = (1.0 / float(i)) * (1.0 - float(i - 2) / 11.0 * 0.5);
        outp += sin(phase * float(i)) * harmAmp * y;
    }

    return outp / (1.0 + y * 0.5);  // Normalize
}
float wavetable_pwm(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Pulse width varies from 10% to 90%
    float pulseWidth = 0.1 + y * 0.8;

    // Generate pulse wave using fourier series
    float wave = 0.0;
    for(int i = 1; i <= 9; i++) {
        float n = float(i);
        wave += (sin(n * 3.14159265359 * pulseWidth) / n) * sin(n * phase);
    }
    wave *= 2.0 / 3.14159265359;

    return wave;
}
float wavetable_pwm2(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Pulse width varies from 10% to 90%
    float pulseWidth = 0.1 + y * 0.8;

    // Bandlimited pulse wave
    float pulse = 0.0;
    for(int i = 1; i <= 10; i++) {
        pulse += sin(phase * float(i)) * sin(float(i) * 3.14159265359 * pulseWidth) / float(i);
    }
    pulse *= 4.0 / 3.14159265359;

    return pulse;
}
float wavetableclassic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.5) {
        // Saw to Triangle
        float t = y * 2.0;

        // Bandlimited saw wave
        float saw = 0.0;
        for(int i = 1; i <= 10; i++) {
            saw += sin(phase * float(i)) / float(i);
        }
        saw *= -2.0 / 3.14159265359;

        // Triangle wave
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;

        return mix(saw, triangle, t);
    } else {
        // Triangle to Sine
        float t = (y - 0.5) * 2.0;
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        float sine = sin(phase);
        return mix(triangle, sine, t);
    }
}
float wavetablevoc(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.33) {
        // "Ooh" formant (darker)
        float t = y * 3.0;
        float ooh = sin(phase) * 0.8 + 
                   sin(phase * 2.0) * 0.15 * (1.0 - t) +
                   sin(phase * 3.0) * 0.05;
        return ooh;
    } else if (y < 0.66) {
        // "Aah" formant (brighter)
        float t = (y - 0.33) * 3.0;
        float aah = sin(phase) * 0.5 +
                   sin(phase * 2.0) * 0.3 +
                   sin(phase * 4.0) * 0.15 +
                   sin(phase * 5.0) * 0.05;
        return aah * (1.0 + t * 0.2);
    } else {
        // "Eeh" formant (brightest)
        float t = (y - 0.66) * 3.0;
        float eeh = sin(phase) * 0.4 +
                   sin(phase * 3.0) * 0.3 +
                   sin(phase * 5.0) * 0.2 +
                   sin(phase * 7.0) * 0.1;
        return eeh;
    }
}
float wavetablebell(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with fundamental
    float outp = sin(phase) * (1.0 - y * 0.5);

    // Add increasingly inharmonic partials
    outp += sin(phase * 2.7) * y * 0.3;   // Minor third-ish
    outp += sin(phase * 4.2) * y * 0.2;   // Between harmonics
    outp += sin(phase * 5.4) * y * 0.15;  // Metallic partial
    outp += sin(phase * 7.8) * y * 0.1;   // Bell-like partial

    if (y > 0.5) {
        float t = (y - 0.5) * 2.0;
        outp += sin(phase * 9.3) * t * 0.08;
        outp += sin(phase * 11.7) * t * 0.05;
    }

    return outp;
}
float wavetabledist(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    float sine = sin(phase);
    float drive = 1.0 + y * 10.0;  // Increase overdrive with y

    // Soft to hard clipping
    float clipped = sine * drive;

    if (y < 0.5) {
        // Soft clipping (tanh-like)
        clipped = tanh(clipped * 2.0) * 0.9;
    } else {
        // Harder clipping
        clipped = clamp(clipped, -1.0, 1.0);
        // Add some harmonics back for bandlimiting
        float t = (y - 0.5) * 2.0;
        clipped = mix(tanh(sine * drive * 2.0), 
                     sign(sine) * pow(abs(sine), 0.3 - t * 0.2), 
                     t) * 0.9;
    }

    return clipped;
}
float wavetable(vec2 uv, sampler2D bmp, float time) {
    float period = time/6.2832;
    int num = int(floor(period));
    num = num%15;
    float sampl;

    switch (num){
        case 0: sampl = wavetablebell(uv); break;
        case 1: sampl = wavetableTex(uv, bmp); break;
        case 2: sampl = wavetabletest(uv); break;
        case 3: sampl = wavetable_metallic(uv); break;
        case 4: sampl = wavetable_fold(uv) ; break;
        case 5: sampl = wavetable_bitcrush(uv) ; break;
        case 6: sampl = wavetable_vowel(uv) ; break;
        case 7: sampl = wavetable_morph(uv) ; break;
        case 8: sampl = wavetable_harmonic(uv) ; break;
        case 9: sampl = wavetable_harmonic2(uv) ; break;
        case 10: sampl = wavetable_pwm(uv) ; break;
        case 11: sampl = wavetable_pwm2(uv); break;
        case 12: sampl = wavetableclassic( uv) ; break;
        case 13: sampl = wavetablevoc(uv) ; break;
        case 14: sampl = wavetabledist(uv) ; break;
    }

   return clamp( sampl  , -1.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Only render on first frame, if the wavetable stays the same.
    if (iFrame > 0) {
        //fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
        //return;
    }

    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = vec3(0.0);
    uv+=0.1;
    // Main wavetable display (isometric view)
    vec2 centerUV = (uv - vec2(0.5, 0.5)) * 2.5;
    vec2 proj = vec2(0.6,0.9);
    // Draw reference grid below (at -1.5)
    for(int i = 0; i < 50; i++) {
        float tablePos = float(i) / 49.0;

        // Isometric projection
        float isoX = tablePos * proj.x;  // Move right
        float isoY = tablePos * proj.y;  // Move up

        // Calculate x position from screen position
        float waveX = ((centerUV.x - isoX) / 1.2) + 0.5;

        // Check if we're within the valid range
        if(waveX >= 0.0 && waveX <= 1.0) {
            // Reference grid at constant -1.5
            float refValue = -1.5;
            float posY = refValue * 0.25 + isoY;

            // Distance from current pixel
            float dist = abs(centerUV.y - posY);
            float lineThickness = 0.006 / (1.0 + tablePos * 0.3); // Perspective

            if(dist < lineThickness) {
                float intensity = 1.0 - dist / lineThickness;
                // Fade lines towards the back
                intensity *= 1.0 - tablePos * 0.4;
                // Darker green for reference grid
                col += vec3(0.0, 0.3, 0.0) * intensity * 0.5;
            }
        }
    }

    // Draw vertical lines from base to waveform
    for(int i = 0; i < 30; i++) {
        float tablePos = float(i) / 29.0;

        // Isometric projection
        float isoX = tablePos * proj.x;  // Move right
        float isoY = tablePos * proj.y;  // Move up

        // Calculate x position from screen position
        float waveX = ((centerUV.x - isoX) / 1.2) + 0.5;

        // Check if we're within the valid range
        if(waveX >= 0.0 && waveX <= 1.05) {
            // Sample every few pixels for vertical lines
            float xMod = mod(waveX * 100.0, 5.0);
            if(xMod < 0.5) {
                float waveValue = wavetable(vec2(waveX, tablePos), iChannel1, iTime);

                // Base and top positions
                float baseY = -1.5 * 0.25 + isoY;
                float topY = waveValue * 0.25 + isoY;

                // Check if we're between base and top
                float minY = min(baseY, topY);
                float maxY = max(baseY, topY);

                if(centerUV.y >= minY && centerUV.y <= maxY) {
                    float lineThickness = 0.003 / (1.0 + tablePos * 0.3);
                    float intensity = 1.0 - tablePos * 0.5;
                    if(waveX<0.05||waveX>0.99){intensity*=2.;}
                    col += vec3(0.0, 0.35, 0.0) * intensity *0.2;
                }
            }
        }
    }

    // Draw wavetable on top
    for(int i = 0; i < 30; i++) {
        float tablePos = float(i) / 29.0;

        // Isometric projection
        float isoX = tablePos * proj.x;  // Move right
        float isoY = tablePos * proj.y;  // Move up

        // Calculate x position in waveform from screen position
        float waveX = ((centerUV.x - isoX) / 1.2) + 0.5;

        // Check if we're within the valid waveform range
        if(waveX >= 0.0 && waveX <= 1.0) {
            float waveValue = wavetable(vec2(waveX, tablePos), iChannel1, iTime);

            // y position: wave amplitude + isometric offset
            float posY = waveValue * 0.25 + isoY;

            // Distance from current pixel
            float dist = abs(centerUV.y - posY);
            float lineThickness = 0.008 / (1.0 + tablePos * 0.3); // Perspective

            if(dist < lineThickness) {
                float intensity = 1.0 - dist / lineThickness;
                // Fade lines towards the back
                intensity *= 1.0 - tablePos * 0.4;
                if(waveX<0.05&&waveX>0.99){intensity*=3.;}
                col += vec3(0.0, 0.7, 0.0) * intensity;
            }
        }
    }
    uv-=0.1;
    // 2D bitmap view in corner (properly square with aspect ratio correction)
    float cornerSize = 0.2;
    vec2 cornerPos = vec2(0.05, 0.75); // Bottom left corner
    float aspectRatio = iResolution.x / iResolution.y;

    // Correct for aspect ratio to make it square
    vec2 cornerUV = (uv - cornerPos);
    cornerUV.x *= aspectRatio;
    cornerUV /= cornerSize;

    if(cornerUV.x >= 0.0 && cornerUV.x <= 1.0 && 
       cornerUV.y >= 0.0 && cornerUV.y <= 1.0) {
        float sampl = wavetable(cornerUV, iChannel1, iTime);
        // Map -1 to 1 to dark green to light green
        float intensity = sampl * 0.5 + 0.5;
        col = vec3(0.0, 0.2 + intensity * 0.6, 0.0);
    }

    fragColor = vec4(tanh(col), 1.0);
}

vec2 mainSound( int samp, float time )
{
    // Audio playback rate
    float frequency = 262.0; // C3
    float sampleRate = 44100.0;

    // Phase accumulator
    float phase = fract(time * frequency);

    // Use middle position of wavetable (y = 0.5)
    float tablePos = 0.5+sin(time)/2.;

    // Get sample from wavetable
    float sampl = wavetable(vec2(phase, tablePos), iChannel0, time);

    return vec2(sampl);
}

// Fork of "vol vox box" by 01000001. https://shadertoy.com/view/wfySzh
// Fork of "Wavetable Synth" by Cotterzz. https://shadertoy.com/view/WXsczs

const float pi = 3.14159;
const float eps = 1e-4;
float pc = 3.5;
int lines = 50;
#define flines float(lines)
float offset;
vec3 voxel(vec3 p){
//p = floor(p*flines)/flines;

float h = wavetable(vec2(p.x, p.y), iChannel0, iTime);

vec3 col = vec3(0);
float mul = 2.;
int dom = 0;
//h = (h+1.5)/5.;
h+=1.5;
h/=3.;
if(h>(p.z) && p.y<offset) {col = vec3(0,0.3,0);dom+=1;}

if(h>(p.z) &&abs(p.y-offset)<0.01){
    col = vec3(0,1,0);mul *= 10.;dom+=2;
  } else if(h>(p.z) &&(p.y<0.01||(p.x>0.99&&p.y<offset)||(p.x<0.01&&p.y<offset))){
    col = vec3(0,1,0);mul *= 5.;
  }
if(abs(h-p.z)<0.01){ mul *= 30./max(0.001,(-5.*(p.y-offset)));dom+=4;}

if(dom==7){col = vec3(1,1,1);}


return col*mul;}


float planeRay(vec3 ro, vec3 rd, vec3 norm, float d){
    float a = dot(ro, norm)-d, b = dot(norm, -rd);
    if (b < 0. && a > 0.) return 1e9;
    return a / b;
}

float line(vec2 uv, vec2 a, vec2 b){
    float x = min(length(uv-a),length(uv-b));
    vec2 v = normalize(b-a);
    if (dot(v, uv-a) > 0. && dot(v, uv-a) < length(a-b)) x = min(x, abs(dot(uv-a, v.yx*vec2(-1,1)))); 
    return x;
}

void mainImage( out vec4 O, vec2 U ){
    float SPEED = 1.2 + iTime/50.;
    offset = 0.5+sin(iTime*SPEED)/2.05;
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv =( iMouse.xy == vec2(0)|| length(iMouse.xy)<50.)?vec2(iTime/5., -.2):(2.*iMouse.xy-r)/r.y;
    O = vec4(0);

    vec2 camM = muv*(pi/2. - 1e-3);
    vec3 camF = vec3(sin(camM.x)*cos(camM.y), cos(camM.x)*cos(camM.y), sin(camM.y));
    vec3 camR = vec3(cos(camM.x), sin(-camM.x), 0);
    vec3 camU = cross(camR, camF);

    vec3 o = vec3(.5) - 3.*camF;

    vec3[8] p;
    float vx,vy,vz;
    for (int i = 0; i < 8; i++){
        vx = dot(camR, vec3(i>>2, (i>>1)%2, i%2)-o);
        vy = dot(camU, vec3(i>>2, (i>>1)%2, i%2)-o);
        vz = dot(camF, vec3(i>>2, (i>>1)%2, i%2)-o);
        p[i] = vec3(vx,vy, vz/pc);
    } // vertex shader lol

    float d = 1e4;

    vec3 a,b;

    //*
    for (int z = 0; z <= lines; z+=lines){
        for (int x = 1; x < lines; x++){
            float fx = float(x)/flines;
            float fz = float(z)/flines;
            a = mix(mix(p[0], p[1], fz), mix(p[4], p[5], fz), fx);
            b = mix(mix(p[2], p[3], fz), mix(p[6], p[7], fz), fx);

            d = min(d, line(cuv, a.xy / a.z, b.xy / b.z));


            a = mix(mix(p[4], p[5], fz), mix(p[6], p[7], fz), fx);
            b = mix(mix(p[0], p[1], fz), mix(p[2], p[3], fz), fx);

            d = min(d, line(cuv, a.xy / a.z, b.xy / b.z));
        }
    }
    //*/

    float x = .1 * 
    //smoothstep(2./r.y, 0., d);
    exp(-pow(d / (2./r.y), 2.));
    // gaussian instead of smoothstep nearly eliminates moire

    d = 1e4;
    // Outlines
    //*
    d = min(d, line(cuv, p[0].xy / p[0].z, p[2].xy / p[2].z));
    d = min(d, line(cuv, p[2].xy / p[2].z, p[6].xy / p[6].z));
    d = min(d, line(cuv, p[6].xy / p[6].z, p[4].xy / p[4].z));
    d = min(d, line(cuv, p[4].xy / p[4].z, p[0].xy / p[0].z));

    d = min(d, line(cuv, p[1].xy / p[1].z, p[3].xy / p[3].z));
    d = min(d, line(cuv, p[3].xy / p[3].z, p[7].xy / p[7].z));
    d = min(d, line(cuv, p[7].xy / p[7].z, p[5].xy / p[5].z));
    d = min(d, line(cuv, p[5].xy / p[5].z, p[1].xy / p[1].z));

    d = min(d, line(cuv, p[0].xy / p[0].z, p[1].xy / p[1].z));
    d = min(d, line(cuv, p[2].xy / p[2].z, p[3].xy / p[3].z));
    d = min(d, line(cuv, p[6].xy / p[6].z, p[7].xy / p[7].z));
    d = min(d, line(cuv, p[4].xy / p[4].z, p[5].xy / p[5].z));
    //*/

    x = mix(x, .8, smoothstep(3./r.y, 0., d));


    vec3 norm, dir = camF + camR * cuv.x/pc + camU*cuv.y/pc;
    float t,dis,tempT,maxT,minT;
    vec4 v;

    maxT = 0.; minT = 1e9;
    for (int q = 0; q < 6; q++){
        norm *= 0.;
        norm[q%3] = q%6<3?-1.:1.;
        dis = q<3?0.:1.;
        tempT = planeRay(o, dir, norm, dis);
        if (dot(o, norm)-dis > 0.){
            maxT = max(maxT, tempT);
        } else {
            if (tempT > 0.) minT = min(minT, tempT);
        }
    }
    t = maxT * flines;

    if (minT - maxT > 0.) {

        float d;
        vec3 p,v=vec3(0);
        bvec3 sides = bvec3(dir.x > 0., dir.y > 0., dir.z > 0.);
        vec3 dists;

        for (int i = 0; i < 512; i++){
            p = o*flines + dir*t;

            if (
                   p.x > flines + eps
                || p.y > flines + eps
                || p.z > flines + eps
                || p.x < -eps 
                || p.y < -eps
                || p.z < -eps
            ) break; // prevents loop unrolling too. 

            vec3 vox = voxel(p/flines);

            for (int q = 0; q < 3; q++){
                dists[q] = (sides[q]?ceil(p[q])-p[q]:floor(p[q])-p[q])/dir[q];
            }

            d = min(dists.x, min(dists.y, dists.z));

            t += d + max(t*1e-5, 1e-4);

            v += d * max(vec3(0), vox);

        }

        O.xyz += (1.-exp(-v*.02));

    }
    // 2D bitmap view in corner (properly square with aspect ratio correction)
    float cornerSize = 0.2;
    vec2 cornerPos = vec2(0.05, 0.75); // Bottom left corner
    float aspectRatio = iResolution.x / iResolution.y;

    // Correct for aspect ratio to make it square
    vec2 cornerUV = (uv - cornerPos);
    cornerUV.x *= aspectRatio;
    cornerUV /= cornerSize;

    if(cornerUV.x >= 0.0 && cornerUV.x <= 1.0 && 
       cornerUV.y >= 0.0 && cornerUV.y <= 1.0) {
        float sampl = wavetable(cornerUV, iChannel0, iTime);
        // Map -1 to 1 to dark green to light green
        float intensity = sampl * 0.5 + 0.5;
       O = vec4(0.0, 0.2 + intensity * 0.6, 0.0, 1.0);
    }
    O += vec4(pow(x, 1./2.2));    
}

// Wavetable functions
float wavetabletest(vec2 uv) {
    float x = uv.x;  // Phase (0-1)
    float y = uv.y;  // Table position (0-1)

    // Ensure continuity at boundaries
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.5) {
        // Interpolate between sine and square
        float t = y * 2.0;  // 0 to 1 for first half

        // Sine wave
        float sine = sin(phase);

        // Square wave (bandlimited with a few harmonics)
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        return mix(sine, square, t);
    } else {
        // Interpolate between square and complex wave
        float t = (y - 0.5) * 4.0;  // 0 to 1 for second half

        // Square wave
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        // Complex wave (multiple harmonics)
        float complex = sin(phase) * 0.5 +           // Fundamental
                       sin(phase * 2.0) * 0.3 +      // 2nd harmonic
                       sin(phase * 3.0) * 0.15 +     // 3rd harmonic
                       sin(phase * 5.0) * 0.05;      // 5th harmonic

        return mix(square, complex, t);
    }
}

float wavetable_metallic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start harmonic, become increasingly inharmonic
    float wave = sin(phase);

    // Add inharmonic partials
    wave += sin(phase * 2.1) * 0.5 * y;
    wave += sin(phase * 3.3) * 0.3 * y;
    wave += sin(phase * 4.7) * 0.2 * y;
    wave += sin(phase * 6.2) * 0.15 * y;
    wave += sin(phase * 7.9) * 0.1 * y;

    return wave / (1.0 + y);
}

float wavetable_fold(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with sine
    float wave = sin(phase);

    // Apply wave folding - increases with y
    float foldAmount = 1.0 + y * 4.0;
    wave *= foldAmount;

    // Fold the wave
    wave = abs(fract(wave * 0.25 + 0.5) * 2.0 - 1.0) * 2.0 - 1.0;

    return wave;
}

float wavetableTex(vec2 uv, sampler2D bmp) {
    vec4 tex = texture(bmp, uv, 0.);
   return ((tex.r +tex.g +tex.b))-0.5;
}
float wavetable_bitcrush(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with clean sine
    float wave = sin(phase);

    // Reduce bit depth as y increases
    float bits = 5.0 - y * 7.0; // From 16-bit to 1-bit
    float steps = pow(2.0, bits);

    wave = floor(wave * steps) / steps;

    return wave;
}
float wavetable_vowel(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Different formant frequencies for vowel sounds
    float wave = 0.0;

    if (y < 0.25) {
        // A to E
        float t = y * 4.0;
        // A: 730, 1090, 2440
        float a = sin(phase) + 0.5*sin(phase*1.5) + 0.3*sin(phase*3.3);
        // E: 530, 1840, 2480
        float e = sin(phase) + 0.4*sin(phase*3.5) + 0.3*sin(phase*4.7);
        wave = mix(a, e, t);

    } else if (y < 0.5) {
        // E to I
        float t = (y - 0.25) * 4.0;
        float e = sin(phase) + 0.4*sin(phase*3.5) + 0.3*sin(phase*4.7);
        // I: 270, 2290, 3010
        float i = sin(phase) + 0.3*sin(phase*8.5) + 0.2*sin(phase*11.2);
        wave = mix(e, i, t);

    } else if (y < 0.75) {
        // I to O
        float t = (y - 0.5) * 4.0;
        float i = sin(phase) + 0.3*sin(phase*8.5) + 0.2*sin(phase*11.2);
        // O: 570, 840, 2410
        float o = sin(phase) + 0.5*sin(phase*1.5) + 0.2*sin(phase*4.2);
        wave = mix(i, o, t);

    } else {
        // O to U
        float t = (y - 0.75) * 4.0;
        float o = sin(phase) + 0.5*sin(phase*1.5) + 0.2*sin(phase*4.2);
        // U: 440, 1020, 2240
        float u = sin(phase) + 0.4*sin(phase*2.3) + 0.2*sin(phase*5.1);
        wave = mix(o, u, t);
    }

    return wave * 0.6; // Normalize
}
float wavetable_morph(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.333) {
        // Sine to Triangle
        float t = y * 3.0;
        float sine = sin(phase);
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        return mix(sine, triangle, t);

    } else if (y < 0.666) {
        // Triangle to Sawtooth
        float t = (y - 0.333) * 3.0;
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        float saw = (fract(x) * 2.0 - 1.0);
        return mix(triangle, saw, t);

    } else {
        // Sawtooth to Square
        float t = (y - 0.666) * 3.0;
        float saw = (fract(x) * 2.0 - 1.0);

        // Square wave (bandlimited)
        float square = 0.0;
        for(int i = 1; i <= 7; i += 2) {
            square += sin(phase * float(i)) / float(i);
        }
        square *= 4.0 / 3.14159265359;

        return mix(saw, square, t);
    }
}
float wavetable_harmonic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with sine, add harmonics as y increases
    float wave = sin(phase);

    // Add harmonics progressively
    int maxHarmonics = int(1.0 + y * 15.0);
    for(int i = 2; i <= 16; i++) {
        if(i <= maxHarmonics) {
            float amplitude = 1.0 / float(i);
            wave += sin(phase * float(i)) * amplitude;
        }
    }

    // Normalize
    return wave / (1.0 + y * 2.0);
}
float wavetable_harmonic2(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    float outp = sin(phase) * 0.5;  // Fundamental always present

    // Gradually add harmonics based on table position
    int numHarmonics = int(y * 12.0) + 1;

    for(int i = 2; i <= numHarmonics && i <= 12; i++) {
        float harmAmp = (1.0 / float(i)) * (1.0 - float(i - 2) / 11.0 * 0.5);
        outp += sin(phase * float(i)) * harmAmp * y;
    }

    return outp / (1.0 + y * 0.5);  // Normalize
}
float wavetable_pwm(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Pulse width varies from 10% to 90%
    float pulseWidth = 0.1 + y * 0.8;

    // Generate pulse wave using fourier series
    float wave = 0.0;
    for(int i = 1; i <= 9; i++) {
        float n = float(i);
        wave += (sin(n * 3.14159265359 * pulseWidth) / n) * sin(n * phase);
    }
    wave *= 2.0 / 3.14159265359;

    return wave;
}
float wavetable_pwm2(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Pulse width varies from 10% to 90%
    float pulseWidth = 0.1 + y * 0.8;

    // Bandlimited pulse wave
    float pulse = 0.0;
    for(int i = 1; i <= 10; i++) {
        pulse += sin(phase * float(i)) * sin(float(i) * 3.14159265359 * pulseWidth) / float(i);
    }
    pulse *= 4.0 / 3.14159265359;

    return pulse;
}
float wavetableclassic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.5) {
        // Saw to Triangle
        float t = y * 2.0;

        // Bandlimited saw wave
        float saw = 0.0;
        for(int i = 1; i <= 10; i++) {
            saw += sin(phase * float(i)) / float(i);
        }
        saw *= -2.0 / 3.14159265359;

        // Triangle wave
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;

        return mix(saw, triangle, t);
    } else {
        // Triangle to Sine
        float t = (y - 0.5) * 2.0;
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        float sine = sin(phase);
        return mix(triangle, sine, t);
    }
}
float wavetablevoc(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.33) {
        // "Ooh" formant (darker)
        float t = y * 3.0;
        float ooh = sin(phase) * 0.8 + 
                   sin(phase * 2.0) * 0.15 * (1.0 - t) +
                   sin(phase * 3.0) * 0.05;
        return ooh;
    } else if (y < 0.66) {
        // "Aah" formant (brighter)
        float t = (y - 0.33) * 3.0;
        float aah = sin(phase) * 0.5 +
                   sin(phase * 2.0) * 0.3 +
                   sin(phase * 4.0) * 0.15 +
                   sin(phase * 5.0) * 0.05;
        return aah * (1.0 + t * 0.2);
    } else {
        // "Eeh" formant (brightest)
        float t = (y - 0.66) * 3.0;
        float eeh = sin(phase) * 0.4 +
                   sin(phase * 3.0) * 0.3 +
                   sin(phase * 5.0) * 0.2 +
                   sin(phase * 7.0) * 0.1;
        return eeh;
    }
}
float wavetablebell(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with fundamental
    float outp = sin(phase) * (1.0 - y * 0.5);

    // Add increasingly inharmonic partials
    outp += sin(phase * 2.7) * y * 0.3;   // Minor third-ish
    outp += sin(phase * 4.2) * y * 0.2;   // Between harmonics
    outp += sin(phase * 5.4) * y * 0.15;  // Metallic partial
    outp += sin(phase * 7.8) * y * 0.1;   // Bell-like partial

    if (y > 0.5) {
        float t = (y - 0.5) * 2.0;
        outp += sin(phase * 9.3) * t * 0.08;
        outp += sin(phase * 11.7) * t * 0.05;
    }

    return outp;
}
float wavetabledist(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    float sine = sin(phase);
    float drive = 1.0 + y * 10.0;  // Increase overdrive with y

    // Soft to hard clipping
    float clipped = sine * drive;

    if (y < 0.5) {
        // Soft clipping (tanh-like)
        clipped = tanh(clipped * 2.0) * 0.9;
    } else {
        // Harder clipping
        clipped = clamp(clipped, -1.0, 1.0);
        // Add some harmonics back for bandlimiting
        float t = (y - 0.5) * 2.0;
        clipped = mix(tanh(sine * drive * 2.0), 
                     sign(sine) * pow(abs(sine), 0.3 - t * 0.2), 
                     t) * 0.9;
    }

    return clipped;
}
float wavetable(vec2 uv, sampler2D bmp, float time) {

    float period = time/6.2832;
    int num = int(floor(period));
    float phase = fract(period);
    num = num%15;
    float sampl;

    switch (num){
        case 0: sampl = wavetablebell(uv); break;
        case 1: sampl = wavetableTex(uv, bmp); break;
        case 2: sampl = wavetabletest(uv); break;
        case 3: sampl = wavetable_metallic(uv); break;
        case 4: sampl = wavetable_fold(uv) ; break;
        case 5: sampl = wavetable_bitcrush(uv) ; break;
        case 6: sampl = wavetable_vowel(uv) ; break;
        case 7: sampl = wavetable_morph(uv) ; break;
        case 8: sampl = wavetable_harmonic(uv) ; break;
        case 9: sampl = wavetable_harmonic2(uv) ; break;
        case 10: sampl = wavetable_pwm(uv) ; break;
        case 11: sampl = wavetable_pwm2(uv); break;
        case 12: sampl = wavetableclassic( uv) ; break;
        case 13: sampl = wavetablevoc(uv) ; break;
        case 14: sampl = wavetabledist(uv) ; break;
    }

    float sampl2;

    switch (num+1){

        case 1: sampl2 = wavetableTex(uv, bmp); break;
        case 2: sampl2 = wavetabletest(uv); break;
        case 3: sampl2 = wavetable_metallic(uv); break;
        case 4: sampl2 = wavetable_fold(uv) ; break;
        case 5: sampl2 = wavetable_bitcrush(uv) ; break;
        case 6: sampl2 = wavetable_vowel(uv) ; break;
        case 7: sampl2 = wavetable_morph(uv) ; break;
        case 8: sampl2 = wavetable_harmonic(uv) ; break;
        case 9: sampl2= wavetable_harmonic2(uv) ; break;
        case 10: sampl2 = wavetable_pwm(uv) ; break;
        case 11: sampl2 = wavetable_pwm2(uv); break;
        case 12: sampl2 = wavetableclassic( uv) ; break;
        case 13: sampl2 = wavetablevoc(uv) ; break;
        case 14: sampl2 = wavetabledist(uv) ; break;
        case 15: sampl2 = wavetablebell(uv); break;
    }

    sampl = mix(sampl, sampl2, phase);

   return clamp( sampl  , -1.,1.);
}

vec2 mainSound( int samp, float time )
{
    float frequency = 262.0;
    float SPEED = 1.2 +time/50.;
    float phase = fract(time * frequency);
    float tablePos = 0.5+sin(time*SPEED)/2.;
    float sampl = wavetable(vec2(phase, tablePos), iChannel0, time);   
    return vec2(sampl);
}

