    float wave = 0.0;
    for(int i = 1; i <= 9; i++) {
        float n = float(i);
        wave += (sin(n * 3.14159265359 * pulseWidth) / n) * sin(n * phase);
    }
    wave *= 2.0 / 3.14159265359;

    return wave;
}
float wavetable_pwm2(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Pulse width varies from 10% to 90%
    float pulseWidth = 0.1 + y * 0.8;

    // Bandlimited pulse wave
    float pulse = 0.0;
    for(int i = 1; i <= 10; i++) {
        pulse += sin(phase * float(i)) * sin(float(i) * 3.14159265359 * pulseWidth) / float(i);
    }
    pulse *= 4.0 / 3.14159265359;

    return pulse;
}
float wavetableclassic(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.5) {
        // Saw to Triangle
        float t = y * 2.0;

        // Bandlimited saw wave
        float saw = 0.0;
        for(int i = 1; i <= 10; i++) {
            saw += sin(phase * float(i)) / float(i);
        }
        saw *= -2.0 / 3.14159265359;

        // Triangle wave
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;

        return mix(saw, triangle, t);
    } else {
        // Triangle to Sine
        float t = (y - 0.5) * 2.0;
        float triangle = asin(sin(phase)) * 2.0 / 3.14159265359;
        float sine = sin(phase);
        return mix(triangle, sine, t);
    }
}
float wavetablevoc(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    if (y < 0.33) {
        // "Ooh" formant (darker)
        float t = y * 3.0;
        float ooh = sin(phase) * 0.8 + 
                   sin(phase * 2.0) * 0.15 * (1.0 - t) +
                   sin(phase * 3.0) * 0.05;
        return ooh;
    } else if (y < 0.66) {
        // "Aah" formant (brighter)
        float t = (y - 0.33) * 3.0;
        float aah = sin(phase) * 0.5 +
                   sin(phase * 2.0) * 0.3 +
                   sin(phase * 4.0) * 0.15 +
                   sin(phase * 5.0) * 0.05;
        return aah * (1.0 + t * 0.2);
    } else {
        // "Eeh" formant (brightest)
        float t = (y - 0.66) * 3.0;
        float eeh = sin(phase) * 0.4 +
                   sin(phase * 3.0) * 0.3 +
                   sin(phase * 5.0) * 0.2 +
                   sin(phase * 7.0) * 0.1;
        return eeh;
    }
}
float wavetablebell(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    // Start with fundamental
    float outp = sin(phase) * (1.0 - y * 0.5);

    // Add increasingly inharmonic partials
    outp += sin(phase * 2.7) * y * 0.3;   // Minor third-ish
    outp += sin(phase * 4.2) * y * 0.2;   // Between harmonics
    outp += sin(phase * 5.4) * y * 0.15;  // Metallic partial
    outp += sin(phase * 7.8) * y * 0.1;   // Bell-like partial

    if (y > 0.5) {
        float t = (y - 0.5) * 2.0;
        outp += sin(phase * 9.3) * t * 0.08;
        outp += sin(phase * 11.7) * t * 0.05;
    }

    return outp;
}
float wavetabledist(vec2 uv) {
    float x = uv.x;
    float y = uv.y;
    float phase = x * 2.0 * 3.14159265359;

    float sine = sin(phase);
    float drive = 1.0 + y * 10.0;  // Increase overdrive with y

    // Soft to hard clipping
    float clipped = sine * drive;

    if (y < 0.5) {
        // Soft clipping (tanh-like)
        clipped = tanh(clipped * 2.0) * 0.9;
    } else {
        // Harder clipping
        clipped = clamp(clipped, -1.0, 1.0);
        // Add some harmonics back for bandlimiting
        float t = (y - 0.5) * 2.0;
        clipped = mix(tanh(sine * drive * 2.0), 
                     sign(sine) * pow(abs(sine), 0.3 - t * 0.2), 
                     t) * 0.9;
    }

    return clipped;
}
float wavetable(vec2 uv, sampler2D bmp, float time) {

    float period = time/6.2832;
    int num = int(floor(period));
    float phase = fract(period);
    num = num%15;
    float sampl;

    switch (num){
        case 0: sampl = wavetablebell(uv); break;
        case 1: sampl = wavetableTex(uv, bmp); break;
        case 2: sampl = wavetabletest(uv); break;
        case 3: sampl = wavetable_metallic(uv); break;
        case 4: sampl = wavetable_fold(uv) ; break;
        case 5: sampl = wavetable_bitcrush(uv) ; break;
        case 6: sampl = wavetable_vowel(uv) ; break;
        case 7: sampl = wavetable_morph(uv) ; break;
        case 8: sampl = wavetable_harmonic(uv) ; break;
        case 9: sampl = wavetable_harmonic2(uv) ; break;
        case 10: sampl = wavetable_pwm(uv) ; break;
        case 11: sampl = wavetable_pwm2(uv); break;
        case 12: sampl = wavetableclassic( uv) ; break;
        case 13: sampl = wavetablevoc(uv) ; break;
        case 14: sampl = wavetabledist(uv) ; break;
    }

    float sampl2;

    switch (num+1){

        case 1: sampl2 = wavetableTex(uv, bmp); break;
        case 2: sampl2 = wavetabletest(uv); break;
        case 3: sampl2 = wavetable_metallic(uv); break;
        case 4: sampl2 = wavetable_fold(uv) ; break;
        case 5: sampl2 = wavetable_bitcrush(uv) ; break;
        case 6: sampl2 = wavetable_vowel(uv) ; break;
        case 7: sampl2 = wavetable_morph(uv) ; break;
        case 8: sampl2 = wavetable_harmonic(uv) ; break;
        case 9: sampl2= wavetable_harmonic2(uv) ; break;
        case 10: sampl2 = wavetable_pwm(uv) ; break;
        case 11: sampl2 = wavetable_pwm2(uv); break;
        case 12: sampl2 = wavetableclassic( uv) ; break;
        case 13: sampl2 = wavetablevoc(uv) ; break;
        case 14: sampl2 = wavetabledist(uv) ; break;
        case 15: sampl2 = wavetablebell(uv); break;
    }

    sampl = mix(sampl, sampl2, phase);

   return clamp( sampl  , -1.,1.);
}

