const float pi = 3.14159;
const float eps = 1e-4;
float pc = 3.5;
int lines = 30;
#define flines float(lines)
float offset;

vec3 voxel(vec3 p){
//p = floor(p*flines)/flines;

float h = wavetable(vec2(p.x, p.y), iChannel1, iTime);

vec3 col = vec3(0);
float mul = 3.;
if((h+1.5)/5.>(p.z) && p.y<offset) {col = vec3(0,0.2,0);}
  if(distance(vec2(p.z,p.y),vec2((h+1.5)/5.,offset))<0.02){ col = vec3(1);mul = 100.;} else if(abs((h+1.5)-(p.z*5.))<0.05){ mul = 50.;}


return col*mul;}


//return vec3((h+1.8)-(p.z*5.))*10.;
