    return vec3(
        .10 - length(mod(p+.10, .5)-.25),
        .15 - length(mod(p+.05, .5)-.25),
        .20 - length(mod(p+.00, .5)-.25)
    )*100.;

    */



float planeRay(vec3 ro, vec3 rd, vec3 norm, float d){
    float a = dot(ro, norm)-d, b = dot(norm, -rd);
    if (b < 0. && a > 0.) return 1e9;
    return a / b;
}

float line(vec2 uv, vec2 a, vec2 b){
    float x = min(length(uv-a),length(uv-b));
    vec2 v = normalize(b-a);
    if (dot(v, uv-a) > 0. && dot(v, uv-a) < length(a-b)) x = min(x, abs(dot(uv-a, v.yx*vec2(-1,1)))); 
    return x;
}

void mainImage( out vec4 O, vec2 U ){
    float SPEED = 1.5;// + (sin(iTime/7.) *1.0);
    offset = 0.49+sin(iTime*SPEED)/2.0;
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = iMouse.xy == vec2(0)?vec2(iTime/5., -.2):(2.*iMouse.xy-r)/r.y;
    O = vec4(0);

    vec2 camM = muv*(pi/2. - 1e-3);
    vec3 camF = vec3(sin(camM.x)*cos(camM.y), cos(camM.x)*cos(camM.y), sin(camM.y));
    vec3 camR = vec3(cos(camM.x), sin(-camM.x), 0);
    vec3 camU = cross(camR, camF);

    vec3 o = vec3(.5) - 3.*camF;

    vec3[8] p;
    float vx,vy,vz;
    for (int i = 0; i < 8; i++){
        vx = dot(camR, vec3(i>>2, (i>>1)%2, i%2)-o);
        vy = dot(camU, vec3(i>>2, (i>>1)%2, i%2)-o);
        vz = dot(camF, vec3(i>>2, (i>>1)%2, i%2)-o);
        p[i] = vec3(vx,vy, vz/pc);
    } // vertex shader lol

    float d = 1e4;

    vec3 a,b;

    //*
    for (int z = 0; z <= lines; z+=lines){
        for (int x = 1; x < lines; x++){
            float fx = float(x)/flines;
            float fz = float(z)/flines;
            a = mix(mix(p[0], p[1], fz), mix(p[4], p[5], fz), fx);
            b = mix(mix(p[2], p[3], fz), mix(p[6], p[7], fz), fx);

            d = min(d, line(cuv, a.xy / a.z, b.xy / b.z));


            a = mix(mix(p[4], p[5], fz), mix(p[6], p[7], fz), fx);
            b = mix(mix(p[0], p[1], fz), mix(p[2], p[3], fz), fx);

            d = min(d, line(cuv, a.xy / a.z, b.xy / b.z));
        }
    }
    //*/

    float x = .1 * 
    //smoothstep(2./r.y, 0., d);
    exp(-pow(d / (2./r.y), 2.));
    // gaussian instead of smoothstep nearly eliminates moire

    d = 1e4;
    // Outlines
    //*
    d = min(d, line(cuv, p[0].xy / p[0].z, p[2].xy / p[2].z));
    d = min(d, line(cuv, p[2].xy / p[2].z, p[6].xy / p[6].z));
    d = min(d, line(cuv, p[6].xy / p[6].z, p[4].xy / p[4].z));
    d = min(d, line(cuv, p[4].xy / p[4].z, p[0].xy / p[0].z));

    d = min(d, line(cuv, p[1].xy / p[1].z, p[3].xy / p[3].z));
    d = min(d, line(cuv, p[3].xy / p[3].z, p[7].xy / p[7].z));
    d = min(d, line(cuv, p[7].xy / p[7].z, p[5].xy / p[5].z));
    d = min(d, line(cuv, p[5].xy / p[5].z, p[1].xy / p[1].z));

    d = min(d, line(cuv, p[0].xy / p[0].z, p[1].xy / p[1].z));
    d = min(d, line(cuv, p[2].xy / p[2].z, p[3].xy / p[3].z));
    d = min(d, line(cuv, p[6].xy / p[6].z, p[7].xy / p[7].z));
    d = min(d, line(cuv, p[4].xy / p[4].z, p[5].xy / p[5].z));
    //*/

    x = mix(x, .8, smoothstep(3./r.y, 0., d));


    vec3 norm, dir = camF + camR * cuv.x/pc + camU*cuv.y/pc;
    float t,dis,tempT,maxT,minT;
    vec4 v;

    maxT = 0.; minT = 1e9;
    for (int q = 0; q < 6; q++){
        norm *= 0.;
        norm[q%3] = q%6<3?-1.:1.;
        dis = q<3?0.:1.;
        tempT = planeRay(o, dir, norm, dis);
        if (dot(o, norm)-dis > 0.){
            maxT = max(maxT, tempT);
        } else {
            if (tempT > 0.) minT = min(minT, tempT);
        }
    }
    t = maxT * flines;

    if (minT - maxT > 0.) {

        float d;
        vec3 p,v=vec3(0);
        bvec3 sides = bvec3(dir.x > 0., dir.y > 0., dir.z > 0.);
        vec3 dists;

        for (int i = 0; i < 512; i++){
            p = o*flines + dir*t;

            if (
                   p.x > flines + eps
                || p.y > flines + eps
                || p.z > flines + eps
                || p.x < -eps 
                || p.y < -eps
                || p.z < -eps
            ) break; // prevents loop unrolling too. 

            vec3 vox = voxel(p/flines);

            for (int q = 0; q < 3; q++){
                dists[q] = (sides[q]?ceil(p[q])-p[q]:floor(p[q])-p[q])/dir[q];
            }

            d = min(dists.x, min(dists.y, dists.z));

            t += d + max(t*1e-5, 1e-4);

            v += d * max(vec3(0), vox);

        }

        O.xyz += (1.-exp(-v*.02));

    }
    // 2D bitmap view in corner (properly square with aspect ratio correction)
    float cornerSize = 0.2;
    vec2 cornerPos = vec2(0.05, 0.75); // Bottom left corner
    float aspectRatio = iResolution.x / iResolution.y;

    // Correct for aspect ratio to make it square
    vec2 cornerUV = (uv - cornerPos);
    cornerUV.x *= aspectRatio;
    cornerUV /= cornerSize;

    if(cornerUV.x >= 0.0 && cornerUV.x <= 1.0 && 
       cornerUV.y >= 0.0 && cornerUV.y <= 1.0) {
        float sampl = wavetable(cornerUV, iChannel1, iTime);
        // Map -1 to 1 to dark green to light green
        float intensity = sampl * 0.5 + 0.5;
       O = vec4(0.0, 0.2 + intensity * 0.6, 0.0, 1.0);
    }
    O += vec4(pow(x, 1./2.2));    
}

vec2 mainSound( int samp, float time )
{
    // Audio playback rate
    float frequency = 262.0; // C3
    float sampleRate = 44100.0;
    float SPEED = 1.5;// + (sin(time/7.) *1.0);
    // Phase accumulator
    float phase = fract(time * frequency);

    // Use middle position of wavetable (y = 0.5)
    float tablePos = 0.5+sin(time*SPEED)/2.;

    // Get sample from wavetable
    float sampl = wavetable(vec2(phase, tablePos), iChannel0, time);

    return vec2(sampl);
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01

mat3 rotation;

mat3 rotateX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1,0,0, 0,c,-s, 0,s,c);
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c,0,s, 0,1,0, -s,0,c);
}

mat3 rotateZ(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c,-s,0, s,c,0, 0,0,1);
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdDodecahedron(vec3 p, float r) {
    const vec3 n = normalize(vec3(1.0, 1.618, 0.0)); // Golden ratio
    p = abs(p);
    float a = dot(p, n.xyz);
    float b = dot(p, n.zxy);
    float c = dot(p, n.yzx);
    return (max(max(a, b), c) - r) * 0.57735; 
}

float getDist(vec3 p) {
    vec2 mouse = (iMouse.xy / iResolution.xy - 0.5) * 2.0;
    float rotY = mouse.x * 3.14159;
    float rotX = mouse.y * 3.14159;
    rotation = rotateY(rotY) * rotateX(rotX);

    // Apply rotation to the entire scene first
    vec3 rotatedP = rotation * p;

    // Center box (2x2x4, so half-extents are 1x1x2)
    float box = sdBox(rotatedP, vec3(1.0, 1.0, 2.0));

    // Center box (2x2x4, so half-extents are 1x1x2)
    float board = sdBox(rotatedP - vec3(0., 2., 0.0), vec3(5.0, .3, 2.));

    // Left sphere (radius 1, positioned at x = -4.5)
    float sphere = sdSphere(rotatedP - vec3(-3., 0.0, 0.0), 1.3);

    // Right dodecahedron (radius 1, positioned at x = 4.5)
    float dodeca = sdDodecahedron(rotatedP - vec3(3., 0.0, 0.0), 1.0);

    // Return minimum distance (union of all objects)
    return min(min(min(box, board), sphere), dodeca);
}

vec3 getNormal(vec3 p) {
    float d = getDist(p);
    vec2 e = vec2(0.01, 0);
    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx)
    );
    return normalize(n);
}

float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;

    for(int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDist(p);
        dO += dS;

        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }

    return dO;
}

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

vec3 getWoodTexture(vec3 worldPos, out float darkness) {

    // UNCOMMENT TO FIX TEXTURE TO OBJECTS
    // worldPos = rotation*worldPos; 

    float distFromYAxis = length(worldPos.xz);
    float angleFromAxis = atan(worldPos.z, worldPos.x);

    distFromYAxis  += (hash(vec2(floor(angleFromAxis*1200.), 0.)) + hash(vec2(0.,floor(angleFromAxis*700.))))*(0.1/max(1.,distFromYAxis*distFromYAxis));

    for(int i = 0; i < 10; i++) {
        // Generate pseudo-random knot parameters
        vec2 seed = vec2(float(i) * 12.345, float(i) * 67.89);
        float knotAngle = hash(seed) * 6.28318;
        float knotSize = 0.25 + hash(seed*2.) * 0.3;
        float knotDist = 2.0 + hash(seed + vec2(1.5, 0.0)) * 0.5; 
        float knotY = -1.5 + hash(seed + vec2(1.0, 0.0)) * 2.0; 


        vec2 knotCenter = vec2(cos(knotAngle) * knotDist, sin(knotAngle) * knotDist);
        float distFromKnot = length(worldPos.xz - knotCenter);

        //float yFalloff = exp(-abs(worldPos.y - knotY) * 0.8);

        // Modify the main distance field with knot influence
        if(distFromKnot < knotSize ) {
            // Create a "well" around the knot - negative values make it a new grain center
            float knotInfluence = (knotSize - distFromKnot) / knotSize; // 1.0 at center, 0.0 at edge
            //knotInfluence *= yFalloff;

            // Subtract from distance field to create negative regions (knot centers)
            distFromYAxis -= (knotInfluence * knotInfluence *3.5);
        }
    }

    // Add some noise for more realistic wood grain
    float noise = sin(worldPos.x * 4.0) * 0.05 + sin(worldPos.z * 4.0) * 0.05;
    distFromYAxis += noise*distFromYAxis*0.9;



    // Now calculate ALL grain from this single modified distance field
    // Main rings
    float rings = fract(distFromYAxis * distFromYAxis *1.5);


    float mainGrain = smoothstep(0.0, 0.3, rings) - smoothstep(0.7, 1.0, rings);



    // Sub-grain - faint lines between main lines
    float subRings = fract(distFromYAxis * 30.0); // Higher frequency
    float subGrain = smoothstep(0.1*mainGrain, 0.2*mainGrain, subRings) - smoothstep(0.7*mainGrain, 0.8*mainGrain, subRings);
    subGrain *= 0.4;

    float combinedGrain = mainGrain + subGrain;
        // Output darkness factor for specular modulation
    darkness = 1.0 - (combinedGrain * 0.5 + 0.5); // Keep in 0-1 range for lighting

    vec3 darkWood = vec3(0.65, 0.45, 0.15);
    vec3 lightWood = vec3(0.85, 0.65, 0.35);
    vec3 knotWood = vec3(0.4, 0.3, 0.1)+(distFromYAxis/10.); 

    vec3 woodColor;
    if(distFromYAxis < 0.0) {
        woodColor = mix(knotWood, darkWood, combinedGrain * 0.5);
        darkness +=0.5;
    } else {
        // Normal grain regions
        woodColor = mix(darkWood, lightWood, combinedGrain);

    }



    return woodColor;
}

vec3 getLight(vec3 p, vec3 normal, vec3 viewDir, vec3 albedo, float darkness) {
    vec3 lightPos = vec3(5.0, 8.0, 10.0);
    vec3 lightDir = normalize(lightPos - p);

    vec3 ambient = albedo * 0.2;

    float diff = max(dot(normal, lightDir), 0.0);
    vec3 diffuse = (albedo * diff) *0.8;

    // Variable specular - stronger where texture is darker
    vec3 reflectDir = reflect(-lightDir, normal);
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), 8.0);
    float specularStrength = 0.1 + darkness * 0.7;
    vec3 specular = vec3(specularStrength) * spec;


    float shadow = 1.0;
    float shadowDist = rayMarch(p + normal * SURF_DIST * 2.0, lightDir);
    if(shadowDist < length(lightPos - p)) {
        shadow = 0.3;
    }

    return ambient + (diffuse + specular) * shadow;
}

void mainImage0(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0, 0, 10); 
    vec3 rd = normalize(vec3(uv, -1)); 
    float d = rayMarch(ro, rd);
    vec3 col = vec3(0.1, 0.1, 0.2); 

    if(d < MAX_DIST) {

        vec3 p = ro + rd * d;
        vec3 normal = getNormal(p);
        float darkness;
        vec3 albedo = getWoodTexture(p, darkness);
        vec3 viewDir = normalize(-rd);
        col = getLight(p, normal, viewDir, albedo, darkness);
    }

    col = pow(col, vec2(0.4545).xxx);
    fragColor = vec4(col, 1.0);
}

