#define PI 3.1416
float atan2(in float y, in float x)
{
    return x == 0.0 ? sign(y)*1.570796 : atan(y, x);
}
struct Surface {
    vec3 normal;
    float depth;
    vec2 uv; // Added UV coordinates
};

struct Sphere {
    vec3 position;
    float radius;
    vec4 orientation; // Added quaternion for rotation
};


// Helper function to rotate a vector by a quaternion
vec3 QuaternionRotateVector(vec4 q, vec3 v) {
    vec3 result;
    float x = q.x, y = q.y, z = q.z, w = q.w;
    float vx = v.x, vy = v.y, vz = v.z;

    result.x = vx*(w*w + x*x - y*y - z*z) + 2.*vy*(x*y - w*z) + 2.*vz*(x*z + w*y);
    result.y = 2.*vx*(x*y + w*z) + vy*(w*w - x*x + y*y - z*z) + 2.*vz*(y*z - w*x);
    result.z = 2.*vx*(x*z - w*y) + 2.*vy*(y*z + w*x) + vz*(w*w - x*x - y*y + z*z);

    return result;
}

vec2 RotateUV(vec2 uv, vec3 normal) {
    // Calculate the rotation angle around the y-axis (latitude)
    float lat_angle = acos(normal.y);

    // Calculate the rotation angle around the x-axis (longitude)
    float long_angle = atan(normal.x, normal.z);

    // Apply the rotations to the UV coordinates
    float u = uv.x + long_angle / (2. * PI);
    float v = uv.y + lat_angle / PI;

    // Ensure UV coordinates are within [0, 1] range
    u = mod(u, 1.0);
    //v = clamp(v, 0.0, 1.0);
    v = mod(v, 1.0);
    u = clamp(u , 0.0, 1.0);
    return vec2(u, v);
}
vec3 Normalize(vec3 v) {
    float length = sqrt(v.x * v.x + v.y * v.y + v.z * v.z);
    return vec3(v.x / length, v.y / length, v.z / length);
}
Surface GetSphereSurface(Sphere sphere, vec2 coords, vec3 normal) {
    vec2 diff = vec2( coords.x - sphere.position.x, coords.y - sphere.position.y );
    float dist = sqrt(diff.x * diff.x + diff.y * diff.y);
    float disp = sphere.radius - dist;
    Surface newsurface;

    if (disp < 0.0f) {
        newsurface.normal = vec3( 0.0f, 0.0f, 0.0f );
        newsurface.depth = 0.0f;
        newsurface.uv = vec2( 0.0f, 0.0f );
        return newsurface;
    } else {
        float depth = sqrt(sphere.radius * sphere.radius - dist * dist);
        // Assuming projection along Z-axis: correct depth to Z position
        newsurface.depth = depth + sphere.position.z; // Corrected to sphere.position.z

        // Compute normal (diff.x, diff.y, depth) normalized
        newsurface.normal = vec3(
            diff.x / sphere.radius,
            diff.y / sphere.radius,
            depth / sphere.radius
        );

        // World space point (assuming projection along Z-axis)
        vec3 world_point = vec3(
            coords.x,
            coords.y,
            sphere.position.z + depth
        );

        // Translate to sphere's local position
        vec3 relative_point =  vec3(
            world_point.x - sphere.position.x,
            world_point.y - sphere.position.y,
            world_point.z - sphere.position.z
        );

        // Invert orientation (conjugate quaternion)
        vec4 inv_orient = vec4( 
            -sphere.orientation.x, 
            -sphere.orientation.y, 
            -sphere.orientation.z, 
            sphere.orientation.w 
        );

        // Rotate to local space
        vec3 local_point = QuaternionRotateVector(inv_orient, relative_point);

        // Normalize to get surface point on unit sphere
        float len = sqrt(local_point.x*local_point.x + local_point.y*local_point.y + local_point.z*local_point.z);
        if (len == 0.0f) {
            newsurface.uv = vec2( 0.5f, 0.5f );
            return newsurface;
        }
        vec3 norm_local = vec3(
            local_point.x / len,
            local_point.y / len,
            local_point.z / len
        );

        // Calculate UV coordinates
        float phi = atan(norm_local.z, norm_local.x); // Azimuthal angle
        float theta = asin(norm_local.y); // Polar angle (latitude)


        float u = (phi + PI) / (2. * PI); // Map to [0, 1]
        float v = 0.5f - theta / PI;     // Map north pole to 0, south to 1

        newsurface.uv = vec2( u, v );


        return newsurface;
    }
}

vec4 ApplyInitialOrientation(vec4 initial_orientation, vec4 axis_angle_quaternion) {
    // Quaternion composition: q1 * q2
    float w = initial_orientation.w * axis_angle_quaternion.w - (initial_orientation.x * axis_angle_quaternion.x + initial_orientation.y * axis_angle_quaternion.y + initial_orientation.z * axis_angle_quaternion.z);
    float x = initial_orientation.w * axis_angle_quaternion.x + initial_orientation.x * axis_angle_quaternion.w + initial_orientation.y * axis_angle_quaternion.z - initial_orientation.z * axis_angle_quaternion.y;
    float y = initial_orientation.w * axis_angle_quaternion.y + initial_orientation.y * axis_angle_quaternion.w + initial_orientation.z * axis_angle_quaternion.x - initial_orientation.x * axis_angle_quaternion.z;
    float z = initial_orientation.w * axis_angle_quaternion.z + initial_orientation.z * axis_angle_quaternion.w + initial_orientation.x * axis_angle_quaternion.y - initial_orientation.y * axis_angle_quaternion.x;

    return vec4(x, y, z, w);
}

vec4 QuaternionFromAxisAngle(vec3 axis, float angle) {
    vec3 norm_axis = Normalize(axis);
    float sin_half_angle = sin(angle / 2.0);
    vec4 q = vec4(
        norm_axis.x * sin_half_angle,
        norm_axis.y * sin_half_angle,
        norm_axis.z * sin_half_angle,
        cos(angle / 2.0)
    );
    return q;
}

vec4 QuaternionFromAxisAngle2(vec3 axis, float angle) {
    vec4 q;
    float halfAngle = angle / 2.;
    float sinHalfAngle = sin(halfAngle);
    float cosHalfAngle = cos(halfAngle);

    q.x = sinHalfAngle * axis.x;
    q.y = sinHalfAngle * axis.y;
    q.z = sinHalfAngle * axis.z;
    q.w = cosHalfAngle;

    return q;
}


