// Simple 2-component height/speed “wave” update in Buffer A
// – height is stored in R (range = [0.0…1.0])
// – speed is stored in G shifted by +0.5 (so speed = 0.5 --> 0)

// You can tune these:
const float k  = 9.8;  // “stiffness” or wave‐speed factor
const float dt = 0.1; // time step
const float u = 0.2; // time step

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // 1) Compute UV and one‐texel offset:
    vec2 uv    = fragCoord / iResolution.xy;
    vec2 cell  = 4. / iResolution.xy; 

    // calm initial conditions
    if (iFrame < 1) {
        fragColor = vec4(0.5, 0.5, 0.0, 1.0);

        return;
    }


    // 3) Read “old” height & speed from R & (G − 0.5):
    float oldH = texture(iChannel0, uv).r;
    float oldS = (texture(iChannel0, uv).g - 0.5);  // shift so 0.5 → speed 0

    // 4) Sample 4 neighbors for height (hlaplace) and speed (slaplace)

    // — Height neighbors:
    float hL = texture(iChannel0, uv + vec2(-cell.x,  0.0)).r; // left
    float hR = texture(iChannel0, uv + vec2(+cell.x,  0.0)).r; // right
    float hU = texture(iChannel0, uv + vec2( 0.0,    +cell.y)).r; // up
    float hD = texture(iChannel0, uv + vec2( 0.0,    -cell.y)).r; // down

    float hlaplace = (hL + hR + hU + hD) - 4.0 * oldH;

    // — Speed neighbors:
    float sL = texture(iChannel0, uv + vec2(-cell.x,  0.0)).g - 0.5;
    float sR = texture(iChannel0, uv + vec2(+cell.x,  0.0)).g - 0.5;
    float sU = texture(iChannel0, uv + vec2( 0.0,    +cell.y)).g - 0.5;
    float sD = texture(iChannel0, uv + vec2( 0.0,    -cell.y)).g - 0.5;

    float slaplace = (sL + sR + sU + sD) - 4.0 * oldS;

    // 5) Update speed (wave eqn: ∂S/∂t ∝ + laplacian(height) )
    float newS = oldS + hlaplace * (k * dt);
     newS += slaplace * (u * dt);


    // 6) Update height using the new speed:
    float newH = oldH + newS * dt;

    vec2 mouse = iMouse.xy/iResolution.xy;

    if(iMouse.z > 0.0  ) {  // mouse is pressed
        if(length(uv-mouse.xy) < 0.01){
            newH = 0.0;
        }
    }

    if(texture(iChannel0, uv).b == 0.){
        fragColor = vec4(0.5, 0.5, 1.0, 1.0);
        return;
    }
    // 7) Pack back into RGBA:
    //    – R = newH
    //    – G = newS + 0.5 (bring speed back into [0,1] range)
    //    – B = 0 (unused)
    //    – A = 1 (opaque)
    fragColor = vec4(newH, newS + 0.5, 1.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 cell = 2.0/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;


    float h = texture(iChannel0,uv).r;
    float s = texture(iChannel0,uv).g;

    float r = (h-0.5)*5.;
    float b = (0.5-h)*5.;

    vec4 mean = vec4(0.,0.,0.,0.); 
    vec4 center = texture(iChannel0, uv);
    mean += texture(iChannel0, uv + vec2(cell.x,  0.0)); 
    mean += texture(iChannel0, uv + vec2(cell.x,  cell.y)); 
    mean += texture(iChannel0, uv + vec2(cell.x,  -cell.y));

    mean += texture(iChannel0, uv + vec2(-cell.x,  0.0)); 
    mean += texture(iChannel0, uv + vec2(-cell.x,  cell.y)); 
    mean += texture(iChannel0, uv + vec2(-cell.x,  -cell.y));

    mean += texture(iChannel0, uv + vec2(0.,  0.0)); 
    mean += texture(iChannel0, uv + vec2(0.,  cell.y)); 
    mean += texture(iChannel0, uv + vec2(0.,  -cell.y));


    center -= (center-(mean/9.))*0.9;

    fragColor = center;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 cell = 2.0/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;


    float h = 0.5+texture(iChannel0, uv).r;
    float hL = texture(iChannel0, uv + vec2(-cell.x,  0.0)).r; // left
    float hR = texture(iChannel0, uv + vec2(+cell.x,  0.0)).r; // right
    float hU = texture(iChannel0, uv + vec2( 0.0,    +cell.y)).r; // up
    float hD = texture(iChannel0, uv + vec2( 0.0,    -cell.y)).r; // down

    float dx = (hL-hR)*h;
    float dy = (hU-hD)*h;

    // refraction based on height derivative: 
    vec3 normal = normalize(vec3(dx,1./(dx*dy),dy));
    float c = dot(normalize(vec3(1.,0.,0.)),normal);
    c*=c;
    float br = texture(iChannel1, uv + vec2(dx,dy)*0.7).r;
    float bg = texture(iChannel1, uv + vec2(dx,dy)*0.62).g;
    float bb = texture(iChannel1, uv + vec2(dx,dy)*0.84).b;

    fragColor = vec4(br,bg,bb,1.) + c*c;

}

float sdCircle(vec2 p, vec2 c, float r) {
    return length(p - c) - r;
}

float sdApollonius(vec2 p) {
    float d = 1e10;

    // Read metadata
    float numCircles = texelFetch(iChannel0, ivec2(0, 0), 0).x;

    // Distance to all circles
    for (int i = 2; i < int(numCircles) && i < 400; i++) {
        vec4 circle = texelFetch(iChannel0, ivec2(0, i), 0);
        float sd = sdCircle(p, circle.xy, circle.z);
        d = min(d, sd); // abs() for filled circles
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= 2.2; // Zoom

    // 2D view
    float d = sdApollonius(uv);

    // Color based on distance
    vec3 col = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7);
    col *= 1.0 - exp(-3.0 * abs(d));
    col *= 0.8 + 0.2 * cos(140.0 * d);
    col = mix(col, vec3(1.0), 1.0 - smoothstep(0.0, 0.01, abs(d)));

    // Show circle count
    float numCircles = texelFetch(iChannel0, ivec2(0, 0), 0).x;
    fragColor = vec4(col, 1.0) + texture(iChannel0, fragCoord.xy/iResolution.xy, 0.);

}

