vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 cdiv(vec2 a, vec2 b) {
    float denom = dot(b, b);
    return vec2(dot(a,b), a.y*b.x - a.x*b.y) / denom;
}

vec2 csqrt(vec2 z) {
    float r = length(z);
    float angle = atan(z.y, z.x) * 0.5;
    return sqrt(r) * vec2(cos(angle), sin(angle));
}

float descartesCurvature(float k1, float k2, float k3, bool plus) {
    float sum = k1 + k2 + k3;
    float root = 2.0 * sqrt(max(0.0, k1*k2 + k2*k3 + k3*k1));
    return sum + (plus ? root : -root);
}

vec2 descartesPosition(vec2 z1, vec2 z2, vec2 z3, 
                       float k1, float k2, float k3, float k4, bool plus) {
    vec2 sum = k1*z1 + k2*z2 + k3*z3;
    vec2 term1 = cmul(vec2(k1*k2, 0.0), cmul(z1, z2));
    vec2 term2 = cmul(vec2(k2*k3, 0.0), cmul(z2, z3));  
    vec2 term3 = cmul(vec2(k3*k1, 0.0), cmul(z3, z1));
    vec2 sqrtArg = term1 + term2 + term3;
    vec2 sqrtVal = csqrt(sqrtArg);
    vec2 result = sum + (plus ? 2.0 : -2.0) * sqrtVal;
    return cdiv(result, vec2(k4, 0.0));
}

const float MIN_RADIUS = 0.002;
const int MAX_CIRCLES = 400;

// STRICTER validation
bool isValidNewCircle(vec2 pos, float r4, float k4, vec4 c1, vec4 c2, vec4 c3, int numCircles) {

    if (r4 < MIN_RADIUS || r4 > 2.0) return false;
    if (any(isnan(pos)) || any(isinf(pos))) return false;

    // STRICT tangency check to all 3 parents
    float d1 = length(pos - c1.xy);
    float exp1 = (c1.w < 0.0) ? abs(c1.z - r4) : (c1.z + r4);
    if (abs(d1 - exp1) > 0.008) return false;  // Tighter tolerance

    float d2 = length(pos - c2.xy);
    float exp2 = (c2.w < 0.0) ? abs(c2.z - r4) : (c2.z + r4);
    if (abs(d2 - exp2) > 0.008) return false;

    float d3 = length(pos - c3.xy);
    float exp3 = (c3.w < 0.0) ? abs(c3.z - r4) : (c3.z + r4);
    if (abs(d3 - exp3) > 0.008) return false;

    // Check ALL existing circles
    for (int e = 1; e <= numCircles && e < 100; e++) {
        vec4 ex = texelFetch(iChannel0, ivec2(0, e), 0);
        float centerDist = distance(ex.xy, pos);

        // Duplicate check
        if (centerDist < 0.008 && abs(ex.z - r4) < 0.008) return false;

        // Strict overlap check for internal circles
        if (ex.w > 0.0 && k4 > 0.0) {
            float sumRadii = ex.z + r4;
            // Must be tangent (within tolerance) or well separated
            if (centerDist < sumRadii - 0.005) {
                // Overlapping - reject unless they're tangent
                if (abs(centerDist - sumRadii) > 0.008) {
                    return false;
                }
            }
        }
    }

    return true;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 coord = ivec2(fragCoord);

    if (coord.x != 0) {
        discard;
        return;
    }

    // INITIALIZATION
    if (iFrame == 0) {
        if (coord.y == 0) {
            // [numCircles, tripletCounter, unused, unused]
            fragColor = vec4(4.0, 0.0, 0.0, 0.0);
            return;
        }

        if (coord.y == 1) {
            fragColor = vec4(0.0, 0.0, 1.0, -1.0);
        }
        else if (coord.y == 2) {
            float r = 1.0 / (1.0 + 2.0/sqrt(3.0));
            float y = 1.0 - r;
            fragColor = vec4(0.0, y, r, 1.0/r);
        }
        else if (coord.y == 3) {
            float r = 1.0 / (1.0 + 2.0/sqrt(3.0));
            float d = 1.0 - r;
            float angle = -2.0 * 3.14159265 / 3.0 + 3.14159265/2.0;
            fragColor = vec4(d * cos(angle), d * sin(angle), r, 1.0/r);
        }
        else if (coord.y == 4) {
            float r = 1.0 / (1.0 + 2.0/sqrt(3.0));
            float d = 1.0 - r;
            float angle = 2.0 * 3.14159265 / 3.0 + 3.14159265/2.0;
            fragColor = vec4(d * cos(angle), d * sin(angle), r, 1.0/r);
        }
        return;
    }

    vec4 prev = texelFetch(iChannel0, coord, 0);
    fragColor = prev;

    // METADATA ROW - Systematic triplet cycling with persistent counter
    if (coord.y == 0) {
        float numCircles = prev.x;
        float tripletCounter = prev.y;  // Persistent counter

        if (numCircles < float(MAX_CIRCLES)) {

            int nc = int(numCircles);
            int maxSearches = 50;  // Increased from 30 to cover more triplets
            bool found = false;

            for (int search = 0; search < maxSearches && !found; search++) {
                // Wrap around when we've tried all triplets
                int maxTriplets = (nc * (nc-1) * (nc-2)) / 6;
                int tripletIdx = int(tripletCounter + float(search)) % max(1, maxTriplets);
                int currentIdx = 0;

                // Systematic iteration through ALL triplets
                for (int i = 1; i <= nc && i < 40 && !found; i++) {
                    for (int j = i+1; j <= nc && j < 40 && !found; j++) {
                        for (int k = j+1; k <= nc && k < 40 && !found; k++) {

                            if (currentIdx == tripletIdx) {

                                vec4 c1 = texelFetch(iChannel0, ivec2(0, i), 0);
                                vec4 c2 = texelFetch(iChannel0, ivec2(0, j), 0);
                                vec4 c3 = texelFetch(iChannel0, ivec2(0, k), 0);

                                for (int curvSol = 0; curvSol < 2 && !found; curvSol++) {
                                    float k4 = descartesCurvature(c1.w, c2.w, c3.w, curvSol == 0);

                                    if (abs(k4) < 0.01 || abs(k4) > 1000.0) continue;

                                    float r4 = abs(1.0 / k4);

                                    for (int posSol = 0; posSol < 2 && !found; posSol++) {
                                        vec2 pos = descartesPosition(c1.xy, c2.xy, c3.xy, 
                                                                    c1.w, c2.w, c3.w, k4, posSol == 0);

                                        if (isValidNewCircle(pos, r4, k4, c1, c2, c3, nc)) {
                                            fragColor.x = numCircles + 1.0;
                                            fragColor.y = mod(tripletCounter + float(search + 1), float(maxTriplets));
                                            found = true;
                                        }
                                    }
                                }
                            }

                            currentIdx++;
                        }
                    }
                }
            }

            // If we didn't find anything, still increment counter and wrap
            if (!found) {
                int maxTriplets = (nc * (nc-1) * (nc-2)) / 6;
                fragColor.y = mod(tripletCounter + float(maxSearches), float(max(1, maxTriplets)));
            }
        }
        return;
    }

    // WRITE NEW CIRCLE
    vec4 metadata = texelFetch(iChannel0, ivec2(0, 0), 0);
    float prevNumCircles = metadata.x;
    float tripletCounter = metadata.y;

    if (coord.y == int(prevNumCircles) + 1) {

        int nc = int(prevNumCircles);
        int maxSearches = 50;
        int maxTriplets = (nc * (nc-1) * (nc-2)) / 6;

        for (int search = 0; search < maxSearches; search++) {
            int tripletIdx = int(tripletCounter + float(search)) % max(1, maxTriplets);
            int currentIdx = 0;

            for (int i = 1; i <= nc && i < 40; i++) {
                for (int j = i+1; j <= nc && j < 40; j++) {
                    for (int k = j+1; k <= nc && k < 40; k++) {

                        if (currentIdx == tripletIdx) {

                            vec4 c1 = texelFetch(iChannel0, ivec2(0, i), 0);
                            vec4 c2 = texelFetch(iChannel0, ivec2(0, j), 0);
                            vec4 c3 = texelFetch(iChannel0, ivec2(0, k), 0);

                            for (int curvSol = 0; curvSol < 2; curvSol++) {
                                float k4 = descartesCurvature(c1.w, c2.w, c3.w, curvSol == 0);

                                if (abs(k4) < 0.01 || abs(k4) > 1000.0) continue;

                                float r4 = abs(1.0 / k4);

                                for (int posSol = 0; posSol < 2; posSol++) {
                                    vec2 pos = descartesPosition(c1.xy, c2.xy, c3.xy, 
                                                                c1.w, c2.w, c3.w, k4, posSol == 0);

                                    if (isValidNewCircle(pos, r4, k4, c1, c2, c3, nc)) {
                                        fragColor = vec4(pos, r4, k4);
                                        return;
                                    }
                                }
                            }
                        }

                        currentIdx++;
                    }
                }
            }
        }
    }
}

