void mainImage0( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);
    float zoom = iMouse.z!=0.?pow((iMouse.x/iResolution.x)*8., 2.5):12.;
    uv *= zoom;

    float f = evalF(uv);

    // Check for zero crossings with neighboring pixels
    float pixelSize = zoom / min(iResolution.x, iResolution.y);
    vec2 dx = vec2(pixelSize, 0.0);
    vec2 dy = vec2(0.0, pixelSize);

    float fx_plus = evalF(uv + dx);
    float fx_minus = evalF(uv - dx);
    float fy_plus = evalF(uv + dy);
    float fy_minus = evalF(uv - dy);

    // Check if zero line crosses through this pixel
    bool zeroCrossing = (f * fx_plus < 0.0) || (f * fx_minus < 0.0) || 
                        (f * fy_plus < 0.0) || (f * fy_minus < 0.0);

    // Also check diagonal neighbors for better coverage
    float fxy_pp = evalF(uv + dx + dy);
    float fxy_pm = evalF(uv + dx - dy);
    float fxy_mp = evalF(uv - dx + dy);
    float fxy_mm = evalF(uv - dx - dy);

    zeroCrossing = zeroCrossing || (f * fxy_pp < 0.0) || (f * fxy_pm < 0.0) ||
                   (f * fxy_mp < 0.0) || (f * fxy_mm < 0.0);

    // Compute distance to zero for smooth transitions
    float dist = abs(f);
    if (zeroCrossing) {
        dist = 0.0; // Force to zero if crossing detected
    }



    // Mode A: Simple zero contour
    vec3 colorA = mix(vec3(0.1, 0.1, 0.2), vec3(1.0, 0.5, 0.0), smoothstep(0.05, 0.0, abs(f)));

    // Mode B: Grid-based with crossing detection
    vec2 grid = abs(fract(uv) - 0.5);
    float gridLine = smoothstep(0.02, 0.0, min(grid.x, grid.y));
    float curve = zeroCrossing ? 1.0 : smoothstep(0.03, 0.0, dist);
    vec3 bgColor = vec3(0.05, 0.05, 0.1);
    vec3 gridColor = vec3(0.2, 0.2, 0.3);
    vec3 curveColor = vec3(1.0, 0.8, 0.2);
    vec3 colorB = mix(bgColor, gridColor, gridLine * 0.5);
    colorB = mix(colorB, curveColor, curve);

    // Mode C: Contour with positive/negative regions
    float contour = sin(f * 20.0) * 0.5 + 0.5;
    vec3 colorC;
    if (f > 0.0) {
        colorC = mix(vec3(0.2, 0.1, 0.4), vec3(1.0, 0.3, 0.1), contour);
    } else {
        colorC = mix(vec3(0.1, 0.2, 0.4), vec3(0.1, 0.7, 1.0), contour);
    } 
    colorC = mix(colorC, vec3(1.0), smoothstep(0.02, 0.0, abs(f)));

    // Mode D: Heat map visualization
    vec3 colorD = vec3(0.5) + 0.5 * cos(6.28 * f + vec3(0, 1, 2));
    curve = smoothstep(0.03, 0.0, abs(f));
    colorD = mix(colorD, vec3(1.0), curve);

    // Blend modes based on mouse Y position
    float mouseY = iMouse.z != 0. ? iMouse.y / iResolution.y : 0.25;
    float blendParam = mouseY * 3.0; 
    vec3 finalColor;
    if (blendParam < 1.0) {
        finalColor = mix(colorA, colorB, smoothstep(0.0, 1.0, blendParam));
    } else if (blendParam < 2.0) {
        finalColor = mix(colorB, colorC, smoothstep(1.0, 2.0, blendParam));
    } else {
        finalColor = mix(colorC, colorD, smoothstep(2.0, 3.0, blendParam));
    }
    fragColor = vec4(finalColor, 1.0);
}

