// Keep all the original Ball struct and helper functions from the shader

#define inf              -1.
#define MAX_ITER         50
#define PI               3.141592653
#define L2(x)            dot(x, x)
#define L2XY(x, y)       dot(x - y, x - y)
#define ZOOM             2.

bvec2 displayRealBalls = bvec2(1, 1);

// a1 = 3, a2 = 4, a3 = 2, a4 = 2 gives Mcleos f(3,6).
// Vertical mirror if a3 = a4
const float a1 = 3.;
const float a2 = 4.;
const float a3 = 2.;
const float a4 = 4.;

// For spheres n is the center, r is the radius
// For planes n is the normal vector, r is the distance between the plane and the origin
// if invert is true then the inside/outside of the sphere is interchanged (not used in this program)
// if hasRealBall is true then this virtual ball has a real ball correspondes to it
struct Ball {
    bool isplane;
    vec2 n;
    float r;
    bool invert;
    bool isRealBall;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[3] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[1] clusters;

vec2 T;

Ball defaultBall() {
    return Ball(false, vec2(0, 0), 1., false, false);
}

// Distance from a point to a ball
float sdistanceToBall(vec2 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec3(p, 1), vec3(B.n, B.r));
        return k;
    }
    else {
        float k = length(p - B.n) - B.r;
        return k;
    }
}

Ball from_plane(vec2 n, float d) {
    return Ball(true, n, d, false, false);
}

Ball from_sphere(vec2 cen, float r) {
    return Ball(false, cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}

float dihedral(float x) {
    return x == inf ? 1. : cos(PI / x);
}


void translate(inout vec2 p, vec2 t) {
    p.x = mod(p.x, t.x);
}

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec2 p, Ball B, inout float scale) {
    if (B.isplane) {
        float k = dot(vec3(p, 1), vec3(B.n, B.r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * B.n;
        return false;
    }
    else {
        vec2 cen = B.n;
        float r = B.r;
        vec2 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1. && B.invert) || (k > 1. && !B.invert) )
            return true;
        p = k * q + cen;
        scale *= k;
        return false;
    }
}


// return distance to the scene, and get the index of the real ball hitted
float DE(vec2 p, inout int index) {
    Ball Bxy;

    float scale = 3.;
    for (int i = 0; i < MAX_ITER; i++) {
        bool cond = true;

        Bxy = coclusters[0];
        cond = cond && try_reflect(p, Bxy, scale);

        translate(p, T);
        Bxy = coclusters[1];
        cond = cond && try_reflect(p, Bxy, scale);

        translate(p, T);        
        Bxy.n = coclusters[1].n + T;
        cond = cond && try_reflect(p, Bxy, scale);

        translate(p, T);
        Bxy = coclusters[2];
        cond = cond && try_reflect(p, Bxy, scale);

        if (cond)
            break;
    }

    index = 0;

    //float d1 = sdistanceToBall(p, clusters[0]);
    //float d2 = sdistanceToBall(p, clusters[1]);
    //d1 = abs(d1); d2 = abs(d2);
    translate(p, T);
    Bxy = clusters[0];
    float d = sdistanceToBall(p, Bxy);
    //d=abs(d);
    return d / scale;
}



void init() {   
    Ball B0 = from_plane(vec2(0, -1.), 0.);

    Ball B1 = from_sphere(vec2(0., -1.), 1./cos(PI/a1));
    float c2=sqrt(1./cos(PI/a1)/cos(PI/a1) + 1./cos(PI/a2)/cos(PI/a2) + 2.*cos(PI/a3)/cos(PI/a1)/cos(PI/a2));
    Ball B2 = from_sphere(vec2(c2, -1.), 1./cos(PI/a2));
    float c3=sqrt(1./cos(PI/a1)/cos(PI/a1) + 1./cos(PI/a2)/cos(PI/a2) + 2.*cos(PI/a4)/cos(PI/a1)/cos(PI/a2));
    T = vec2(c2+c3,0);

    invertBall(B1);
    invertBall(B2);
    coclusters = Ball[3] (B0,B1,B2);

    clusters[0] = from_plane(vec2(0., -1.), -1.);
}


float map(vec2 p, inout int index) {
    vec2 mouse = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    float k = 1.0;
    //if (iMouse.z > 0.0) {
        p -= mouse;
        k = dot(p,p);
        p /= k;
        p += mouse;
    //}
    const float strong_factor = 4.;
    return DE(p, index) * strong_factor;
}

vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 post_process(vec3 col, vec2 uv) {
  col = pow(clamp(col, 0., 1.), vec3(1.0/2.2)); 
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *= 0.5 + 0.5*pow(19.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.7);
  return col;
}


// Invert SDF within a circular or rectangular boundary
float invertSDFWithinBounds(vec2 p, float sdf) {
    // Define boundary - you can use different shapes
    float boundaryDist = length(p) - 1.5;  // Circular boundary, radius 1.5
    // Or rectangular: float boundaryDist = max(abs(p.x), abs(p.y)) - 1.5;

    // If inside boundary, invert the SDF
    if (boundaryDist < 0.0) {
        return -sdf;
    }

    // Outside boundary, keep original
    return sdf;
}

// Smooth version that blends at the boundary
float invertSDFWithinBoundSmooth(vec2 p, float sdf, float boundaryRadius) {
    float boundaryDist = length(p) - boundaryRadius;

    // Create a smooth blend factor (0 = inverted, 1 = original)
    float blendWidth = 0.1;  // How smooth the transition is
    float blend = smoothstep(-blendWidth, blendWidth, boundaryDist);

    return mix(-sdf, sdf, blend);
}

// Apply to your circle packing
float circlePacking2DInverted(vec2 p) {
    init();
    int index;
    float sdf = map(p, index);

    // Choose your boundary size
    float boundaryRadius = 1.5;
    return invertSDFWithinBoundSmooth(p, sdf, boundaryRadius);
}

// Or with a hard boundary (no smoothing)
float circlePacking2DInvertedHard(vec2 p) {
    init();
    int index;
    float sdf = map(p, index);

    float boundaryDist = length(p) - 1.5;

    if (boundaryDist < 0.0) {
        return -sdf;  // Inverted inside
    }
    return sdf;  // Original outside
}
float circlePacking2DInvertedNatural(vec2 p) {
    init();
    int index;
    float sdf = map(p, index);

    // Use the natural outer bound from the algorithm
    float outerBound = (1./cos(PI/a1) + tan(PI/a1)) / 3.77 * ZOOM;
    float boundaryDist = length(p) - outerBound;

    if (boundaryDist < 0.0) {
        return -sdf;
    }

    return sdf;
}
// Then use it in extrusion
float extrudedPackingInverted(vec3 p, float height) {
    float d2D = circlePacking2DInvertedNatural(p.xz);
    float dExtrude = abs(p.y) - height * 0.5;
    return max(d2D, dExtrude);
}

// First, let's wrap the 2D SDF in a clean function
float circlePacking2D(vec2 p) {
    init();  // Initialize the mirror configuration
    int index;
    return map(p, index);  // Returns distance to the mesh/web
}

// Basic extrusion - uniform height
float extrudedPacking(vec3 p, float height) {
    float d2D = circlePacking2D(p.xz);  // or p.xy depending on orientation
    float dExtrude = abs(p.y) - height * 0.5;
    return max(d2D, dExtrude);
}

// More interesting: varying thickness along height
float extrudedPackingVarying(vec3 p, float height) {
    float t = p.y / height + 0.5;  // Normalize to [0,1]

    // Scale the pattern based on height
    float scale = mix(1.0, 0.7, t);  // Tapers from bottom to top
    float d2D = circlePacking2D(p.xz / scale) * scale;

    // You could also add offset/bias to make the mesh thicker/thinner
    float thickness = mix(0.0, -0.02, sin(t * 3.14159));  // Bulges in middle
    d2D += thickness;

    float dExtrude = abs(p.y) - height * 0.5;
    return max(d2D, dExtrude);
}

// Twisted extrusion
float extrudedPackingTwisted(vec3 p, float height) {
    float t = p.y / height + 0.5;
    float angle = t * 3.14159 * 0.5;  // 90 degree twist

    // Rotate xz plane based on height
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 q = rot * p.xz;

    float d2D = circlePacking2D(q);
    float dExtrude = abs(p.y) - height * 0.5;
    return max(d2D, dExtrude);
}

// Advanced: morphing between different configurations
float extrudedPackingMorph(vec3 p, float height) {
    float t = p.y / height + 0.5;

    // You could reinitialize with different parameters at different heights
    // This would require modifying the init() function to take parameters

    // For now, let's use a simpler approach - blend scales
    float scale1 = 1.0;
    float scale2 = 1.0;
    float s = mix(scale1, scale2, smoothstep(0.2, 0.8, t));

    float d2D = circlePacking2D(p.xz / s) * s;

    // Add some wave distortion
    //d2D += sin(p.y * 10.0) * 0.01;

    float dExtrude = abs(p.y) - height * 0.5;
    return max(d2D, dExtrude);
}

float extrude(vec3 p, float height) {
    return extrudedPackingMorph(p,height);
}

// Full raymarching setup
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera setup
    //vec3 ro = vec3(2.0 * sin(iTime), 1.5, 2.0 * cos(iTime));
     vec3 ro = vec3(3.5, 3.5, 5.0);
    vec3 ta = vec3(0.0, 0.0, 0.0);
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww, vec3(0, 1, 0)));
    vec3 vv = normalize(cross(uu, ww));
    vec3 rd = normalize(uv.x * uu + uv.y * vv + 1.5 * ww);

    // Raymarching
    float t = 0.0;
    for(int i = 0; i < 120; i++) {
        vec3 p = ro + rd * t;
        float d = extrude(p, 2.0);  // Try different variations!

        if(d < 0.001 || t > 30.0) break;
        t += d * 0.4;  // Conservative marching
    }

    // Shading
    vec3 col = vec3(0.0);
    if(t < 30.0) {
        vec3 p = ro + rd * t;

        // Normal calculation
        vec2 e = vec2(0.001, 0.0);
        vec3 n = normalize(vec3(
            extrude(p + e.xyy, 2.0) - extrude(p - e.xyy, 2.0),
            extrude(p + e.yxy, 2.0) - extrude(p - e.yxy, 2.0),
            extrude(p + e.yyx, 2.0) - extrude(p - e.yyx, 2.0)
        ));

        // Simple lighting
        vec3 lightPos = vec3(2.0, 4.0, 3.0);
        vec3 l = normalize(lightPos - p);
        float diff = max(dot(n, l), 0.0);
        float spec = pow(max(dot(reflect(-l, n), -rd), 0.0), 32.0);

        col = vec3(0.8, 0.7, 0.6) * diff + vec3(1.0) * spec * 0.5;
        col *= 0.5 + 0.5 * exp(-0.1 * t);  // Fog
    }

    fragColor = vec4(col, 1.0);
}

// Update - now works with one buffer instead of three. I'm not sure it can be reduced further.

/*
 I wanted to put this together to see if it would work, I still need to test it out properly.

 I'm not 100% sure this does what I think it does, or will give us any performance increase, but the output looks nice.

 The idea is to get fwidth from two 2x2 cells, using dx/dy in four directions, which might be as good as usign four cells.

 The idea being that dx/dy gives us two directions out of four, so we only need to double up to get a complete picture.
 (Fwidth on it's own gives us one direction of four, so we would need four of them for a complete picture.)

 Start by getting dFdx and dFdy from two images
 One has a 1,1 diagonal offset, so the retreived dx and dy go up and down and left and right
 the 2x2 fwidth cells are offset from each other. Each pixel is now in two 'fwidth cells'
 But the dx/dy now run in four directions, which I think means we can calculate four fWidth cells for the price of two:

   F3  dy1  F1

        ^
  dx2 < P > dx1
        v

   F2  dy2  F4

 (There is no F3 or F4, but with dx/dy in four directions it doesn't matter, does it?)


*/

float myFwidth( float xa, float ya, float xb, float yb  ){
    return abs(xa) + abs(ya) +  abs(xb) + abs(yb); // same as fwidth calculation fwidth(p) = abs(dFdx(p)) + abs(dFdy(p)) but doubled up
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 imageA = texelFetch(iChannel0,  ivec2(fragCoord), 0); // first image
    vec4 imageB = texelFetch(iChannel0, ivec2(fragCoord)+ivec2(-1,-1), 0); // second image, adjusted for offset

    float total = myFwidth( imageA.r, imageA.g, imageB.b, imageB.a); //calculate total combined fwidth

    fragColor = vec4(pow( total/2., 0.45));

    if(iMouse.z>0.){
        fragColor = 1.-fragColor;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // this is the first image for dFdx in red and dFdy in green
    vec4 imageColorA = texelFetch(iChannel0, ivec2(fragCoord), 0);
    // this is the second image for dFdx in blue and dFdy in alpha, with a 1,1 diagonal offset
    vec4 imageColorB = texelFetch(iChannel0, ivec2(fragCoord)+ivec2(1,1), 0);

    fragColor = vec4(dFdx(imageColorA.r), dFdy(imageColorA.r), dFdx(imageColorB.r), dFdy(imageColorB.r));
}

