#define R iResolution

void mainImage(out vec4 O, vec2 I)
{
    if(iFrame < 1)
    {
        O = texture(iChannel0, I / R.xy);
        return;
    }


    if(iFrame<100){
    int b = iFrame - 1;
    int i = int(I.x);

    int h = int((sqrt(float(8 * b + 1)) - 1.0) / 2.0);
    int k = (h * (h + 3) / 2 - b) % (h + 1);
    int j = i ^ (k < h ? 1 << k : (2 << k) - 1);
    vec4 A = texelFetch(iChannel1, ivec2(i, I.y), 0); if (j >= int(R.x)) { O = A; return; }
    vec4 B = texelFetch(iChannel1, ivec2(j, I.y), 0);
    float va = dot(A.xyz, vec3(0.2126, 0.7152, 0.0722));
    float vb = dot(B.xyz, vec3(0.2126, 0.7152, 0.0722));
    O = i < j ^^ va < vb ? B : A;

    }else{
    int b = iFrame - 100;


    int h = int((sqrt(float(8 * b + 1)) - 1.0) / 2.0);
    int k = (h * (h + 3) / 2 - b) % (h + 1);

    int iy = int(I.y);
    int jy = iy ^ (k < h ? 1 << k : (2 << k) - 1);
    vec4 Ay = texelFetch(iChannel1, ivec2(I.x, iy), 0); if (jy >= int(R.y)) { O = Ay; return; }
    vec4 By = texelFetch(iChannel1, ivec2(I.x, jy), 0);
    float vay = dot(Ay.xyz, vec3(0.2126, 0.7152, 0.0722));
    float vby = dot(By.xyz, vec3(0.2126, 0.7152, 0.0722));
    O = iy < jy ^^ vay < vby ? By : Ay;}

}

