#define WAVEFORM_SAMPLE_COUNT 512 //TODO: derive this from the actual ShaderToyAudioTexture constants??? it doesnt even need to 512?
#define WAVEFORM_SAMPLE_COUNT_F float(WAVEFORM_SAMPLE_COUNT)
#define AUDIO_TEXTURE_WAVEFORM_ROW 1.0 //TODO: derive this from the actual ShaderToyAudioTexture constants

#define UNUSED_FEEDBACK_CHANNEL 0.0
#define WAVEFORM_SEGMENT_CENTERING_OFFSET 0.5

#define WAVEFORM_SAMPLES_PER_SEGMENT (WAVEFORM_SAMPLE_COUNT / DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS)
#define WAVEFORM_SAMPLES_PER_SEGMENT_F (WAVEFORM_SAMPLE_COUNT_F / DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS_F)

#define TARGET_BLEND_INTENSITY_BETWEEN_ENVELOPE_INJECTION_SNAPSHOTS 0.6
#define INJECTION_INTERVAL 0.1 //e.g. every 0.1 second: open the injection window

#define INJECTION_WINDOW 0.8 // e.g. when injection occurs allow 80% of 0.1 second = 0.08 seconds of buffered waveforms to enter the envelope???

#define PROPAGATION_RATE_FRAME 0.5 // coupled to frame rate?????  e.g. for 30fps, every 15 frames (50% of 30fps) = 15/30 fps -> propagation occurs every 0.5 seconds, i.e. ~2 times per second. propagate the injected waveforms upwards through the envelope snapshots

float compute_normalized_sample_coordinate_for_downscaled_segment_in_1D(float segment_index, float sample_index) {
    float sample_coordinate_on_1D_x_axis = segment_index * WAVEFORM_SAMPLES_PER_SEGMENT_F + sample_index + WAVEFORM_SEGMENT_CENTERING_OFFSET;
    return sample_coordinate_on_1D_x_axis / WAVEFORM_SAMPLE_COUNT_F; 
}

float sample_audio_texture_waveform_data(int segment_index) {
    float accumulated_amplitude = 0.0;
    for (int sample_index = 0; sample_index < WAVEFORM_SAMPLES_PER_SEGMENT; sample_index++) {
        float segment_index_in_continious_space = float(segment_index);
        float sample_index_in_continious_space = float(sample_index);
        float normalized_sample_coordinate_for_downscaled_segment_in_1D = compute_normalized_sample_coordinate_for_downscaled_segment_in_1D(segment_index_in_continious_space, sample_index_in_continious_space);
        vec2 sample_coordinates = vec2(normalized_sample_coordinate_for_downscaled_segment_in_1D, AUDIO_TEXTURE_WAVEFORM_ROW);
        float amplitude_value_at_sample_coordinates = texture(iChannel1, sample_coordinates).r;
        accumulated_amplitude += abs(amplitude_value_at_sample_coordinates);
    }
    return accumulated_amplitude / WAVEFORM_SAMPLES_PER_SEGMENT_F;
}

float propagate_envelope_injection_snapshot_upwards(vec2 uv, float y_shift) {
    vec4 envelope_fragment = texture(iChannel0, uv + vec2(0.0, y_shift));
    return envelope_fragment.r;
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
    vec2 uv = frag_coord.xy / iResolution.xy;
    float uv_height_per_envelope_snapshot = 1.0 / float(NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS);
    if (uv.y < 1.0 - uv_height_per_envelope_snapshot) {
        float envelope_fragment = propagate_envelope_injection_snapshot_upwards(uv, PROPAGATION_RATE_FRAME * uv_height_per_envelope_snapshot);
        frag_color = vec4(envelope_fragment, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL);
    } else {
        float current_envelope = texture(iChannel0, uv).r;
        float normalized_time_since_last_injection = fract(iTime / INJECTION_INTERVAL);
        if (normalized_time_since_last_injection < INJECTION_WINDOW) {
            int segment_index_in_discrete_space = int(floor(uv.x * DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS_F));
            float next_envelope = sample_audio_texture_waveform_data(segment_index_in_discrete_space);
            float injection_blend_intensity_coefficient = 1.0;
            float t = normalized_time_since_last_injection / INJECTION_WINDOW;

            float smooth_injection = 0.5 * (1.0 - cos(6.28318 * t)); //TODO: I dont like this, but it kind of helps with smoothing?
            float effective_injection_blend = smooth_injection * injection_blend_intensity_coefficient * TARGET_BLEND_INTENSITY_BETWEEN_ENVELOPE_INJECTION_SNAPSHOTS;
            float blended_envelope = mix(current_envelope, next_envelope, effective_injection_blend);
            frag_color = vec4(blended_envelope, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL);
        } else {
            frag_color = vec4(current_envelope, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL, UNUSED_FEEDBACK_CHANNEL);
        }
    }
}

#define DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS 64
#define DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS_F 64.
#define NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS 1
#define AMPLITUDE_SCALE 200.0
#define ISOMETRIC_ZOOM 6.0
#define ROW_SPACING 8.0

vec2 project_envelope(vec3 envelope_coordinate) {
    float segment_index   = envelope_coordinate.x;
    float envelope_value  = envelope_coordinate.y;
    float history_row     = envelope_coordinate.z;
    float effective_row   = history_row * ROW_SPACING;
    float projected_x = segment_index - effective_row;
    float projected_y = (segment_index + effective_row) * 0.5 - envelope_value * AMPLITUDE_SCALE;
    return vec2(projected_x, projected_y) * ISOMETRIC_ZOOM;
}

vec2 compute_envelope_grid_center() {
    vec2 projected_bottom_left  = project_envelope(vec3(0.0, 0.0, 0.0));
    vec2 projected_bottom_right = project_envelope(vec3(float(DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS - 1), 0.0, 0.0));
    vec2 projected_top_left     = project_envelope(vec3(0.0, 1.0, float(NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS - 1)));
    vec2 projected_top_right    = project_envelope(vec3(float(DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS - 1), 1.0, float(NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS - 1)));

    vec2 minimum_corner = min(min(projected_bottom_left, projected_bottom_right),
                              min(projected_top_left, projected_top_right));
    vec2 maximum_corner = max(max(projected_bottom_left, projected_bottom_right),
                              max(projected_top_left, projected_top_right));
    return (minimum_corner + maximum_corner) * 0.5;
}

float distance_to_line(vec2 pixel_coordinate, vec2 line_start, vec2 line_end) {
    vec2 line_vector = line_end - line_start;
    float line_length_squared = dot(line_vector, line_vector);
    float projection_factor = dot(pixel_coordinate - line_start, line_vector) / line_length_squared;
    float clamped_projection_factor = clamp(projection_factor, 0.0, 1.0);
    vec2 closest_point = line_start + clamped_projection_factor * line_vector;
    return distance(pixel_coordinate, closest_point);
}

#define LINE_RENDER_WIDTH 0.75
#define LINE_RENDER_MARGIN (LINE_RENDER_WIDTH * 2.0) // amount of thickness (in fragment size/single pixel) that surrounds both sides of the wave signal lines
#define MAX_DISTANCE 1e6 // some stupid number to just initialize the min distance to closest wave signal logic

#define WHITE vec4(1.0, 1.0, 1.0, 1.0)
#define BLACK vec4(0.0, 0.0, 0.0, 1.0)


float get_envelope_from_buffer(int history_row, int segment_index) {
    float texture_v = 1.0 - (float(history_row) + 0.5) / float(NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS);
    float texture_u = (float(segment_index) + 0.5) / float(DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS);
    return texture(iChannel0, vec2(texture_u, texture_v)).r;
}

vec2 project_centered_envelope(vec3 envelope_coordinate) {
    vec2 raw_projected_coordinate = project_envelope(envelope_coordinate);
    vec2 grid_center = compute_envelope_grid_center();
    vec2 screen_center = iResolution.xy * 0.5;
    return raw_projected_coordinate + (screen_center - grid_center);
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
    float closest_distance = MAX_DISTANCE;
   // for (int history_row = 0; history_row < NUMBER_OF_HISTORICAL_ENVELOPE_SNAPSHOTS; history_row++) {
        for (int segment_index = 0; segment_index < DOWNSCALED_TARGET_NUMBER_OF_WAVEFORM_SEGMENTS - 1; segment_index++) {
            float envelope_left = get_envelope_from_buffer(0, segment_index);
            float envelope_right = get_envelope_from_buffer(0, segment_index + 1);

            vec3 envelope_coordinate_left = vec3(float(segment_index), envelope_left, 0.);
            vec3 envelope_coordinate_right = vec3(float(segment_index + 1), envelope_right, 0.);

            vec2 screen_position_left = project_centered_envelope(envelope_coordinate_left);
            vec2 screen_position_right = project_centered_envelope(envelope_coordinate_right);

            float distance_to_wave_line = distance_to_line(frag_coord.xy, screen_position_left, screen_position_right);
            closest_distance = min(closest_distance, distance_to_wave_line);
        }
   // }

    float intensity = 1.0 - smoothstep(LINE_RENDER_WIDTH, LINE_RENDER_MARGIN, closest_distance);
    vec3 oldb = texture(iChannel1, (frag_coord+vec2(30., -20.))/iResolution.xy, 0.).rgb;
    frag_color = vec4(oldb+vec3(intensity), 1.0);
}

// fwidth, in red, is draggable.
