void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ir = iResolution;

    vec3 bounds = vec3(ir.y*0.8, fragCoord.x/(ir.x/ir.y), ir.y - fragCoord.x/(ir.x/ir.y)); // dividing lines

    if( iMouse.z>0. || iMouse.x>0. || iMouse.y>0.) { bounds.x = iMouse.y;}

    vec3 iA = texture(iChannel0, fragCoord/iResolution.xy, 0.).rgb; // image texture

    float ibw = (iA.r+iA.b+iA.g)/3.; // black and white, so we're only dealing with oen value, but you can use types other than float

    float enhance = 100.; // This is for very soft images, turn down to under 10 for other textures.

    vec3 col = vec3(ibw)/enhance; 

    if(fragCoord.y> bounds.x) { col.r+=fwidth(ibw)/2.5; } // set red channel to show fwidth, attenuate the red because it's too bright

    if(fragCoord.y< bounds.y) { col.g+= /*abs*/(dFdx(ibw)); } // set green channel to show dFdx 

    if(fragCoord.y< bounds.z) { col.b+= /*abs*/(dFdy(ibw)); } // set blue channel to show dFdy

    col*=enhance;

    if(floor(fragCoord.y)==floor(bounds.x)){col.r=1.;} 
    if(floor(fragCoord.y)==floor(bounds.y)){col.g=1.;} 
    if(floor(fragCoord.y)==floor(bounds.z)){col.b=1.;} 

    fragColor = vec4(col, 1.);
}

// Line strip with width intersecting unit square
// Mouse controls line position and angle

float sdLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

float getIntersectionArea(vec2 linePoint, vec2 lineDir, float halfWidth) {
    // Calculate intersection area between strip and unit square
    // This is simplified - doing proper clipping in a shader is complex
    // So we'll sample the area instead

    float area = 0.0;
    const int SAMPLES = 100;
    float step = 1.0 / float(SAMPLES);

    for(int i = 0; i < SAMPLES; i++) {
        for(int j = 0; j < SAMPLES; j++) {
            vec2 samplePoint = vec2(float(i) * step, float(j) * step);

                // Check if point is in strip
                // Distance from point to line
                vec2 lineNormal = vec2(-lineDir.y, lineDir.x);
                float dist = abs(dot(samplePoint - linePoint, lineNormal));

                if(dist <= halfWidth) {
                    area += step * step;
                }
        }
    }

    return area;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 coord = (uv - 0.5) * aspect * 2.5;

    // Mouse position
    vec2 mouse = (iMouse.xy / iResolution.xy - 0.5) * aspect * 2.5;
    if(iMouse.z < 0.5) mouse = vec2(0.0);

    // Line parameters
    float angle = iTime * 0.5;
    vec2 lineDir = vec2(cos(angle), sin(angle));
    vec2 linePoint = mouse;
    float lineWidth = 0.7;
    float halfWidth = lineWidth * 0.5;



    // Render
    vec3 col = vec3(0.1);

    // Draw unit square
    vec2 squarePos = coord + vec2(0.5, 0.5);
    if(squarePos.x >= 0.0 && squarePos.x <= 1.0 && 
       squarePos.y >= 0.0 && squarePos.y <= 1.0) {
        col = vec3(0.3, 0.3, 0.4);
    }

    // Draw line strip
    vec2 lineNormal = vec2(-lineDir.y, lineDir.x);
    float distToLine = abs(dot(coord - linePoint, lineNormal));

    if(distToLine <= halfWidth) {
        if(squarePos.x >= 0.0 && squarePos.x <= 1.0 && 
           squarePos.y >= 0.0 && squarePos.y <= 1.0) {
            col = vec3(0.8, 0.4, 0.4);
        } else {
            col = vec3(0.5, 0.5, 0.6);
        }
    }

    //UI
    vec2 barPos = vec2(10., iResolution.y - 50.0);
    vec2 barSize = vec2(iResolution.x - 20., 40.0);

    if(fragCoord.x > barPos.x && fragCoord.x < barPos.x + barSize.x &&
       fragCoord.y > barPos.y && fragCoord.y < barPos.y + barSize.y) {
        col = vec3(0.5);
        // Calculate intersection area
        float area = getIntersectionArea(linePoint + vec2(0.5), lineDir, halfWidth);
        float fillWidth = barSize.x * area;
        if(fragCoord.x < barPos.x + fillWidth) {
            col = vec3(0.0, 0.3, 0);
        }
    }
    fragColor = vec4(col, 1.0);
}

// Main Image: Display all 4 quadrants
// iChannel0 = Webcam
// iChannel1 = Buffer A (Cr, Cg, Hue)
// iChannel2 = Buffer B (pixel delta)
// iChannel3 = Buffer C (split: current | previous)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec2 quadrant = floor(uv * 2.0);
    vec2 localUV = fract(uv * 2.0);

    vec3 col;

    if (quadrant.x == 0.0 && quadrant.y == 1.0) {
        // Top-left: Original
        col = texture(iChannel0, localUV).rgb;
    }
    else if (quadrant.x == 1.0 && quadrant.y == 1.0) {
        // Top-right: Chromaticity + Hue
        col = texture(iChannel1, localUV).rgb;
    }
    else if (quadrant.x == 0.0 && quadrant.y == 0.0) {
        // Bottom-left: Pixel Delta (Image 3)
        col = texture(iChannel2, localUV).rgb * 5.0;
    }
    else {
        // Bottom-right: TIME DELTA of Pixel Delta (Image 4)
        // Read left half (current) and right half (previous) from Buffer C
        vec3 currentPixelDelta = texture(iChannel3, localUV * vec2(0.5, 1.0)).rgb;
        vec3 previousPixelDelta = texture(iChannel3, localUV * vec2(0.5, 1.0) + vec2(0.5, 0.0)).rgb;

        vec3 timeDelta = abs(currentPixelDelta - previousPixelDelta);

        col = ((timeDelta * 10.)-texture(iChannel2, localUV).rgb * 15.0);
    }

    // Grid lines
    if (abs(uv.x - 0.5) < 0.002 || abs(uv.y - 0.5) < 0.002) {
        col = vec3(0.2);
    }

    fragColor = vec4(col, 1.0);
}

// Helper function
vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0/3.0, 2.0/3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// Buffer A: Convert to Cr, Cg, Hue
// iChannel0 = Webcam
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec3 rgb = texture(iChannel0, uv).rgb;

    float sum = rgb.r + rgb.g + rgb.b + 0.001;
    float cr = rgb.r / sum;
    float cg = rgb.g / sum;

    vec3 hsv = rgb2hsv(rgb);
    float hue = hsv.x;

    fragColor = vec4(cr, cg, hue, 1.0);
}

// Buffer B: Spatial delta of Cr,Cg,Hue
// iChannel0 = Buffer A
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 pixelSize = 1.0 / iResolution.xy;

    vec3 center = texture(iChannel0, uv).rgb;
    vec3 right = texture(iChannel0, uv + vec2(pixelSize.x, 0)).rgb;
    vec3 down = texture(iChannel0, uv + vec2(0, pixelSize.y)).rgb;

    vec3 deltaX = abs(center - right);
    vec3 deltaY = abs(center - down);
    vec3 delta = max(deltaX, deltaY);

    fragColor = vec4(delta, 1.0);
}

// Buffer C: Left half = current Buffer B, Right half = previous Buffer B
// iChannel0 = Buffer C (itself - for previous data)
// iChannel1 = Buffer B (current data)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    if (uv.x < 0.5) {
        // LEFT HALF: Store current Buffer B
        vec2 sampleUV = vec2(uv.x * 2.0, uv.y);
        fragColor = texture(iChannel1, sampleUV);
    } else {
        // RIGHT HALF: Store what was in left half last frame (previous Buffer B)
        vec2 previousUV = vec2((uv.x - 0.5) * 2.0, uv.y);
        vec2 leftSideUV = vec2(previousUV.x * 0.5, previousUV.y);
        // vec2 rightSideUV = leftSideUV + vec2(0.5,0);
        if(iFrame%5 == 0){
        fragColor = texture(iChannel0, leftSideUV);}// Read from left half of self
        else {
        fragColor = texture(iChannel0, uv, 0.);
        }
    }
}

