float sdCylinder(vec3 p, float r, float h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}


float map(vec3 p) {
    // Configuration
    float spacingA = 0.45;        // Distance between rings
    float spacingB = 0.5;         // Distance between rods
    float rodRadius = 0.07;       // Radius of each rod
    float rodHeight = 1.5;        // Height of each rod
    int numRings = 12;            // Maximum ring index

    // Wave configuration
    float waveTime = iTime * 0.9;
    float maxRadius = (float(numRings) + 0.5) * spacingA;
    float waveRadius = maxRadius * 0.5;
    float waveAmplitude = 1.5;
    float waveWidth = 2.5;

    // Two wave center positions
    vec2 waveCenter1 = vec2(cos(waveTime), sin(waveTime)) * waveRadius;
    vec2 waveCenter2 = -waveCenter1;

    // Get radial distance and angle
    float radialDist = length(p.xz);
    float angle = atan(p.z, p.x);

    // Special handling for ring 0 (center ring)
    float minDist = 1e6;

    // Check ring 0 separately since it has special properties
    if (radialDist < spacingA) {
        float ringRadius = spacingA/1.7;
        float numRods = 4.0;
        float angleStep = 2.0 * 3.14159 / numRods;

        float rodIndex = floor(angle / angleStep + 0.5);
        float rodAngle = rodIndex * angleStep;

        vec3 rodCenter = vec3(cos(rodAngle) * ringRadius, 0.0, sin(rodAngle) * ringRadius);

        // no wave here in the center.

        vec3 adjustedRodCenter = rodCenter;
        minDist = sdCylinder(p - adjustedRodCenter, rodRadius, rodHeight);
    }

    // Domain repetition for rings 1 to numRings
    if (radialDist >= spacingA * 0.5) {
        // Find the nearest ring index based on radial distance
        float ring = round((radialDist - spacingA * 0.5) / spacingA);
        ring = clamp(ring, 1.0, float(numRings));

        float ringRadius = (ring + 0.5) * spacingA;
        float circumference = 2.0 * 3.14159 * ringRadius;
        float numRods = floor((circumference / spacingB + 0.5) + (circumference * 2.0) - 9.4);

        // Angular domain repetition for rods in this ring
        float angleStep = 2.0 * 3.14159 / numRods;
        float rodIndex = floor(angle / angleStep + 0.5);
        float rodAngle = rodIndex * angleStep;

        vec3 rodCenter = vec3(cos(rodAngle) * ringRadius, 0.0, sin(rodAngle) * ringRadius);

        // Calculate wave influence
        vec2 rodPos2D = rodCenter.xz;
        float dist1 = length(rodPos2D - waveCenter1);
        float dist2 = length(rodPos2D - waveCenter2);
        float minWaveDist = min(2.2, min(dist1, dist2));

        float wave = cos(minWaveDist / waveWidth * 3.14159) * 0.5 + 0.5;
        wave = smoothstep(0.0, 1.0, wave);
        float heightOffset = wave * waveAmplitude;

        vec3 adjustedRodCenter = rodCenter + vec3(0.0, heightOffset, 0.0);
        float dist = sdCylinder(p - adjustedRodCenter, rodRadius, rodHeight);
        minDist = min(minDist, dist);
    }

    return minDist;
}

vec3 calcNormal(vec3 p) {
    const vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));
}


vec3 lighting(vec3 p, vec3 normal, vec3 rayDir, vec3 lightPos) {
    vec3 lightDir = normalize(lightPos - p);
    vec3 viewDir = -rayDir;
    vec3 reflectDir = reflect(-lightDir, normal);

    float diff = max(dot(normal, lightDir), 0.0);
    //float spec = pow(max(dot(viewDir, reflectDir), 0.0), 16.0);

    vec3 ambient = vec3(0.4);
    vec3 diffuse = vec3(0.5) * diff;
   // vec3 specular = vec3(0) * spec;

    return ambient + diffuse;// + specular;
}


float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 60; i++) {
        vec3 p = ro + t * rd;
        float d = map(p);
        if (d < 0.001 || t > 50.0) break;
        t += d/2.;
    }
    return t;
}

