// Generated with SDF Builder
// Paste into Shadertoy

// SDF Primitives and Operations
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float sdCylinder(vec3 p, float r, float h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdOctahedron(vec3 p, float s) {
    p = abs(p);
    float m = p.x + p.y + p.z - s;
    vec3 q;
         if (3.0 * p.x < m) q = p.xyz;
    else if (3.0 * p.y < m) q = p.yzx;
    else if (3.0 * p.z < m) q = p.zxy;
    else return m * 0.57735027;

    float k = clamp(0.5 * (q.z - q.y + s), 0.0, s);
    return length(vec3(q.x, q.y - s + k, q.z - k));
}

float sdCone(vec3 p, vec2 c, float h) {
    float safeCos = max(c.y, 0.0001);
    vec2 q = h * vec2(c.x / safeCos, -1.0);
    vec2 w = vec2(length(p.xz), p.y);
    vec2 a = w - q * clamp(dot(w, q) / dot(q, q), 0.0, 1.0);
    vec2 b = w - q * vec2(clamp(w.x / max(q.x, 0.0001), 0.0, 1.0), 1.0);
    float k = sign(q.y);
    float d = min(dot(a, a), dot(b, b));
    float s = max(k * (w.x * q.y - w.y * q.x), k * (w.y - q.y));
    return sqrt(d) * sign(s);
}

float sdRoundCone(vec3 p, float r1, float r2, float h) {
    float b = (r1 - r2) / h;
    float a = sqrt(max(1.0 - b * b, 0.0001));
    vec2 q = vec2(length(p.xz), p.y);
    float k = dot(q, vec2(-b, a));
    if (k < 0.0) return length(q) - r1;
    if (k > a * h) return length(q - vec2(0.0, h)) - r2;
    return dot(q, vec2(a, b)) - r1;
}

float sdVerticalCapsule(vec3 p, float h, float r) {
    p.y -= clamp(p.y, 0.0, h);
    return length(p) - r;
}

float sdCutHollowSphere(vec3 p, float r, float h, float t) {
    float w = sqrt(max(r * r - h * h, 0.0));
    vec2 q = vec2(length(p.xz), p.y);
    return ((h * q.x < w * q.y) ? length(q - vec2(w, h)) : abs(length(q) - r)) - t;
}

float sdDeathStar(vec3 p2, float ra, float rb, float d) {
    float a = (ra * ra - rb * rb + d * d) / (2.0 * d);
    float b = sqrt(max(ra * ra - a * a, 0.0));
    vec2 p = vec2(p2.x, length(p2.yz));
    if (p.x * b - p.y * a > d * max(b - p.y, 0.0)) {
        return length(p - vec2(a, b));
    }
    return max(length(p) - ra, -(length(p - vec2(d, 0.0)) - rb));
}

float sdVesicaSegment(vec3 p, vec3 a, vec3 b, float w) {
    vec3 c = (a + b) * 0.5;
    vec3 ba = b - a;
    float l = length(ba);
    vec3 v = ba / max(l, 0.0001);
    vec3 rel = p - c;
    float y = dot(rel, v);
    vec2 q = vec2(length(rel - y * v), abs(y));
    float r = 0.5 * l;
    float ww = max(w, 0.0001);
    float d = 0.5 * (r * r - ww * ww) / ww;
    vec3 h = (r * q.x < d * (q.y - r)) ? vec3(0.0, r, 0.0) : vec3(-d, 0.0, d + ww);
    return length(q - h.xy) - h.z;
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

float opSmoothIntersection(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

float opXor(float d1, float d2) {
    return max(min(d1, d2), -max(d1, d2));
}

vec3 hsvToRgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 intToColor(int i) {
    float goldenRatio = 0.618033988749895;
    float hue = fract(float(i) * goldenRatio);
    float satBucket = floor(mod(float(i), 3.0));
    float valBucket = floor(mod(float(i), 4.0));
    float sat = clamp(0.7 + satBucket * 0.1, 0.0, 1.0);
    float val = clamp(0.7 + valBucket * 0.075, 0.0, 1.0);
    return hsvToRgb(vec3(hue, sat, val));
}

mat3 rotateX(float a) {
    float c = cos(a), s = sin(a);
    return mat3(1, 0, 0, 0, c, -s, 0, s, c);
}

mat3 rotateY(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, 0, s, 0, 1, 0, -s, 0, c);
}

mat3 rotateZ(float a) {
    float c = cos(a), s = sin(a);
    return mat3(c, -s, 0, s, c, 0, 0, 0, 1);
}

// === Experimental Shapes ===

// Cart2Polar for Mandelbulb
vec3 cart2polar(in vec3 st) {
    float r = length(st);
    float phi = acos(st.z / r);
    float theta = atan(st.y, st.x);
    return vec3(r, phi, theta);
}

// Mandelbulb SDF
float sdMandelbulb(in vec3 st, float power, float maxIterations) {
    vec3 zeta = st;
    float m = dot(st, st);
    float dz = 1.0;
    int iterations = int(maxIterations);

    for (int i = 0; i < 50; i++) {
        if (i >= iterations) break;

        dz = power * pow(m, 3.5) * dz + 1.0;
        vec3 sphericalZ = cart2polar(zeta);
        float newx = pow(sphericalZ.x, power) * sin(sphericalZ.y * power) * cos(sphericalZ.z * power);
        float newy = pow(sphericalZ.x, power) * sin(sphericalZ.y * power) * sin(sphericalZ.z * power);
        float newz = pow(sphericalZ.x, power) * cos(sphericalZ.y * power);
        zeta.x = newx + st.x;
        zeta.y = newy + st.y;
        zeta.z = newz + st.z;

        m = dot(zeta, zeta);
        if (m > 2.0) break;
    }

    // Distance estimation through the Hubbard-Douady potential
    return 0.25 * log(m) * sqrt(m) / dz;
}

// Supershape helpers
vec3 Spherical(vec3 pos) {
    float r = sqrt(pos.x * pos.x + pos.y * pos.y + pos.z * pos.z);
    float theta = pos.z / r;
    float phi = atan(pos.y, pos.x);
    vec3 w = vec3(r, theta, phi);
    return w;
}

float superFormula(float theta, float m, float n1, float n2, float n3, float a, float b) {
    float t1 = abs((1.0 / a) * cos(m * theta / 4.0));
    t1 = pow(t1, n2);

    float t2 = abs((1.0 / b) * sin(m * theta / 4.0));
    t2 = pow(t2, n3);

    float t3 = t1 + t2;
    float rr = pow(t3, -1.0 / n1);
    return rr;
}

float sdSupershape3D(vec3 p, float size, float m, float n1, float n2, float n3, float a, float b) {
    float r = Spherical(p).x;
    float theta = Spherical(p).y;
    float phi = Spherical(p).z;
    float r1 = superFormula(phi, m, n1, n2, n3, a, b);
    float r2 = superFormula(theta, m, n1, n2, n3, a, b);
    float d1 = size * r1 * cos(phi) * r2 * cos(theta);
    float d2 = size * r1 * sin(phi) * r2 * cos(theta);
    float d3 = size * r2 * sin(theta);
    vec3 q = vec3(d1, d2, d3);
    return r - length(q);
}

