vec3 calcNormal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(
        sceneSDF(p + e.xyy) - sceneSDF(p - e.xyy),
        sceneSDF(p + e.yxy) - sceneSDF(p - e.yxy),
        sceneSDF(p + e.yyx) - sceneSDF(p - e.yyx)
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera settings from editor (adjust these as needed)
    vec3 cameraPos = vec3(0.8+(iMouse.x/iResolution.x)*10., -3.+(iMouse.y/iResolution.y)*10., 5.81);
    vec3 cameraTarget = vec3(-0.46, -0.81, 0.01);

    // Calculate ray direction
    vec3 forward = normalize(cameraTarget - cameraPos);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);
    vec3 ro = cameraPos;

    float t = 0.0;
    vec3 col = vec3(1.0);

    for (int i = 0; i < 150; i++) {
        vec3 p = ro + rd * t;
        float d = sceneSDF(p);

        if (d < 0.001) {
            vec3 n = calcNormal(p);
            vec3 lightDir = normalize(vec3(1.0, 1.0, 1.0));
            float diff = max(dot(n, lightDir), 0.0);
            float amb = 0.3;
            col = vec3(0.85, 0.75, 0.7) * (diff + amb);
            break;
        }

        if (t > 20.0) break;
        // Conservative step multiplier for complex/approximate SDFs
        t += d * 0.5;
    }

    fragColor = vec4(col, 1.0);
}

// Fork of "simple heightmap from texture" by Cotterzz. https://shadertoy.com/view/WcV3Dh
