void mainImage( out vec4 fragColor, in vec2 fragCoord ){fragColor = texture(iChannel0, fragCoord/iResolution.xy, 0.);}

vec3 getCol(int c, vec2 uv){
    return c==0?texture(iChannel0, uv, 0.).rgb:texture(iChannel1, uv, 0.).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int tex = 0;

    if(iFrame>1){tex = 1;}
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = getCol(tex, uv);

    vec2 nuv = vec2(col.b, col.r)-0.5;

    vec2 muv = uv+(nuv*col.g/100.);

    col = getCol(tex, muv);

    fragColor = vec4(col,1);

}
